package VehicleQualitySystem {

    part def Vehicle {
        attribute curbWeight : Real;
    }

    part def QualityCheckSystem {
        in item vehicle : Vehicle;
        out item evaluationResult : EvaluationResult;

        attribute maxAllowedWeight : Real = 2500.0;

        action collectWeightData {
            in item v : Vehicle;
            out item actualWeight : Real;
        }

        action processWeightData {
            in item rawWeight : Real;
            out item processedWeight : Real;
        }

        action evaluateWeight {
            in item weight : Real;
            in item threshold : Real;
            out item result : EvaluationResult;
        }

        action performQualityCheck : ActionUsage {
            in item inputVehicle : Vehicle = vehicle;
            out item result : EvaluationResult;

            bind collectWeightData.v = inputVehicle;
            bind processWeightData.rawWeight = collectWeightData.actualWeight;
            bind evaluateWeight.weight = processWeightData.processedWeight;
            bind evaluateWeight.threshold = maxAllowedWeight;
            bind result = evaluateWeight.result;

            first collectWeightData
            then processWeightData
            then evaluateWeight;
        }
    }

    enum def EvaluationResult {
        enum qualified;
        enum unqualified;
    }

    constraint def WeightRequirement {
        in v : Vehicle;
        v.curbWeight <= 2500.0;
    }
}

package VehicleMassVerificationSystem {

    part def Vehicle {
        attribute actualMass : Real;
    }

    part def WeighingEquipment {
        attribute measuredMass : Real;
    }

    part def MassVerificationUnit {
        attribute massLimit : Real = 2500.0;
        attribute verificationResult : Boolean;
    }

    part def VehicleMassVerificationSystem {
        part vehicle : Vehicle;
        part weighingEquipment : WeighingEquipment;
        part massVerificationUnit : MassVerificationUnit;

        connection : Connect connect vehicle to weighingEquipment;
        connection : Connect connect weighingEquipment to massVerificationUnit;
    }

    action def MeasureVehicleMass {
        in vehicle : Vehicle;
        out measuredMass : Real;

        action measureMass {
            assign measuredMass = vehicle.actualMass;
        }
    }

    action def VerifyMass {
        in measuredMass : Real;
        in massLimit : Real;
        out isQualified : Boolean;

        action checkCompliance {
            assign isQualified = (measuredMass <= massLimit);
        }
    }

    action def VehicleMassInspectionProcess {
        in vehicle : Vehicle;
        out verificationResult : Boolean;

        action measure : MeasureVehicleMass {
            in vehicle = vehicle;
        }

        action verify : VerifyMass {
            in measuredMass = measure.measuredMass;
            in massLimit = 2500.0;
        }

        flow from measure.measuredMass to verify.measuredMass;

        assign verificationResult = verify.isQualified;
    }
}

