package VehicleDynamicsSimulationSystem {

    part def VehicleDynamicsSystem {
        attribute initialVelocity : Real;
        attribute initialPosition : Real;
        attribute acceleration : Real;
        attribute timeStep : Real;
        attribute finalVelocity : Real;
        attribute finalPosition : Real;

        port inputPort : DataInputPort;
        port outputPort : DataOutputPort;
        port modelCenterInterface : AnalysisToolInterface;
    }

    part def DataInputPort {
        in item initialVelocityData : Real;
        in item initialPositionData : Real;
        in item accelerationData : Real;
        in item timeStepData : Real;
    }

    part def DataOutputPort {
        out item finalVelocityData : Real;
        out item finalPositionData : Real;
    }

    part def AnalysisToolInterface {
        in item simulationParameters : SimulationData;
        out item simulationResults : SimulationData;
    }

    item def SimulationData {
        attribute velocity : Real;
        attribute position : Real;
        attribute acceleration : Real;
        attribute time : Real;
    }

    part def ModelCenterTool {
        port toolInterface : AnalysisToolInterface;

        action calculateDynamics {
            in inputData : SimulationData;
            out outputData : SimulationData;
        }
    }

    part vehicleSimulationSystem : VehicleDynamicsSystem {
        part modelCenter : ModelCenterTool;

        interface : InterfaceDefinition connect inputPort to modelCenter.toolInterface;
        interface : InterfaceDefinition connect modelCenter.toolInterface to outputPort;

        action performSimulation {
            action receiveInput {
                in initialVel : Real = initialVelocity;
                in initialPos : Real = initialPosition;
                in accel : Real = acceleration;
                in deltaT : Real = timeStep;
            }

            action executeCalculation {
                in parameters : SimulationData;
                out results : SimulationData;

                bind parameters.velocity = initialVelocity;
                bind parameters.position = initialPosition;
                bind parameters.acceleration = acceleration;
                bind parameters.time = timeStep;

                send parameters to modelCenter.calculateDynamics;
                results = modelCenter.calculateDynamics.outputData;
            }

            action outputResults {
                out finalVel : Real = finalVelocity;
                out finalPos : Real = finalPosition;

                bind finalVel = results.velocity;
                bind finalPos = results.position;
            }

            flow receiveInput then executeCalculation then outputResults;
        }
    }

    requirement vehicleDynamicsSimulationCapability {
        doc /* The system shall be capable of performing automated simulation 
             and analysis of vehicle dynamic behavior */

        require constraint {
            vehicleSimulationSystem.performSimulation != null
        }
    }

    requirement userInputCapability {
        doc /* Users should be able to input the vehicle's initial velocity, 
             initial position, acceleration at a specific moment, and the time step */

        require constraint {
            vehicleSimulationSystem.inputPort != null and
            vehicleSimulationSystem.initialVelocity != null and
            vehicleSimulationSystem.initialPosition != null and
            vehicleSimulationSystem.acceleration != null and
            vehicleSimulationSystem.timeStep != null
        }
    }

    requirement modelCenterIntegration {
        doc /* Through integration with the ModelCenter analysis tool, the system 
             shall automatically calculate and output the vehicle's velocity and 
             position after the specified time step */

        require constraint {
            vehicleSimulationSystem.modelCenter != null and
            vehicleSimulationSystem.modelCenterInterface != null
        }
    }

    requirement accurateDataTransfer {
        doc /* The system must ensure accurate data transfer and reliable output 
             of simulation results */

        require constraint {
            vehicleSimulationSystem.outputPort != null and
            vehicleSimulationSystem.finalVelocity != null and
            vehicleSimulationSystem.finalPosition != null
        }
    }
}

