package EquipmentRiskAnalysisSystem {

    part def Equipment {
        part battery : Battery;
        attribute operationalStatus : String;
        attribute criticalFunctionStatus : String;
    }

    part def Battery {
        attribute batteryLevel : Real;
        attribute age : Real;
        attribute failureProbability : Real;
        attribute minimumThreshold : Real = 20.0;
    }

    part def RiskAnalysisSystem {
        attribute monitoringActive : Boolean = true;
        attribute riskDetected : Boolean;
        attribute severityLevel : String;
    }

    occurrence def BatteryAging {
        attribute agingFactor : Real;
        attribute impactOnFailureProbability : Real;
    }

    occurrence def LowBatteryLevel {
        attribute currentLevel : Real;
        attribute duration : Real;
        attribute belowThreshold : Boolean;
    }

    occurrence def DeviceShutdown {
        attribute shutdownTime : Real;
        attribute criticalFunctionInterrupted : Boolean;
    }

    part riskAnalysisSystem : RiskAnalysisSystem {
        perform monitorBatteryLevel {
            in batteryToMonitor : Battery;
            out batteryStatus : String;
        }

        perform detectAgingRisk {
            in battery : Battery;
            out agingRiskDetected : Boolean;
        }

        perform analyzeFailureScenario {
            in lowBatteryState : LowBatteryLevel;
            out failureConsequence : String;
        }

        perform assignSeverityLevel {
            in failureType : String;
            out severity : String;
        }
    }

    connection def CausesLowBattery {
        end cause : BatteryAging;
        end effect : LowBatteryLevel;
    }

    connection def LeadsToShutdown {
        end cause : LowBatteryLevel;
        end effect : DeviceShutdown;
    }

    constraint def BatteryFailureProbability {
        batteryAge : Real;
        failureProbability : Real;
        failureProbability > 0.7 implies batteryAge > 3.0;
    }

    constraint def LowBatteryCondition {
        batteryLevel : Real;
        minimumThreshold : Real;
        lowBattery : Boolean;
        lowBattery == (batteryLevel < minimumThreshold);
    }

    constraint def CriticalSeverity {
        criticalFunctionInterrupted : Boolean;
        severityLevel : String;
        criticalFunctionInterrupted implies severityLevel == "High";
    }
}

package SystemStructuralDecomposition {

    part def System {
        doc /* The system must possess a clear capability for structural decomposition */

        part subsystems : Subsystem[1..*];
        part components : Component[1..*];

        port interfaces : Interface[1..*];
    }

    part def Subsystem {
        doc /* Subsystem as part of hierarchical structure */

        part subComponents : Component[1..*];
        port subInterfaces : Interface[1..*];

        attribute level : Integer;
        attribute functions : String[1..*];
        attribute capabilities : String[1..*];
    }

    part def Component {
        doc /* Component that can be independently understood and analyzed */

        attribute name : String;
        attribute functions : String[1..*];
        attribute capabilities : String[1..*];

        port componentInterfaces : Interface[1..*];
    }

    port def Interface {
        doc /* Well defined interfaces between system components */

        attribute interfaceType : String;
        attribute protocol : String;
    }

    connection def ComponentConnection {
        doc /* Connection enabling components to collaborate effectively */

        end sourceComponent : Component[1];
        end targetComponent : Component[1];

        attribute connectionType : String;
    }

    part systemInstance : System {
        doc /* Top-down hierarchical structure instance */

        part subsystem1 : Subsystem {
            :>> level = 1;
        }

        part subsystem2 : Subsystem {
            :>> level = 1;
        }

        connection : ComponentConnection 
            connect subsystem1.subComponents[1] to subsystem2.subComponents[1];
    }

    requirement def StructuralDecompositionRequirement {
        doc /* Requirement for structural decomposition capability */

        subject system : System;

        require constraint {
            system.subsystems->size() >= 1 and
            system.components->size() >= 1
        }
    }

    requirement def InterfaceDefinitionRequirement {
        doc /* Interfaces between components should be well defined */

        subject component : Component;

        require constraint {
            component.componentInterfaces->size() >= 1
        }
    }

    requirement def IndependentAnalysisRequirement {
        doc /* Each component can be independently understood and analyzed */

        subject component : Component;

        require constraint {
            component.functions->size() >= 1 and
            component.capabilities->size() >= 1
        }
    }

    requirement def VerificationRequirement {
        doc /* System engineers and IV&V must be able to verify system integrity */

        subject system : System;

        require constraint {
            system.subsystems->forAll(s | s.functions->notEmpty()) and
            system.subsystems->forAll(s | s.capabilities->notEmpty())
        }
    }
}

package AutomotiveSystemArchitecture {

    part def Component {
        attribute name : String;
        attribute id : String;
    }

    part def SafetyComponent :> Component {
        attribute safetyLevel : String;
        attribute isMandatory : Boolean;
    }

    part def FunctionalComponent :> Component {
        attribute function : String;
    }

    part def System :> Component {
        part subsystems : Subsystem[*];
        part components : Component[*];
    }

    part def Subsystem :> Component {
        part components : Component[*];
    }

    part def SeatBelt :> SafetyComponent {
        attribute :>> isMandatory = true;
        attribute :>> safetyLevel = "Critical";
    }

    part def Bumper :> SafetyComponent {
        attribute :>> isMandatory = true;
        attribute :>> safetyLevel = "Critical";
    }

    part def DriverAirbag :> SafetyComponent {
        attribute :>> isMandatory = false;
        attribute :>> safetyLevel = "High";
    }

    part def ABS :> SafetyComponent {
        attribute :>> isMandatory = false;
        attribute :>> safetyLevel = "High";
        attribute :>> name = "Anti-lock Braking System";
    }

    part def AutomotiveSystem :> System {
        attribute :>> name = "Root Automotive System";

        part safetySubsystem : Subsystem {
            attribute :>> name = "Safety Subsystem";

            part seatBelt : SeatBelt {
                attribute :>> name = "Seat Belt System";
            }

            part bumper : Bumper {
                attribute :>> name = "Bumper System";
            }

            part driverAirbag : DriverAirbag {
                attribute :>> name = "Driver Airbag System";
            }

            part abs : ABS;
        }

        part functionalSubsystem : Subsystem {
            attribute :>> name = "Functional Subsystem";

            part engineControl : FunctionalComponent {
                attribute :>> name = "Engine Control Unit";
                attribute :>> function = "Engine Management";
            }

            part transmission : FunctionalComponent {
                attribute :>> name = "Transmission Control";
                attribute :>> function = "Gear Management";
            }
        }
    }

    view def ComponentHierarchyView {
        expose AutomotiveSystem::**;
    }

    view def SafetyComponentView {
        filter AutomotiveSystem::**:>SafetyComponent;
    }

    view def NonSafetyComponentView {
        filter AutomotiveSystem::**:>FunctionalComponent;
    }
}

