package VehicleControlSystem

  // State Machine for Vehicle Operation
  statemachine VehicleOperationStateMachine {
    state Off {
      transition "StartSignal" to Starting when BrakePedalPressed;
    }
    state Starting {
      transition "VehicleOnSignal" to Running;
    }
    state Running {
      entry "SelfChecks";
      entry "PowerSupply";
      exit "ParkingBrakeApply";
      transition "OverTemperatureSignal" to Degraded;
    }
    state Degraded {
      transition "RestoreToNormalSignal" to Running;
    }
    state Maintenance {
      transition "RestoreToNormalSignal" to Running;
    }
  }

  // Block for Vehicle Controller
  block VehicleController {
    // ... (Define controller's operation state management)
  }

endpackage
