    output MeasuredMass : Real;

    block WeighingProcess {
      activity MeasureMass {
        input Vehicle;
        output MeasuredMass : Real;
      }
    }

    activity CompareMass {
      input MeasuredMass : Real;
      input MaximumPermissibleMass : Real;
      output ComplianceStatus : Boolean;
    }
  }
