package VehicleDynamics

  // Define the input parameters
  parameter InitialPosition : Real = 0.0
  parameter InitialVelocity : Real = 0.0
  parameter VehicleMass : Real = 1000.0
  parameter WheelEndDrivePower : Real = 100.0
  parameter TimeInterval : Real = 1.0

  // Define the output parameters
  parameter FinalVelocity : Real
  parameter FinalPosition : Real

  // Define the system behavior
  block VehicleDynamicsSystem {
    // Define the internal variables
    variable Acceleration : Real

    // Define the equations
    equation Acceleration = (WheelEndDrivePower - (DragCoefficient * Velocity^2) - (FrictionCoefficient * VehicleMass * Gravity)) / VehicleMass
    equation FinalVelocity = InitialVelocity + (Acceleration * TimeInterval)
    equation FinalPosition = InitialPosition + (InitialVelocity * TimeInterval) + (0.5 * Acceleration * TimeInterval^2)
  }

endpackage
