block VehicleMaintenanceSystem {

  property Vehicle {
    type Vehicle
  }

  property MaintenanceInterval {
    type Duration
  }

  property MaxTemperature {
    type Real
  }

  activity NextMaintenanceTime {
    input Vehicle
    input MaintenanceInterval
    output Time
  }

  activity IsMaintenanceTime {
    input Vehicle
    output Boolean
  }

  activity StartMaintenance {
    input Vehicle
  }

  activity EndMaintenance {
    input Vehicle
  }

  state MachineState {
    state Healthy
    state UnderMaintenance
  }

  transition StartMaintenance {
    from Healthy
    to UnderMaintenance
  }

  transition EndMaintenance {
    from UnderMaintenance
    to Healthy
  }

  constraint MaxMaintenanceDuration {
    EndMaintenance - StartMaintenance <= 48 hours
  }

  constraint MaintenanceStartDelay {
    StartMaintenance >= NextMaintenanceTime + 2 seconds
  }
