package 'VehicleSystem' {
	attribute def VehicleMass;
	attribute def GearSetting;
	attribute def AcceleratorPosition;
	attribute def Engine;
	attribute def Driver;
	port def EnginePort {
		in engine: Engine;
	}
	port def DriverPort {
		in driver: Driver;
	}
	part context {
		part vehicle {
			port enginePort: ~EnginePort;
			port driverPort: ~DriverPort;
			attribute mass: VehicleMass;
			attribute gearSetting: GearSetting;
			attribute acceleratorPosition: AcceleratorPosition;
			perform defineVehicleInfo.setVehicleParameters {
				out mass = this.mass;
				out gearSetting = this.gearSetting;
				out acceleratorPosition = this.acceleratorPosition;
				out engine = enginePort.engine;
				out driver = driverPort.driver;
			}
		}
		interface vehicleToEngine connect vehicle.enginePort to engineComponent.enginePort {
			ref flow references defineVehicleInfo.engineFlow
				from source.engine to target.engine;
		}
		interface vehicleToDriver connect vehicle.driverPort to driverComponent.driverPort {
			ref flow references defineVehicleInfo.driverFlow
				from source.driver to target.driver;
		}
		part engineComponent {
			port enginePort: EnginePort;
			attribute engine: Engine;
		}
		part driverComponent {
			port driverPort: DriverPort;
			attribute driver: Driver;
		}
	}
	action defineVehicleInfo {
		action setVehicleParameters {
			out mass: VehicleMass;
			out gearSetting: GearSetting;
			out acceleratorPosition: AcceleratorPosition;
			out engine: Engine;
			out driver: Driver;
		}
		succession flow massFlow from setVehicleParameters.mass to vehicle.mass;
		succession flow gearFlow from setVehicleParameters.gearSetting to vehicle.gearSetting;
		succession flow acceleratorFlow from setVehicleParameters.acceleratorPosition to vehicle.acceleratorPosition;
		succession flow engineFlow from setVehicleParameters.engine to engineComponent.enginePort.engine;
		succession flow driverFlow from setVehicleParameters.driver to driverComponent.driverPort.driver;
	}
}

package 'Vehicles' {
	interface def driverToVehicle;
	interface def engineToVehicle;

	part context {
		part personnel;
		part engine;

		part manuallyOperatedVehicle {
			port driverPort: driverToVehicle;
		}
		interface driverConnection connect personnel to manuallyOperatedVehicle.driverPort;

		part poweredVehicle {
			port enginePort: engineToVehicle;
		}
		interface engineConnection connect poweredVehicle.enginePort to engine;

		part manuallyOperatedPoweredVehicle {
			port driverPort: driverToVehicle;
			port enginePort: engineToVehicle;
		}
		interface driverEngineConnection1 connect personnel to manuallyOperatedPoweredVehicle.driverPort;
		interface driverEngineConnection2 connect manuallyOperatedPoweredVehicle.enginePort to engine;
	}
}

