package 'InformationClassification' {  
    value type SecurityLevel {  
        literal uncl {  
            code = "uncl";  
            color = "green";  
        }  
        literal conf {  
            code = "conf";  
            color = "yellow";  
        }  
        literal secr {  
            code = "secr";  
            color = "red";  
        }  
    }  

    value type GradeLevel {  
        literal A {  
            points = 4.0;  
        }  
        literal B {  
            points = 3.0;  
        }  
        literal C {  
            points = 2.0;  
        }  
        literal D {  
            points = 1.0;  
        }  
        literal F {  
            points = 0.0;  
        }  
    }  

    attribute def SecurityLevelCmd;  
    attribute def GradeLevelCmd;  

    port def SecurityCmdPort {  
        out securityLevelCmd : SecurityLevelCmd;  
    }  

    port def GradeCmdPort {  
        out gradeLevelCmd : GradeLevelCmd;  
    }  

    part context {  
        part user {  
            port securityCmdPort: SecurityCmdPort;  
            port gradeCmdPort: GradeCmdPort;  
            perform classification.assignSecurityLevelCmd {  
                out securityLevelCmd = securityCmdPort.securityLevelCmd;  
            }  
            perform classification.assignGradeLevelCmd {  
                out gradeLevelCmd = gradeCmdPort.gradeLevelCmd;  
            }  
        }  

        interface userToSecurityClassifier connect user.securityCmdPort to securityClassifier.securityCmdPort {  
            ref flow references classification.securityLevelFlow  
                from source.securityLevelCmd to target.securityLevelCmd;  
        }  

        interface userToGradeClassifier connect user.gradeCmdPort to gradeClassifier.gradeCmdPort {  
            ref flow references classification.gradeLevelFlow  
                from source.gradeLevelCmd to target.gradeLevelCmd;  
        }  

        part securityClassifier {  
            port securityCmdPort: ~SecurityCmdPort;  
            perform classification.processSecurityLevel {  
                in securityLevelCmd = securityCmdPort.securityLevelCmd;  
                out securityLevel = info.securityLevel;  
            }  
        }  

        part gradeClassifier {  
            port gradeCmdPort: ~GradeCmdPort;  
            perform classification.processGradeLevel {  
                in gradeLevelCmd = gradeCmdPort.gradeLevelCmd;  
                out gradeLevel = info.gradeLevel;  
            }  
        }  

        part info {  
            attribute securityLevel: SecurityLevel;  
            attribute gradeLevel: GradeLevel;  
        }  
    }  

    action classification {  
        action assignSecurityLevelCmd { out securityLevelCmd: SecurityLevelCmd; }  
        succession flow securityLevelFlow from assignSecurityLevelCmd.securityLevelCmd to processSecurityLevel.securityLevelCmd;  
        action processSecurityLevel { in securityLevelCmd: SecurityLevelCmd; out securityLevel: SecurityLevel; }  

        action assignGradeLevelCmd { out gradeLevelCmd: GradeLevelCmd; }  
        succession flow gradeLevelFlow from assignGradeLevelCmd.gradeLevelCmd to processGradeLevel.gradeLevelCmd;  
        action processGradeLevel { in gradeLevelCmd: GradeLevelCmd; out gradeLevel: GradeLevel; }  
    }  
}

