	attribute def FuelType;
	attribute def DriverInfo;
	attribute def Personnel;
	port def FuelCmdPort {
		in fuelType : FuelType;
	}
	port def DriverInfoPort {
		in driverInfo : DriverInfo;
	}
	part context {
		part vehicle {
			attribute quality : QualityAttributes;
			part driverInfo {
				port driverInfoPort : DriverInfoPort;
				perform defineVehicle.driverInfoAction {
					out driverInfo = driverInfoPort.driverInfo;
				}
			}
			part fuelTank {
				port fuelCmdPort : FuelCmdPort;
				perform defineVehicle.fuelTankAction {
					in fuelType = fuelCmdPort.fuelType;
				}
			}
		}
		interface vehicleToPersonnel connect vehicle.driverInfo.driverInfoPort to personnel.driverInfoPort {
			ref flow references defineVehicle.driverInfoFlow
				from source.driverInfo to target.driverInfo;
		}
		interface vehicleToFuel connect vehicle.fuelTank.fuelCmdPort to fuel.fuelCmdPort {
			ref flow references defineVehicle.fuelTypeFlow
				from source.fuelType to target.fuelType;
		}
		part personnel {
			port driverInfoPort : ~DriverInfoPort;
			attribute data : Personnel;
		}
		part fuel {
			port fuelCmdPort : ~FuelCmdPort;
			attribute type : FuelType;
		}
	}
	action defineVehicle {
		action driverInfoAction { out driverInfo : DriverInfo; }
		action fuelTankAction { in fuelType : FuelType; }
		succession flow driverInfoFlow from driverInfoAction.driverInfo to personnel.driverInfo;
		succession flow fuelTypeFlow from fuelTankAction.fuelType to fuel.type;
	}
}

