package 'FuelFlowManagement' {
	attribute def Fuel;
	attribute def FuelReturn;
	attribute def TemperatureData;
	port def FuelSupplyPort {
		out fuel: Fuel;
	}
	port def FuelReturnPort {
		out fuelReturn: FuelReturn;
	}
	port def TemperaturePort {
		out temperatureData: TemperatureData;
	}
	part context {
		part fuelTankAssembly {
			port fuelSupplyPort: FuelSupplyPort;
			port fuelReturnPort: ~FuelReturnPort;
			port temperaturePort: TemperaturePort;
			perform fuelTransfer.supplyFuel {
				out fuel = fuelSupplyPort.fuel;
			}
			perform fuelTransfer.receiveReturnFuel {
				in fuelReturn = fuelReturnPort.fuelReturn;
			}
			perform fuelTransfer.monitorFuelTankTemperature {
				out temperatureData = temperaturePort.temperatureData;
			}
		}
		part engine {
			port fuelSupplyPort: ~FuelSupplyPort;
			port fuelReturnPort: FuelReturnPort;
			port temperaturePort: TemperaturePort;
			perform fuelTransfer.receiveFuel {
				in fuel = fuelSupplyPort.fuel;
			}
			perform fuelTransfer.returnExcessFuel {
				out fuelReturn = fuelReturnPort.fuelReturn;
			}
			perform fuelTransfer.monitorEngineTemperature {
				out temperatureData = temperaturePort.temperatureData;
			}
		}
		part userInterface {
			port fuelSupplyStatusPort: ~FuelSupplyPort;
			port fuelReturnStatusPort: ~FuelReturnPort;
			port fuelTankTempPort: ~TemperaturePort;
			port engineTempPort: ~TemperaturePort;
			perform fuelTransfer.displayFuelData {
				in fuel = fuelSupplyStatusPort.fuel;
				in fuelReturn = fuelReturnStatusPort.fuelReturn;
				in fuelTankTemp = fuelTankTempPort.temperatureData;
				in engineTemp = engineTempPort.temperatureData;
			}
		}
		interface fuelSupplyInterface connect fuelTankAssembly.fuelSupplyPort to engine.fuelSupplyPort {
			ref flow references fuelTransfer.fuelSupplyFlow from source.fuel to target.fuel;
		}
		interface fuelReturnInterface connect engine.fuelReturnPort to fuelTankAssembly.fuelReturnPort {
			ref flow references fuelTransfer.fuelReturnFlow from source.fuelReturn to target.fuelReturn;
		}
		interface fuelTankTempInterface connect fuelTankAssembly.temperaturePort to userInterface.fuelTankTempPort {
			ref flow references fuelTransfer.fuelTankTempFlow from source.temperatureData to target.temperatureData;
		}
		interface engineTempInterface connect engine.temperaturePort to userInterface.engineTempPort {
			ref flow references fuelTransfer.engineTempFlow from source.temperatureData to target.temperatureData;
		}
	}
	action fuelTransfer {
		action supplyFuel { out fuel: Fuel; }
		succession flow fuelSupplyFlow from supplyFuel.fuel to receiveFuel.fuel;
		action receiveFuel { in fuel: Fuel; }
		action returnExcessFuel { out fuelReturn: FuelReturn; }
		succession flow fuelReturnFlow from returnExcessFuel.fuelReturn to receiveReturnFuel.fuelReturn;
		action receiveReturnFuel { in fuelReturn: FuelReturn; }
		action monitorFuelTankTemperature { out temperatureData: TemperatureData; }
		succession flow fuelTankTempFlow from monitorFuelTankTemperature.temperatureData to displayFuelData.fuelTankTemp;
		action monitorEngineTemperature { out temperatureData: TemperatureData; }
		succession flow engineTempFlow from monitorEngineTemperature.temperatureData to displayFuelData.engineTemp;
		action displayFuelData {
			in fuel: Fuel;
			in fuelReturn: FuelReturn;
			in fuelTankTemp: TemperatureData;
			in engineTemp: TemperatureData;
		}
	}
}

