package 'VehicleTrailerCoupling' {
	attribute def CouplingBall;
	attribute def TrailerConnector;
	port def CouplingPort {
		out couplingBall: CouplingBall;
	}
	port def TrailerPort {
		out trailerConnector: TrailerConnector;
	}
	part context {
		part vehicle {
			port couplingPort: ~CouplingPort;
			perform connectToHitch.attachVehicle {
				ref couplingBall = couplingPort.couplingBall;
			}
			perform disconnectFromHitch.detachVehicle {
				unref couplingBall;
			}
		}
		part hitch {
			part couplingBall: CouplingBall;
			part trailerConnector: TrailerConnector;
			port couplingPort: CouplingPort {
				out couplingBall = this.couplingBall;
			}
			port trailerPort: TrailerPort {
				out trailerConnector = this.trailerConnector;
			}
		}
		part trailer {
			port trailerPort: ~TrailerPort;
			perform connectToHitch.attachTrailer {
				ref trailerConnector = trailerPort.trailerConnector;
			}
			perform disconnectFromHitch.detachTrailer {
				unref trailerConnector;
			}
		}
		interface vehicleToHitch connect vehicle.couplingPort to hitch.couplingPort {
			ref flow references connectToHitch.attachVehicle.couplingBall;
			ref flow references disconnectFromHitch.detachVehicle.couplingBall;
		}
		interface trailerToHitch connect trailer.trailerPort to hitch.trailerPort {
			ref flow references connectToHitch.attachTrailer.trailerConnector;
			ref flow references disconnectFromHitch.detachTrailer.trailerConnector;
		}
	}
	action connectToHitch {
		action attachVehicle { ref couplingBall: CouplingBall; }
		action attachTrailer { ref trailerConnector: TrailerConnector; }
	}
	action disconnectFromHitch {
		action detachVehicle { unref couplingBall: CouplingBall; }
		action detachTrailer { unref trailerConnector: TrailerConnector; }
	}
}

package 'VehiclePowerTransmission' {
	attribute def FuelCmd;
	attribute def Power;
	attribute def ClutchSignal;
	attribute def DriveshaftPower;
	attribute def Torque;
	port def FuelCmdPort {
		out fuelCmd : FuelCmd;
	}
	port def PowerPort {
		out power : Power;
	}
	port def ClutchPort {
		out clutch : ClutchSignal;
	}
	port def DriveshaftPowerPort {
		out driveshaftPower : DriveshaftPower;
	}
	port def DifferentialPort {
		out power : Power;
	}
	port def HalfShaftPowerPort {
		out shaftPower : DriveshaftPower;
	}
	port def RoadInterfacePort {
		out torque : Torque;
	}
	part context {
		part user {
			port fuelCmdPort: FuelCmdPort;
			perform powerTransmissionAction.sendFuelCmd {
				out fuelCmd = fuelCmdPort.fuelCmd;
			}
		}
		interface userToEngine connect user.fuelCmdPort to engine.fuelCmdPort {
			ref flow references powerTransmissionAction.fuelCmdFlow from source.fuelCmd to target.fuelCmd;
		}
		part engine {
			port fuelCmdPort: ~FuelCmdPort;
			port powerPort: PowerPort;
			port clutchPort: ClutchPort;
			perform powerTransmissionAction.produceEnginePower {
				in fuelCmd = fuelCmdPort.fuelCmd;
				out power = powerPort.power;
				out clutch = clutchPort.clutch;
			}
		}
		part transmission {
			port enginePowerPort: ~PowerPort;
			port clutchPort: ~ClutchPort;
			port driveshaftPowerPort: DriveshaftPowerPort;
			perform powerTransmissionAction.transmitPower {
				in power = enginePowerPort.power;
				in clutch = clutchPort.clutch;
				out driveshaftPower = driveshaftPowerPort.driveshaftPower;
			}
		}
		interface engineToTransmission connect engine.powerPort to transmission.enginePowerPort {
			ref flow references powerTransmissionAction.powerFlow from source.power to target.power;
		}
		interface engineToTransmissionClutch connect engine.clutchPort to transmission.clutchPort {
			ref flow references powerTransmissionAction.clutchFlow from source.clutch to target.clutch;
		}
		part driveshaft {
			port inputPort: ~DriveshaftPowerPort;
			port outputPort: DriveshaftPowerPort;
			perform powerTransmissionAction.transmitDriveshaftPower {
				in power = inputPort.driveshaftPower;
				out driveshaftPower = outputPort.driveshaftPower;
			}
		}
		interface transmissionToDriveshaft connect transmission.driveshaftPowerPort to driveshaft.inputPort {
			ref flow references powerTransmissionAction.driveshaftPowerFlow from source.driveshaftPower to target.driveshaftPower;
		}
		part rearAxleAssembly {
			port inputPort: ~DriveshaftPowerPort;
			part differential {
				port input: DriveshaftPowerPort;
				port leftOutput: DifferentialPort;
				port rightOutput: DifferentialPort;
				perform powerTransmissionAction.splitPower {
					in power = input.driveshaftPower;
					out left = leftOutput.power;
					out right = rightOutput.power;
				}
			}
			interface rearAxleInput to inputPort: DriveshaftPowerPort {
				ref flow references powerTransmissionAction.rearAxleInputFlow from source.driveshaftPower to target.driveshaftPower;
			}
			interface differentialInput connect rearAxleAssembly.inputPort to differential.input {
				ref flow references powerTransmissionAction.differentialInputFlow from source.driveshaftPower to target.driveshaftPower;
			}
			part leftHalfShaft {
				port input: ~DifferentialPort;
				port output: HalfShaftPowerPort;
				perform powerTransmissionAction.leftHalfShaftTransfer {
					in power = input.power;
					out shaftPower = output.shaftPower;
				}
			}
			interface differentialToLeft connect differential.leftOutput to leftHalfShaft.input {
				ref flow references powerTransmissionAction.differentialLeftFlow from source.power to target.power;
			}
			part rightHalfShaft {
				port input: ~DifferentialPort;
				port output: HalfShaftPowerPort;
				perform powerTransmissionAction.rightHalfShaftTransfer {
					in power = input.power;
					out shaftPower = output.shaftPower;
				}
			}
			interface differentialToRight connect differential.rightOutput to rightHalfShaft.input {
				ref flow references powerTransmissionAction.differentialRightFlow from source.power to target.power;
			}
		}
		interface driveshaftToRearAxle connect driveshaft.outputPort to rearAxleAssembly.inputPort {
			ref flow references powerTransmission.drivetoshaftFlow from source.driveshaftPower to target.driveshaftPower;
		}
		part leftWheel {
			port shaftInput: ~HalfShaftPowerPort;
			port roadPort: RoadInterfacePort;
			perform powerTransmissionAction.leftWheelAction {
				in power = shaftInput.shaftPower;
				out torque = roadPort.torque;
			}
		}
		interface leftHalfShaftToWheel connect leftHalfShaft.output to leftWheel.shaftInput {
			ref flow references powerTransmissionAction.leftShaftToWheelFlow from source.shaftPower to target.shaftPower;
		}
		interface leftWheelToRoad connect leftWheel.roadPort to road.roadInterfacePort {
			ref flow references powerTransmissionAction.leftWheelToRoadFlow from source.torque to target.torque;
		}
		part rightWheel {
			port shaftInput: ~HalfShaftPowerPort;
			port roadPort: RoadInterfacePort;
			perform powerTransmissionAction.rightWheelAction {
				in power = shaftInput.shaftPower;
				out torque = roadPort.torque;
			}
		}
		interface rightHalfShaftToWheel connect rightHalfShaft.output to rightWheel.shaftInput {
			ref flow references powerTransmissionAction.rightShaftToWheelFlow from source.shaftPower to target.shaftPower;
		}
		interface rightWheelToRoad connect rightWheel.roadPort to road.roadInterfacePort {
			ref flow references powerTransmissionAction.rightWheelToRoadFlow from source.torque to target.torque;
		}
		part road {
			port roadInterfacePort: RoadInterfacePort;
		}
	}
	action powerTransmissionAction {
		action sendFuelCmd { out fuelCmd: FuelCmd; }
		succession flow fuelCmdFlow from sendFuelCmd.fuelCmd to produceEnginePower.fuelCmd;
		action produceEnginePower { in fuelCmd; out power: Power; out clutch: ClutchSignal; }
		succession flow powerFlow from produceEnginePower.power to transmitPower.power;
		succession flow clutchFlow from produceEnginePower.clutch to transmitPower.clutch;
		action transmitPower { in power: Power; in clutch: ClutchSignal; out driveshaftPower: DriveshaftPower; }
		succession flow driveshaftPowerFlow from transmitPower.driveshaftPower to transmitDriveshaftPower.driveshaftPower;
		action transmitDriveshaftPower { in driveshaftPower: DriveshaftPower; out driveshaftPower: DriveshaftPower; }
		succession flow driveshaftToRearAxleFlow from transmitDriveshaftPower.driveshaftPower to splitPower.power;
		action splitPower { in power: DriveshaftPower; out left: Power; out right: Power; }
		succession flow differentialLeftFlow from splitPower.left to distributePower.power;
		action distributePower { in power: Power; out left: Power; out right: Power; }
		succession flow leftShaftToWheelFlow from distributePower.left to leftWheelAction.power;
		action leftWheelAction { in power: Power; out torque: Torque; }
		succession flow leftWheelToRoadFlow from leftWheelAction.torque to roadInterfaceLeft.torque;
		action rightWheelAction { in power: Power; out torque: Torque; }
		succession flow rightWheelToRoadFlow from rightWheelAction.torque to roadInterfaceRight.torque;
	}
}

package 'MultiArchitectureSystem' {
	attribute def Signal;
	port def PortC1_pb {
		out signal : Signal;
	}
	port def PortC2_pa {
		in signal : Signal;
	}
	port def PortC3_pd {
		out signal : Signal;
	}
	port def PortC4_pc {
		in signal : Signal;
	}
	part context {
		part b11 {
			part c1 {
				port pb : PortC1_pb;
				perform sendSignalB11 {
					out signal = pb.signal;
				}
			}
			part c2 {
				port pa : PortC2_pa;
				perform receiveSignalB11 {
					in signal = pa.signal;
				}
			}
			interface pe connect c1.pb to c2.pa {
				ref flow references b11.signalFlow from source.signal to target.signal;
			}
		}
		part b12 {
			part c3 {
				port pd : PortC3_pd;
				perform sendSignalB12 {
					out signal = pd.signal;
				}
			}
			part c4 {
				port pc : PortC4_pc;
				perform receiveSignalB12 {
					in signal = pc.signal;
				}
			}
			interface pf connect c3.pd to c4.pc {
				ref flow references b12.signalFlow from source.signal to target.signal;
			}
		}
		interface systemInterface connect b11.pe to b12.pf {
			ref flow references systemFlow from source.signal to target.signal;
		}
	}
	part context {
		part b21 {
			part c1 : ~b11.c1;
			part c2 : ~b11.c2;
			part c3 : ~b12.c3;
			part c4 : ~b12.c4;
			perform reorganizeB21 {
				out signal = c1.pb.signal;
				in signal = c3.pd.signal;
			}
		}
		part b22 {
			part c1 : ~b11.c1;
			part c2 : ~b11.c2;
			part c3 : ~b12.c3;
			part c4 : ~b12.c4;
			perform reorganizeB22 {
				out signal = c2.pa.signal;
				in signal = c4.pc.signal;
			}
		}
	}
	action system {
		action sendSignalB11 { out signal : Signal; }
		succession flow signalFlow from sendSignalB11.signal to receiveSignalB11.signal;
		action receiveSignalB11 { in signal : Signal; }
		action sendSignalB12 { out signal : Signal; }
		succession flow signalFlow from sendSignalB12.signal to receiveSignalB12.signal;
		action receiveSignalB12 { in signal : Signal; }
		succession flow systemFlow from sendSignalB11.signal to sendSignalB12.signal;
		action reorganizeB21 { out signal : Signal; in signal : Signal; }
		succession flow b21Flow from reorganizeB21.signal to reorganizeB22.signal;
		action reorganizeB22 { in signal : Signal; out signal : Signal; }
	}
}

package 'AutomotivePowertrain' {  
	attribute def FuelCmd;  
	attribute def EngineState;  
	attribute def EngineTorque;  
	attribute def TransmissionTorque;  
	attribute def DriveshaftTorque;  
	attribute def WheelTorque;  
	attribute def ShutdownCmd;  

	port def FuelCmdPort {  
		out fuelCmd : FuelCmd;  
	}  
	port def EngineStatePort {  
		out engineState : EngineState;  
	}  
	port def TorquePort {  
		out torque : EngineTorque;  
	}  
	port def TransmissionInputPort {  
		in torque : EngineTorque;  
	}  
	port def TransmissionOutputPort {  
		out torque : TransmissionTorque;  
	}  
	port def DriveshaftInputPort {  
		in torque : TransmissionTorque;  
	}  
	port def DriveshaftOutputPort {  
		out torque : DriveshaftTorque;  
	}  
	port def WheelInputPort {  
		in torque : DriveshaftTorque;  
	}  
	port def ShutdownCmdPort {  
		out shutdownCmd : ShutdownCmd;  
	}  

	part context {  
		part user {  
			port fuelCmdPort: FuelCmdPort;  
			port shutdownCmdPort: ShutdownCmdPort;  
			perform controlPower.sendFuelCmd {  
				out fuelCmd = fuelCmdPort.fuelCmd;  
			}  
			perform controlPower.sendShutdownCmd {  
				out shutdownCmd = shutdownCmdPort.shutdownCmd;  
			}  
		}  
		interface userToEngine connect user.fuelCmdPort to engine.fuelCmdPort {  
			ref flow references controlPower.fuelCmdFlow  
				from source.fuelCmd to target.fuelCmd;  
		}  
		interface userToEngineShutdown connect user.shutdownCmdPort to engine.shutdownCmdPort {  
			ref flow references controlPower.shutdownFlow  
				from source.shutdownCmd to target.shutdownCmd;  
		}  

		part engine {  
			port fuelCmdPort: ~FuelCmdPort;  
			port shutdownCmdPort: ~ShutdownCmdPort;  
			port engineStatePort: EngineStatePort;  
			port torquePort: TorquePort;  
			perform controlPower.generateEngineTorque {  
				in fuelCmd = fuelCmdPort.fuelCmd;  
				in shutdownCmd = shutdownCmdPort.shutdownCmd;  
				out engineState = engineStatePort.engineState;  
				out torque = torquePort.torque;  
			}  
		}  

		part transmission {  
			port fuelCmdPort: FuelCmdPort;  
			port engineStatePort: ~EngineStatePort;  
			port transmissionInputPort: ~TransmissionInputPort;  
			port transmissionOutputPort: TransmissionOutputPort;  
			perform controlPower.amplifyTorque {  
				in fuelCmd = fuelCmdPort.fuelCmd;  
				in engineState = engineStatePort.engineState;  
				in torque = transmissionInputPort.torque;  
				out torque = transmissionOutputPort.torque;  
			}  
		}  

		interface engineToTransmission connect engine.torquePort to transmission.transmissionInputPort {  
			ref flow references controlPower.torqueFlow1  
				from source.torque to target.torque;  
		}  
		interface engineToTransmissionState connect engine.engineStatePort to transmission.engineStatePort {  
			ref flow references controlPower.stateFlow  
				from source.engineState to target.engineState;  
		}  
		interface engineToTransmissionCmd connect user.fuelCmdPort to transmission.fuelCmdPort {  
			ref flow references controlPower.fuelCmdFlow  
				from source.fuelCmd to target.fuelCmd;  
		}  

		part driveshaft {  
			port transmissionInputPort: ~TransmissionOutputPort;  
			port driveshaftOutputPort: DriveshaftOutputPort;  
			perform controlPower.transmitTorque {  
				in torque = transmissionInputPort.torque;  
				out torque = driveshaftOutputPort.torque;  
			}  
		}  
		interface transmissionToDriveshaft connect transmission.transmissionOutputPort to driveshaft.transmissionInputPort {  
			ref flow references controlPower.torqueFlow2  
				from source.torque to target.torque;  
		}  

		part leftWheel {  
			port wheelInputPort: ~DriveshaftOutputPort;  
			perform controlPower.distributeTorque {  
				in torque = wheelInputPort.torque;  
				out wheelTorque = wheelInputPort.torque;  
			}  
		}  
		interface driveshaftToLeftWheel connect driveshaft.driveshaftOutputPort to leftWheel.wheelInputPort {  
			ref flow references controlPower.torqueFlow3  
				from source.torque to target.torque;  
		}  

		part rightWheel {  
			port wheelInputPort: ~DriveshaftOutputPort;  
			perform controlPower.distributeTorque {  
				in torque = wheelInputPort.torque;  
				out wheelTorque = wheelInputPort.torque;  
			}  
		}  
		interface driveshaftToRightWheel connect driveshaft.driveshaftOutputPort to rightWheel.wheelInputPort {  
			ref flow references controlPower.torqueFlow3  
				from source.torque to target.torque;  
		}  
	}  

	action controlPower {  
		action sendFuelCmd { out fuelCmd: FuelCmd; }  
		action sendShutdownCmd { out shutdownCmd: ShutdownCmd; }  
		succession flow shutdownFlow from sendShutdownCmd.shutdownCmd to generateEngineTorque.shutdownCmd;  
		succession flow fuelCmdFlow from sendFuelCmd.fuelCmd to generateEngineTorque.fuelCmd;  
		succession flow fuelCmdFlow to amplifyTorque.fuelCmd;  

		action generateEngineTorque {  
			in fuelCmd: FuelCmd;  
			in shutdownCmd: ShutdownCmd;  
			out engineState: EngineState;  
			out torque: EngineTorque;  
		}  
		succession flow stateFlow from generateEngineTorque.engineState to amplifyTorque.engineState;  
		succession flow torqueFlow1 from generateEngineTorque.torque to amplifyTorque.torque;  

		action amplifyTorque {  
			in fuelCmd: FuelCmd;  
			in engineState: EngineState;  
			in torque: EngineTorque;  
			out torque: TransmissionTorque;  
		}  
		succession flow torqueFlow2 from amplifyTorque.torque to transmitTorque.torque;  

		action transmitTorque {  
			in torque: TransmissionTorque;  
			out torque: DriveshaftTorque;  
		}  
		succession flow torqueFlow3 from transmitTorque.torque to distributeTorque.torque;  

		action distributeTorque {  
			in torque: DriveshaftTorque;  
			out wheelTorque: WheelTorque;  
		}  
	}  
}

package 'AutomotivePowertrain' {
	attribute def FuelControlCmd;
	attribute def EngineControlCmd;
	attribute def StallSignal;
	attribute def Torque;
	attribute def AmplifiedTorque;
	attribute def TransmittedTorque;
	attribute def DistributedTorque;
	attribute def WheelTorque;

	port def FuelCmdPort {
		out fuelCmd : FuelControlCmd;
	}
	port def EngineControlPort {
		out engineControlCmd : EngineControlCmd;
	}
	port def StallSignalPort {
		in stallSignal : StallSignal;
	}
	port def WheelTorquePort {
		out wheelTorque : WheelTorque;
	}

	part context {
		part user {
			port fuelCmdPort: FuelCmdPort;
			perform powertrainControl.sendFuelCmd {
				out fuelCmd = fuelCmdPort.fuelCmd;
			}
			port engineControlPort: EngineControlPort;
			perform powertrainControl.sendEngineControlCmd {
				out engineControlCmd = engineControlPort.engineControlCmd;
			}
		}
		interface userToPowertrain connect user.fuelCmdPort to powertrain.fuelCmdPort {
			ref flow references powertrainControl.fuelCmdFlow
				from source.fuelCmd to target.fuelCmd;
		}
		interface userToPowertrain2 connect user.engineControlPort to powertrain.engineControlPort {
			ref flow references powertrainControl.engineControlFlow
				from source.engineControlCmd to target.engineControlCmd;
		}
		part powertrain {
			port fuelCmdPort: ~FuelCmdPort;
			port engineControlPort: ~EngineControlPort;
			port stallSignalPort: ~StallSignalPort;
			port wheelTorquePort: WheelTorquePort;
			perform powertrainControl.receiveFuelCmd {
				in fuelCmd = fuelCmdPort.fuelCmd;
			}
			perform powertrainControl.receiveEngineControlCmd {
				in engineControlCmd = engineControlPort.engineControlCmd;
			}
			perform powertrainControl.startEngine {
				in fuelCmd = receiveFuelCmd.fuelCmd;
				in engineControlCmd = receiveEngineControlCmd.engineControlCmd;
				out torque = generateTorque.torque;
			}
			perform powertrainControl.generateTorque {
				in torqueInput = startEngine.torque;
				out torqueOutput = amplifyTorque.torque;
			}
			perform powertrainControl.amplifyTorque {
				in torque = generateTorque.torqueOutput;
				out amplified = transmitTorque.amplified;
			}
			perform powertrainControl.transmitTorque {
				in amplified = amplifyTorque.amplified;
				out transmitted = distributeTorque.transmitted;
			}
			perform powertrainControl.distributeTorque {
				in transmitted = transmitTorque.transmitted;
				out distributed = deliverToWheels.distributed;
			}
			perform powertrainControl.deliverToWheels {
				in distributed = distributeTorque.distributed;
				out wheelTorque = wheelTorquePort.wheelTorque;
			}
			perform powertrainControl.detectStall {
				in stallSignal = stallSignalPort.stallSignal;
			}
			perform powertrainControl.haltProcesses {
				in stallSignal = detectStall.stallSignal;
			}
		}
	}
	action powertrainControl {
		action sendFuelCmd { out fuelCmd: FuelControlCmd; }
		action receiveFuelCmd { in fuelCmd: FuelControlCmd; }
		succession flow fuelCmdFlow from sendFuelCmd.fuelCmd to receiveFuelCmd.fuelCmd;
		action sendEngineControlCmd { out engineControlCmd: EngineControlCmd; }
		action receiveEngineControlCmd { in engineControlCmd: EngineControlCmd; }
		succession flow engineControlFlow from sendEngineControlCmd.engineControlCmd to receiveEngineControlCmd.engineControlCmd;
		action startEngine { in fuelCmd: FuelControlCmd; in engineControlCmd: EngineControlCmd; out torque: Torque; }
		succession flow startEngineFlow from receiveFuelCmd.fuelCmd to startEngine.fuelCmd;
		succession flow engineControlToStart from receiveEngineControlCmd.engineControlCmd to startEngine.engineControlCmd;
		action generateTorque { in torqueInput: Torque; out torqueOutput: Torque; }
		succession flow generateTorqueFlow from startEngine.torque to generateTorque.torqueInput;
		action amplifyTorque { in torque: Torque; out amplified: AmplifiedTorque; }
		succession flow amplifyFlow from generateTorque.torqueOutput to amplifyTorque.torque;
		action transmitTorque { in amplified: AmplifiedTorque; out transmitted: TransmittedTorque; }
		succession flow transmitFlow from amplifyTorque.amplified to transmitTorque.amplified;
		action distributeTorque { in transmitted: TransmittedTorque; out distributed: DistributedTorque; }
		succession flow distributeFlow from transmitTorque.transmitted to distributeTorque.transmitted;
		action deliverToWheels { in distributed: DistributedTorque; out wheelTorque: WheelTorque; }
		succession flow deliverFlow from distributeTorque.distributed to deliverToWheels.distributed;
		action detectStall { in stallSignal: StallSignal; }
		succession flow stallSignalFlow from detectStall.stallSignal to haltProcesses.stallSignal;
		action haltProcesses { in stallSignal: StallSignal; }
	}
}

