		}
		interface engineToMonitor connect engine.temperaturePort to monitor.temperaturePort {
			ref flow references fuelSupplyRegion.temperatureFlow
				from source.temperature to target.temperature;
		}
		part engine {
			port supplyPort: ~FuelSupplyPort;
			port returnPort: FuelReturnPort;
			port temperaturePort: TemperaturePort;
			perform fuelSupplyRegion.processFuel {
				in fuel = supplyPort.fuel;
				out fuel = returnPort.fuel;
				out temperature = temperaturePort.temperature;
			}
		}
		part monitor {
			port temperaturePort: ~TemperaturePort;
			perform fuelSupplyRegion.monitorTemperature {
				in temperature = temperaturePort.temperature;
			}
		}
	}
	action fuelSupplyRegion {
		action supplyFuel { out fuel: Fuel; }
		succession flow supplyFlow from supplyFuel.fuel to processFuel.fuel;
		action processFuel { in fuel; out fuel: Fuel; out temperature: Temperature; }
		succession flow returnFlow from processFuel.fuel to returnFuel.fuel;
		succession flow temperatureFlow from processFuel.temperature to monitorTemperature.temperature;
		action returnFuel { in fuel: Fuel; }
		action monitorTemperature { in temperature: Temperature; }
	}
}

package 'FuelDeliverySystem' {
	attribute def Fuel;
	attribute def MaxCapacity;
	attribute def CurrentFuelLevel;
	port def StorageFuelPort {
		out fuel: Fuel;
	}
	port def PumpFuelPort {
		in fuel: Fuel;
		out fuel: Fuel;
	}
	port def VehicleFuelPort {
		in fuel: Fuel;
	}
	part context {
		part storageTank {
			port fuelPort: StorageFuelPort;
			perform fuelDelivery.extractFuel {
				out fuel = fuelPort.fuel;
			}
		}
		interface storageToPump connect storageTank.fuelPort to fuelPump.fuelPort {
			ref flow references fuelDelivery.fuelFlow1
				from source.fuel to target.fuel; 
		}
		part fuelPump {
			port fuelPort: ~PumpFuelPort;
			perform fuelDelivery.pumpFuel {
				in fuel = fuelPort.fuel;
				out fuel = vehicleFuelPort.fuel;
			}
			port vehicleFuelPort: PumpFuelPort;
		}
		interface pumpToVehicle connect fuelPump.vehicleFuelPort to vehicle.fuelTank.fuelPort {
			ref flow references fuelDelivery.fuelFlow2
				from source.fuel to target.fuel; 
		}
		part vehicle {
			part fuelTank {
				port fuelPort: VehicleFuelPort;
				perform fuelDelivery.deliverFuel {
					in fuel = fuelPort.fuel;
				}
				attribute maxCapacity: MaxCapacity;
				attribute currentFuelLevel: CurrentFuelLevel;
				perform fuelDelivery.monitorLevel {
					in maxCapacity = maxCapacity;
					in currentFuelLevel = currentFuelLevel;
				}
			}
		}
	}
	action fuelDelivery {
		action extractFuel { out fuel: Fuel; }
		succession flow fuelFlow1 from extractFuel.fuel to pumpFuel.fuel;
		action pumpFuel { in fuel: Fuel; out fuel: Fuel; }
		succession flow fuelFlow2 from pumpFuel.fuel to deliverFuel.fuel;
		action deliverFuel { in fuel: Fuel; }
		action monitorLevel { in maxCapacity: MaxCapacity; in currentFuelLevel: CurrentFuelLevel; }
	}
}

package 'AutomobileAssembly' {
	attribute def VehicleWithoutGearboxAndEngine;
	attribute def VehicleWithGearboxWithoutEngine;
	attribute def FullyAssembledVehicle;
	attribute def Gearbox;
	attribute def Engine;
	port def StartAssemblyPort {
		out vehicle: VehicleWithoutGearboxAndEngine;
	}
	port def GearboxInstallationPort {
		in vehicle: VehicleWithoutGearboxAndEngine;
		in gearbox: Gearbox;
		out vehicle: VehicleWithGearboxWithoutEngine;
	}
	port def EngineInstallationPort {
		in vehicle: VehicleWithGearboxWithoutEngine;
		in engine: Engine;
		out vehicle: FullyAssembledVehicle;
	}
	port def FinalVehiclePort {
		out vehicle: FullyAssembledVehicle;
	}
	part context {
		part assemblyLine {
			part initialVehicle {
				port startPort: StartAssemblyPort;
				perform startAssembly {
					out vehicle = startPort.vehicle;
				}
			}
			interface initialToGearbox connect initialVehicle.startPort to gearboxInstallation.inPort {
				ref flow references startAssembly.vehicle to installGearbox.vehicle;
				ref flow references installGearbox.gearbox to gearboxInstallation.gearbox;
			}
			part gearboxInstallation {
				port inPort: ~GearboxInstallationPort;
				port outPort: GearboxInstallationPort;
				perform installGearbox {
					in vehicle = inPort.vehicle;
					in gearbox = inPort.gearbox;
					out vehicle = outPort.vehicle;
				}
			}
			interface gearboxToEngine connect gearboxInstallation.outPort to engineInstallation.inPort {
				ref flow references installGearbox.vehicle to installEngine.vehicle;
				ref flow references installEngine.engine to engineInstallation.engine;
			}
			part engineInstallation {
				port inPort: ~EngineInstallationPort;
				port outPort: EngineInstallationPort;
				perform installEngine {
					in vehicle = inPort.vehicle;
					in engine = inPort.engine;
					out vehicle = outPort.vehicle;
				}
			}
			part finalVehicle {
				port finalPort: FinalVehiclePort;
				perform finalizeVehicle {
					in vehicle = engineInstallation.outPort.vehicle;
					out vehicle = finalPort.vehicle;
				}
				part gearbox;
				part engine;
			}
		}
	}
	action assemblyProcess {
		action startAssembly { out vehicle: VehicleWithoutGearboxAndEngine; }
		succession flow toGearbox from startAssembly.vehicle to installGearbox.vehicle;
		action installGearbox { in vehicle; in gearbox: Gearbox; out vehicle: VehicleWithGearboxWithoutEngine; }
		succession flow toEngine from installGearbox.vehicle to installEngine.vehicle;
		action installEngine { in vehicle; in engine: Engine; out vehicle: FullyAssembledVehicle; }
		succession flow toFinal from installEngine.vehicle to finalizeVehicle.vehicle;
		action finalizeVehicle { in vehicle; out vehicle: FullyAssembledVehicle; }
	}
}

package 'VehicleControlSystem' {
    signal StartSignal;
    signal VehicleOnSignal;
    signal OverTemperatureSignal;
    signal RestoreToNormalSignal;
    signal MaintenanceSignal;

    port def StartSignalPort {
        in startSignal: StartSignal;
    }

    port def VehicleOnSignalPort {
        in vehicleOnSignal: VehicleOnSignal;
    }

    port def OverTemperatureSignalPort {
        out overTemperatureSignal: OverTemperatureSignal;
    }

    port def RestoreToNormalSignalPort {
        in restoreToNormalSignal: RestoreToNormalSignal;
    }

    port def MaintenanceSignalPort {
        in maintenanceSignal: MaintenanceSignal;
    }

    part context VehicleA {
        port startSignalPort: StartSignalPort;
        port vehicleOnSignalPort: VehicleOnSignalPort;
        port overTemperatureSignalPort: ~OverTemperatureSignalPort;
        port restoreToNormalSignalPort: RestoreToNormalSignalPort;
        port maintenanceSignalPort: MaintenanceSignalPort;

        state machine operationStates {
            initial state Off;
            state Off;
            state Starting;
            state Running;
            state Degraded;
            state Maintenance;
            state HealthManagement;

            transition from Off to Starting when startSignalPort.startSignal and BrakePedalPressed;
            transition from Starting to Running when vehicleOnSignalPort.vehicleOnSignal;

            exit Running when exit, perform applyParkingBrake;

            transition from Running to Degraded when temperature > threshold;
            transition from Degraded to Normal when restoreToNormalSignalPort.restoreToNormalSignal;
            transition from Maintenance to Normal when restoreToNormalSignalPort.restoreToNormalSignal;

            transition from Running to Maintenance when maintenanceSignalPort.maintenanceSignal;
            transition from Running to HealthManagement when healthCheckTrigger;
            transition from HealthManagement to Running when healthCheckComplete;
        }
    }
}

package 'VehicleSystem' {
	attribute def StartupSignal;
	attribute def BrakePressed;
	attribute def Temperature;
	attribute def WarningSignal;
	attribute def HealthStatus;
	attribute def FuelLevel;
	attribute def MaintenanceInterval;
	attribute def TempThreshold;
	port def ControlInputPort {
		in startupSignal : StartupSignal;
		in brakePressed : BrakePressed;
	}
	port def StatusOutputPort {
		out healthStatus : HealthStatus;
		out warningSignal : WarningSignal;
	}
	port def TemperaturePort {
		in temperature : Temperature;
	}
	port def ConfigurationPort {
		in fuelLevel : FuelLevel;
		in maintenanceInterval : MaintenanceInterval;
		in tempThreshold : TempThreshold;
	}
	part context {
		part vehicle {
			port controlInputPort: ControlInputPort;
			port temperaturePort: TemperaturePort;
			port statusOutputPort: StatusOutputPort;
			perform startupProcess.startup {
				in startupSignal = controlInputPort.startupSignal;
				in brakePressed = controlInput-port.brakePressed;
			}
			perform healthManagement.detectOverheat {
				in temperature = temperaturePort.temperature;
			}
			perform healthManagement.maintenance {
				in healthStatus = healthManagement.healthStatus;
			}
			perform healthManagement.faultReCOVERY {
				in healthStatus = healthManagement.healthStatus;
			}
		}
		interface vehicleToController connect vehicle.statusOutputPort to controller.statusINPUTPort {
			ref flow references healthManagement.statusFlow
				from source.healthStatus to target.healthStatus;
			ref flow references healthManagement.warningFlow
				from source.warningSignal to target.warningSignal;
		}
		part controller {
			port statusInputPort: ~StatusOutputPort;
			port configurationPort: ~ConfigurationPort;
			perform controlOperations.processStartup {
				in startupSignal = vehicle.controlInputPort.startupSignal;
				in brakePressed = vehicle.controlInputPort.brakePressed;
				out healthStatus = statusInputPort.healthStatus;
			}
			perform controlOperations.processShutdown {
				in healthStatus = statusInputPort.healthStatus;
			}
			perform controlOperations.configureSystem {
				in fuelLevel = configurationPort.fuelLevel;
				in maintenanceInterval = configurationPort.maintenanceInterval;
				in tempThreshold =$ configurationPort.tempThreshold;
			}
		}
	}
	action startupProcess {
		action startup {
			in startupSignal : StartupSignal;
			in brakePressed : BrakePressed;
			out healthStatus : HealthStatus;
		}
		succession flow startupFlow from startup to selfInspection;
		action.selfInspection {
			in healthStatus;
			out powerSupplyStatus : PowerSupplyStatus;
		}
		succession flow powerSupplyFlow from selfInspection.powerSupplyStatus to.powerSupply;
		action powerSupply {
			in powerSupplyStatus;
			out healthStatus : HealthStatus;
		$
	}
	action healthManagement {
		action detectOverheat {
			in temperature : Temperature;
			out warningSignal : WarningSignal;
			out healthStatus : HealthStatus;
		}
		succession flow statusFlow from detectOverheat.healthStatus to maintenance.healthStatus;
		succession flow warningFlow from detectOverheat.warningSignal to processStartup.warningSignal;
		action maintenance {
			in healthStatus : HealthStatus;
			out maintenanceStatus : MaintenanceStatus;
		}
		succession flow recoveryFlow from maintenance.maintenanceStatus to faultRecovery.healthStatus;
		action faultReCOVERY {
			in healthStatus : HealthStatus;
			out operationalStatus : OperationalStatus;
		}
	}
	action controlOperations {
		action processStartup {
			in startupSignal : StartupSignal;
			in brakePressed : BrakePressed;
			out healthStatus : HealthStatus;
			out warningSignal : WarningSignal;
		}
		succession flow statusFlow from processStartup.healthStatus to vehicle.healthStatus;
	succession flow warningFlow from processStartup.warningSignal to vehicle.warningSignal;
		action processShutdown {
			in healthStatus : HealthStatus;
			out shutdownCommand : ShutdownCommand;
		}
		action configureSystem {
			in fuelLevel : FuelLevel;
			in maintenanceInterval : MaintenanceInterval;
			in tempThreshold : TempThreshold;
		}
	}
}

package 'VehicleDynamicsSimulation' {  
	attribute def VehicleMass;  
	attribute def VehiclePosition;  
	attribute def VehicleVelocity;  
	attribute def VehicleAcceleration;  
	attribute def RoadSlope;  
	attribute def RoadFriction;  
	attribute def TimePoint;  
	attribute def StateCmd;  
	attribute def Snapshot;  

	port def VehicleParamPort {  
		out mass: VehicleMass;  
		out position: VehiclePosition;  
		out velocity: VehicleVelocity;  
		out acceleration: VehicleAcceleration;  
	}  
	port def RoadParamPort {  
		out slope: RoadSlope;  
		out friction: RoadFriction;  
	}  
	port def SimulationCmdPort {  
		out startCmd: StateCmd;  
		out time: TimePoint;  
	}  
	port def SnapshotPort {  
		out snapshot: Snapshot;  
	}  

	part context {  
		part user {  
			port vehicleParamPort: VehicleParamPort;  
			port roadParamPort: RoadParamPort;  
			port simulationCmdPort: SimulationCmdPort;  

			perform simulateVehicleDynamics.defineVehicleParams {  
				out mass = vehicleParamPort.mass;  
				out position = vehicleParamPort.position;  
				out velocity = vehicleParamPort.velocity;  
				out acceleration = vehicleParamPort.acceleration;  
			}  
			perform simulateVehicleDynamics.defineRoadParams {  
				out slope = roadParamPort.slope;  
				out friction = roadParamPort.friction;  
			}  
			perform simulateVehicleDynamics.initiateSimulation {  
				out startCmd = simulationCmdPort.startCmd;  
				out time = simulationCmdPort.time;  
			}  
		}  

		interface userToVehicle connect user.vehicleParamPort to vehicle.vehicleParamPort {  
			ref flow references simulateVehicleDynamics.vehicleParamFlow  
				from source.mass to target.mass,  
				from source.position to target.position,  
				from source.velocity to target.velocity,  
				from source.acceleration to target.acceleration;  
		}  
		interface userToRoad connect user.roadParamPort to road.roadParamPort {  
			ref flow references simulateVehicleDynamics.roadParamFlow  
				from source.slope to target.slope,  
				from source.friction to target.friction;  
		}  
		interface userToSimulation connect user.simulationCmdPort to simulationEngine.simulationCmdPort {  
			ref flow references simulateVehicleDynamics.simulationCmdFlow  
				from source.startCmd to target.startCmd,  
				from source.time to target.time;  
		}  

		part vehicle {  
			port vehicleParamPort: ~VehicleParamPort;  
			port statePort: SimulationCmdPort;  

			perform simulateVehicleDynamics.updateVehicleState {  
				in mass = vehicleParamPort.mass;  
				in position = vehicleParamPort.position;  
				in velocity = vehicleParamPort.velocity;  
				in acceleration = vehicleParamPort.acceleration;  
				in startCmd = statePort.startCmd;  
				in time = statePort.time;  
				out state = statePort.state;  
			}  
		}  

		part road {  
			port roadParamPort: ~RoadParamPort;  
			port statePort: SimulationCmdPort;  

			perform simulateVehicleDynamics.updateRoadState {  
				in slope = roadParamPort.slope;  
				in friction = roadParamPort.friction;  
				in time = statePort.time;  
				out state = statePort.state;  
			}  
		}  

		part simulationEngine {  
			port simulationCmdPort: ~SimulationCmdPort;  
			port snapshotPort: SnapshotPort;  

			perform simulateVehicleDynamics.recordSnapshot {  
				in vehicleState = vehicle.statePort.state;  
				in roadState = road.statePort.state;  
				in time = simulationCmdPort.time;  
				out snapshot = snapshotPort.snapshot;  
			}  
			perform simulateVehicleDynamics.aggregateSnapshots {  
				in snapshots = snapshotPort.snapshot;  
				out stateTransitions: StateCmd;  
			}  
		}  
	}  

	action simulateVehicleDynamics {  
		action defineVehicleParams {  
			out mass: VehicleMass;  
			out position: VehiclePosition;  
			out velocity: VehicleVelocity;  
			out acceleration: VehicleAcceleration;  
		}  
		action defineRoadParams {  
			out slope: RoadSlope;  
			out friction: RoadFriction;  
		}  
		action initiateSimulation {  
			out startCmd: StateCmd;  
			out time: TimePoint;  
		}  
		succession flow vehicleParamFlow from defineVehicleParams to updateVehicleState;  
		succession flow roadParamFlow from defineRoadParams to updateRoadState;  
		succession flow simulationCmdFlow from initiateSimulation to updateVehicleState.startCmd, updateRoadState.time;  

		action updateVehicleState {  
			in mass: VehicleMass;  
			in position: VehiclePosition;  
			in velocity: VehicleVelocity;  
			in acceleration: VehicleAcceleration;  
			in startCmd: StateCmd;  
			in time: TimePoint;  
			out state: StateCmd;  
		}  
		action updateRoadState {  
			in slope: RoadSlope;  
			in friction: RoadFriction;  
			in time: TimePoint;  
			out state: StateCmd;  
		}  
		action recordSnapshot {  
			in vehicleState: StateCmd;  
			in roadState: StateCmd;  
			in time: TimePoint;  
			out snapshot: Snapshot;  
		}  
		succession flow snapshotFlow from updateVehicleState.state to recordSnapshot.vehicleState;  
		succession flow snapshotFlow from updateRoadState.state to recordSnapshot.roadState;  
		succession flow snapshotFlow from initiateSimulation.time to recordSnapshot.time;  

		action aggregateSnapshots {  
			in snapshots: Snapshot*;  
			out stateTransitions: StateCmd;  
		}  
		succession flow snapshotAggregation from recordSnapshot.snapshot to aggregateSnapshots.snapshots;  
	}  
}

package 'AutomotiveConfigurations' {
	block Automobile {
		part subA: SubsystemA;
		part subB: SubsystemB;
		constraint configConstraint {
			if (subA.variant == SubsystemA::Config2) then
				subB.variant == SubsystemB::Config2;
		}
	}

	block SubsystemA {
		variant Config1 {
			part comp1: Component1;
			part comp2: Component2;
		}
		variant Config2 {
			part comp2: Component2;
			part comp3: Component3;
		}
	}

	block SubsystemB {
		variant Config1 {
			part comp4: Component4;
			part comp5: Component5;
		}
		variant Config2 {
			part comp5: Component5;
			part comp6: Component6;
		}
	}

	block ConfigurationA {
		variant of Automobile {
			select subA.variant = SubsystemA::Config1;
			select subB.variant = SubsystemB::Config1;
		}
	}

	block ConfigurationB {
		variant of Automobile {
			select subA.variant = SubsystemA::Config2;
			select subB.variant = SubsystemB::Config2;
		}
	}
}

