package 'VehicleConfiguration' {  
	attribute def EngineType;  
	attribute def TransmissionType;  
	attribute def WheelType;  
	attribute def PerformanceLevel;  
	attribute def UserPreferences;  
	port def ConfigRequestPort {  
		out performanceLevel: PerformanceLevel;  
		out userPreferences: UserPreferences;  
	}  
	port def ComponentConfigPort {  
		out engineType: EngineType;  
		out transmissionType: TransmissionType;  
		out wheelType: WheelType;  
	}  
	part context {  
		part user {  
			port configRequestPort: ConfigRequestPort;  
			perform configureVehicle.sendConfigRequest {  
				out performanceLevel = configRequestPort.performanceLevel;  
				out userPreferences = configRequestPort.userPreferences;  
			}  
		}  
		interface userToSystem connect user.configRequestPort to system.configRequestPort {  
			ref flow references configureVehicle.requestFlow  
				from source.performanceLevel to target.performanceLevel,  
				from source.userPreferences to target.userPreferences;  
		}  
		part system {  
			port configRequestPort: ~ConfigRequestPort;  
			perform configureVehicle.determineConfiguration {  
				in performanceLevel = configRequestPort.performanceLevel;  
				in userPreferences = configRequestPort.userPreferences;  
				out engineType = componentConfigPort.engineType;  
				out transmissionType = componentConfigPort.transmissionType;  
				out wheelType = componentConfigPort.wheelType;  
			}  
			port componentConfigPort: ComponentConfigPort;  
			perform configureVehicle.checkConstraints {  
				in engineType = componentConfigPort.engineType;  
				in transmissionType = componentConfigPort.transmissionType;  
				in wheelType = componentConfigPort.wheelType;  
			}  
		}  
		part engine {  
			port componentPort: ~ComponentConfigPort;  
		}  
		part transmission {  
			port componentPort: ~ComponentConfigPort;  
		}  
		part wheels {  
			port componentPort: ~ComponentConfigPort;  
		}  
	}  
	action configureVehicle {  
		action sendConfigRequest {  
			out performanceLevel: PerformanceLevel;  
			out userPreferences: UserPreferences;  
		}  
		succession flow requestFlow from sendConfigRequest.performanceLevel to determineConfiguration.performanceLevel,  
			from sendConfigRequest.userPreferences to determineConfiguration.userPreferences;  
		action determineConfiguration {  
			in performanceLevel;  
			in userPreferences;  
			out engineType: EngineType;  
			out transmissionType: TransmissionType;  
			out wheelType: WheelType;  
		}  
		succession flow configFlow from determineConfiguration.engineType to selectEngine.engineType,  
			from determineConfiguration.transmissionType to selectTransmission.transmissionType,  
			from determineConfiguration.wheelType to selectWheels.wheelType;  
		action selectEngine { out engineType: EngineType; }  
		action selectTransmission { out transmissionType: TransmissionType; }  
		action selectWheels { out wheelType: WheelType; }  
		action checkConstraints {  
			in engineType: EngineType;  
			in transmissionType: TransmissionType;  
			in wheelType: WheelType;  
		}  
	}  
}

package 'AutomobileSystem' {
