	attribute def MaxMassLimitation;
	attribute def DurabilityConstraint;
	port def VehicleMassPort {
		out mass: VehicleMass;
	}
	port def FuelStatusPort {
		out fuelStatus: FuelStatus;
	}
	port def DriveInterfacePort {
		out torque: Torque;
	}
	port def ReliabilityPort {
		out reliability: Reliability;
	}
	part context {
		part vehicle {
			port massPort: VehicleMassPort;
			port fuelStatusPort: FuelStatusPort;
			port reliabilityPort: ReliabilityPort;
			perform checkMassCompliance.validateMass {
				in mass = massPort.mass;
				in fuelStatus = fuelStatusPort.fuelStatus;
				ref constraint references maxMassConstraint.MaxMassLimitation;
			}
			perform ensureDurability.validateLifecycle {
				out reliability = reliabilityPort.reliability;
				ref constraint references durabilityConstraint.DurabilityConstraint;
			}
		}
		interface vehicleToEngine connect vehicle.massPort to engine.massPort {
			ref flow references checkMassCompliance.massFlow;
		}
		part engine {
			port massPort: ~VehicleMassPort;
			port drivePort: DriveInterfacePort;
			perform produceTorque.deliverPower {
				out torque = drivePort.torque;
			}
