			perform receiveTorque.transferPower {
				in torque = drivePort.torque;
			}
		}
	}
	action checkMassCompliance {
		action validateMass {
			in mass: VehicleMass;
			in fuelStatus: FuelStatus;
			ref constraint MaxMassLimitation;
		}
		action produceTorque {
			out torque: Torque;
		}
		succession flow massFlow from validateMass.mass to receiveTorque.mass;
