		}
		part vehicle {
			port commandPort: ~CommandPort;
			port massMeasurementPort: MassMeasurementPort;
			perform measureVehicleMass.provideActualMass {
				in startTest = commandPort.startTest;
				out actualMass = massMeasurementPort.actualMass;
			}
		}
		interface vehicleToWeighingEquipment connect vehicle.massMeasurementPort to weighingEquipment.massInputPort {
			ref flow references measureVehicleMass.massFlow
				from source.actualMass to target.actualMass;
		}
		part weighingEquipment {
			port massInputPort: ~MassMeasurementPort;
			port systemMassPort: ~MassMeasurementPort;
			perform measureVehicleMass.transmitMeasuredMass {
				in actualMass = massInputPort.actualMass;
				out measuredMass = system.massMeasurementPort.actualMass;
			}
		}
		part system {
			port massMeasurementPort: MassMeasurementPort;
			port complianceResultPort: ComplianceResultPort;
			perform measureVehicleMass.compareMass {
				in measuredMass = massMeasurementPort.actualMass;
				in maxMass = config.maxPermissibleMass;
				out comparisonResult;
			}
			perform measureVehicleMass.determineCompliance {
				in comparisonResult;
				out complianceStatus = complianceResultPort.complianceStatus;
			}
		}
	}
	action measureVehicleMass {
		action sendStartTest { out startTest: Boolean; }
		succession flow testCommandFlow from sendStartTest.startTest to provideActualMass.startTest;
		action provideActualMass { in startTest; out actualMass: ActualMass; }
		succession flow massFlow from provideActualMass.actualMass to transmitMeasuredMass.actualMass;
		action transmitMeasuredMass { in actualMass; out measuredMass: ActualMass; }
		succession flow massComparisonFlow from transmitMeasuredMass.measuredMass to compareMass.measuredMass;
		action compareMass { 
			in measuredMass: ActualMass; 
			in maxPermissibleMass: MaxPermissibleMass; 
			out comparisonResult: Boolean; 
		}
		succession flow complianceFlow from compareMass.comparisonResult to determineCompliance.comparisonResult;
		action determineCompliance { 
			in comparisonResult: Boolean; 
			out complianceStatus: ComplianceStatus; 
		}
	}
}

