package 'VehicleQualitySystem' {
	attribute def Mass;
	attribute def TotalMass;
	attribute def QualityRequirement;
	attribute def AnalysisUseCase;
	attribute def AnalysisObjective;
	attribute def AnalysisTask;
	attribute def AnalysisPlan;
	port def MassPort {
		out mass: Mass;
	}
	port def TotalMassPort {
		out totalMass: TotalMass;
	}
	port def QualityRequirementPort {
		out qualityRequirement: QualityRequirement;
	}
	port def AnalysisUseCasePort {
		out analysisUseCase: AnalysisUseCase;
	}
	port def AnalysisObjectivePort {
		out analysisObjective: AnalysisObjective;
	}
	port def AnalysisTaskPort {
		out analysisTask: AnalysisTask;
	}
	port def AnalysisPlanPort {
		out analysisPlan: AnalysisPlan;
	}
	part context {
		part vehicle {
			part engine {
				port massPort: MassPort;
				perform qualityAnalysis.aggregateTotalMass.engineMass {
					out mass = massPort.mass;
				}
			}
			part transmission {
				port massPort: MassPort;
				perform qualityAnalysis.aggregateTotalMass.transmissionMass {
					out mass = massPort.mass;
				}
			}
			part frontAxle {
				port massPort: MassPort;
				perform qualityAnalysis.aggregateTotalMass.frontAxleMass {
					out mass = massPort.mass;
				}
			}
			part rearAxle {
				port massPort: MassPort;
				perform qualityAnalysis.aggregateTotalMass.rearAxleMass {
					out mass = massPort.mass;
				}
			}
			port totalMassPort: TotalMassPort;
		}
		interface componentToAnalyzer connect engine.massPort to qualityAnalyzer.massInputPorts.engineMass {
			ref flow references qualityAnalysis.aggregateTotalMass.engineMassFlow
				from source.mass to target.engineMass;
		}
		interface componentToAnalyzer connect transmission.massPort to qualityAnalyzer.massInputPorts.transmissionMass {
			ref flow references qualityAnalysis.aggregateTotalMass.transmissionMassFlow
				from source.mass to target.transmissionMass;
		}
		interface componentToAnalyzer connect frontAxle.massPort to qualityAnalyzer.massInputPorts.frontAxleMass {
			ref flow references qualityAnalysis.aggregateTotalMass.frontAxleMassFlow
				from source.mass to target.frontAxleMass;
		}
		interface componentToAnalyzer connect rearAxle.massPort to qualityAnalyzer.massInputPorts.rearAxleMass {
			ref flow references qualityAnalysis.aggregateTotalMass.rearAxleMassFlow
				from source.mass to target.rearAxleMass;
		}
		interface analyzerToVehicle connect qualityAnalyzer.totalMassPort to vehicle.totalMassPort {
			ref flow references qualityAnalysis.aggregateTotalMass.totalMassFlow
				from source.totalMass to target.totalMass;
		}
		part qualityAnalyzer {
			port massInputPorts: MassPort[4];
			port totalMassPort: ~TotalMassPort;
			perform qualityAnalysis.aggregateTotalMass {
				in engineMass = massInputPorts[0].mass;
				in transmissionMass = massInputPorts[1].mass;
				in frontAxleMass = massInputPorts[2].mass;
				in rearAxleMass = massInputPorts[3].mass;
				out totalMass = totalMassPort.totalMass;
			}
			perform qualityAnalysis.defineQualityRequirements {
				out qualityRequirement = qualityRequirementPort.qualityRequirement;
			}
			port qualityRequirementPort: QualityRequirementPort;
			perform qualityAnalysis.formulateAnalysisUseCases {
				in qualityRequirement = qualityRequirementPort.qualityRequirement;
				out analysisUseCase = analysisUseCasePort.analysisUseCase;
			}
			port analysisUseCasePort: AnalysisUseCasePort;
			perform qualityAnalysis.generateAnalysisTasks {
				in analysisObjective = analysisObjectivePort.analysisObjective;
				in analysisUseCase = analysisUseCasePort.analysisUseCase;
				out analysisTask = analysisTaskPort.analysisTask;
				out analysisPlan = analysisPlanPort.analysisPlan;
			}
			port analysisObjectivePort: AnalysisObjectivePort;
			port analysisTaskPort: AnalysisTaskPort;
			port analysisPlanPort: AnalysisPlanPort;
		}
	}
	action qualityAnalysis {
		action aggregateTotalMass {
			in engineMass: Mass;
			in transmissionMass: Mass;
			in frontAxleMass: Mass;
			in rearAxleMass: Mass;
			out totalMass: TotalMass;
		}
		action defineQualityRequirements { out qualityRequirement: QualityRequirement; }
		action formulateAnalysisUseCases {
			in qualityRequirement: QualityRequirement;
			out analysisUseCase: AnalysisUseCase;
		}
		action generateAnalysisTasks {
			in analysisObjective: AnalysisObjective;
			in analysisUseCase: AnalysisUseCase;
			out analysisTask: AnalysisTask;
			out analysisPlan: AnalysisPlan;
		}
		succession flow massAggregationFlow from aggregateTotalMass.totalMass to formulateAnalysisUseCases.analysisUseCase;
		succession flow requirementFlow from defineQualityRequirements.qualityRequirement to formulateAnalysisUseCases.qualityRequirement;
		succession flow useCaseFlow from formulateAnalysisUseCases.analysisUseCase to generateAnalysisTasks.analysisUseCase;
		succession flow objectiveFlow from analysisObjective to generateAnalysisTasks.analysisObjective;
	}
}

package 'VehiclePowertrainConfigurator' {  
	attribute def Power;  
	attribute def Mass;  
	attribute def Efficiency;  
	attribute def Reliability;  
	attribute def Cost;  
	attribute def EvaluationResult;  
	attribute def Explanation;  

	port def EngineDataPort {  
		out power: Power;  
		out mass: Mass;  
		out efficiency: Efficiency;  
		out reliability: Reliability;  
		out cost: Cost;  
	}  
	port def RecommendationPort {  
		out evaluationResult: EvaluationResult;  
		out explanation: Explanation;  
	}  

	part context {  
		part configurator {  
			port engineDataPort: ~EngineDataPort;  
			perform tradeOffAnalysis.sendEngineData {  
				out power = engineDataPort.power;  
				out mass = engineDataPort.mass;  
				out efficiency = engineDataPort.efficiency;  
				out reliability = engineDataPort.reliability;  
				out cost = engineDataPort.cost;  
			}  
			perform recommendEngine.generateRecommendation {  
				in evaluationResult = tradeOffAnalysis.evaluationResult;  
				out evaluationResult = recommendationPort.evaluationResult;  
				out explanation = recommendationPort.explanation;  
			}  
		}  
		interface engineToEvaluator connect engine.engineDataPort to configurator.engineDataPort {  
			ref flow references tradeOffAnalysis.engineDataFlow  
				from source.power to target.power,  
				from source.mass to target.mass,  
				from source.efficiency to target.efficiency,  
				from source.reliability to target.reliability,  
				from source.cost to target.cost;  
		}  
		part engine {  
			port engineDataPort: EngineDataPort;  
			perform tradeOffAnalysis.produceEngineData {  
				out power = engineDataPort.power;  
				out mass = engineDataPort.mass;  
				out efficiency = engineDataPort.efficiency;  
				out reliability = engineDataPort.reliability;  
				out cost = engineDataPort.cost;  
			}  
		}  
		part evaluationModel {  
			port engineDataPort: ~EngineDataPort;  
			port recommendationPort: ~RecommendationPort;  
			perform tradeOffAnalysis.analyzeTradeOffs {  
				in power = engineDataPort.power;  
				in mass = engineDataPort.mass;  
				in efficiency = engineDataPort.efficiency;  
				in reliability = engineDataPort.reliability;  
				in cost = engineDataPort.cost;  
				out evaluationResult = recommendationPort.evaluationResult;  
				out explanation = recommendationPort.explanation;  
			}  
		}  
	}  

	action tradeOffAnalysis {  
		action sendEngineData {  
			out power: Power;  
			out mass: Mass;  
			out efficiency: Efficiency;  
			out reliability: Reliability;  
			out cost: Cost;  
		}  
		succession flow engineDataFlow from sendEngineData.power to analyzeTradeOffs.power,  
			sendEngineData.mass to analyzeTradeOffs.mass,  
			sendEngineData.efficiency to analyzeTradeOffs.efficiency,  
			sendEngineData.reliability to analyzeTradeOffs.reliability,  
			sendEngineData.cost to analyzeTradeOffs.cost;  
		action analyzeTradeOffs {  
			in power: Power;  
			in mass: Mass;  
			in efficiency: Efficiency;  
			in reliability: Reliability;  
			in cost: Cost;  
			out evaluationResult: EvaluationResult;  
			out explanation: Explanation;  
		}  
		succession flow analysisResultFlow from analyzeTradeOffs.evaluationResult to generateRecommendation.evaluationResult;  
		action generateRecommendation {  
			in evaluationResult: EvaluationResult;  
			out evaluationResult: EvaluationResult;  
			out explanation: Explanation;  
		}  
	}  
}

package 'VehicleFuelEconomy' {
	attribute def mpgUrban;
	attribute def mpgHighway;
	attribute def usGallon = 231; // cubic inches
	attribute def testLoad = 1000; // pounds
	port def FuelEconomyPort {
		out mpgUrban: mpgUrban;
		out mpgHighway: mpgHighway;
	}
	port def TestScenarioPort {
		in mpg: mpgUrban or mpgHighway;
		in load: testLoad;
		in conditions: String;
	}
	part context {
		part vehicle {
			port fuelEconomyPort: FuelEconomyPort;
			perform calculateFuelEconomy {
				out mpgUrban = fuelEconomyPort.mpgUrban;
				out mpgHighway = fuelEconomyPort.mpgHighway;
			}
		}
		interface urbanTestScenario connect vehicle.fuelEconomyPort to urbanTest.fuelEconomyPort {
			ref flow references validateUrbanScenario.mpgFlow
				from source.mpgUrban to target.mpg;
			ref flow references validateUrbanScenario.loadFlow
				from source.testLoad to target.load;
		}
		interface highwayTestScenario connect vehicle.fuelEconomyPort to highwayTest.fuelEconomyPort {
			ref flow references validateHighwayScenario.mpgFlow
				from source.mpgHighway to target.mpg;
			ref flow references validateHighwayScenario.loadFlow
				from source.testLoad to target.load;
		}
		part urbanTest {
			port fuelEconomyPort: ~FuelEconomyPort;
			perform validateUrbanScenario {
				in mpg: vehicle.mpgUrban;
				in load: testLoad;
				constraint assert mpg >= 25;
			}
		}
		part highwayTest {
			port fuelEconomyPort: ~FuelEconomyPort;
			perform validateHighwayScenario {
				in mpg: vehicle.mpgHighway;
				in load: testLoad;
				constraint assert mpg >= 30;
			}
		}
	}
	action calculateFuelEconomy {
		action calculateFuelEconomy {
			in powertrain: String;
			in load: testLoad;
			in conditions: String;
			out mpgUrban: mpgUrban;
			out mpgHighway: mpgHighway;
		}
		succession flow mpgFlow from calculateFuelEconomy.mpgUrban to validateUrbanScenario.mpg;
		succession flow mpgFlow from calculateFuelEconomy.mpgHighway to validateHighwayScenario.mpg;
		succession flow loadFlow from calculateFuelEconomy.load to validateUrbanScenario.load;
		succession flow loadFlow from calculateFuelEconomy.load to validateHighwayScenario.load;
	}
}

package 'VehicleDynamicsSimulation' {
	attribute def vehicleMass;
	attribute def initialPosition;
	attribute def initialVelocity;
	attribute def powerOutputData;
	attribute def timeStep;
	attribute def accelerationSequence;
	attribute def velocityVariationSequence;
	attribute def displacementSequence;
	port def VehicleMassPort {
		out vehicleMass : vehicleMass;
	}
	port def InitialPositionPort {
		out initialPosition : initialPosition;
	}
	port def InitialVelocityPort {
		out initialVelocity : initialVelocity;
	}
	port def PowerOutputPort {
		out powerOutputData : powerOutputData;
	}
	port def TimeStepPort {
		out timeStep : timeStep;
	}
	port def AccelerationSequencePort {
		out accelerationSequence : accelerationSequence;
	}
	port def VelocityVariationSequencePort {
		out velocityVariationSequence : velocityVariationSequence;
	}
	port def DisplacementSequencePort {
		out displacementSequence : displacementSequence;
	}
	part context {
		part user {
			port vehicleMassPort: VehicleMassPort;
			port initialPositionPort: InitialPositionPort;
			port initialVelocityPort: InitialVelocityPort;
			port powerOutputPort: PowerOutputPort;
			port timeStepPort: TimeStepPort;
			perform simulateLongitudinalMotion.sendInputs {
				out vehicleMass = vehicleMassPort.vehicleMass;
				out initialPosition = initialPositionPort.initialPosition;
				out initialVelocity = initialVelocityPort.initialVelocity;
				out powerOutputData = powerOutputPort.powerOutputData;
				out timeStep = timeStepPort.timeStep;
			}
		}
		interface userToSimulation connect user.vehicleMassPort to simulationSystem.vehicleMassPort, user.initialPositionPort to simulationSystem.initialPositionPort, user.initialVelocityPort to simulationSystem.initialVelocityPort, user.powerOutputPort to simulationSystem.powerOutputPort, user.timeStepPort to simulationSystem.timeStepPort {
			ref flow references simulateLongitudinalMotion.inputFlow, simulateLongitudinalMotion.accelerationFlow, simulateLongitudinalMotion.velocityFlow, simulateLongitudinalMotion.positionFlow, simulateLongitudinalMotion.sequenceFlow;
		}
		part simulationSystem {
			port vehicleMassPort: ~VehicleMassPort;
			port initialPositionPort: ~InitialPositionPort;
			port initialVelocityPort: ~InitialVelocityPort;
			port powerOutputPort: ~PowerOutputPort;
			port timeStepPort: ~TimeStepPort;
			perform simulateLongitudinalMotion.calculateAcceleration {
				in vehicleMass = vehicleMassPort.vehicleMass;
				in powerOutput = powerOutputPort.powerOutputData;
				in timeStep = timeStepPort.timeStep;
				out acceleration = accelerationPort.acceleration;
			}
			perform simulateLongitudinalMotion.updateVelocity {
				in acceleration = accelerationPort.acceleration;
				in initialVelocity = initialVelocityPort.initialVelocity;
				in timeStep = timeStepPort.timeStep;
				out velocity = velocityPort.velocity;
			}
			perform simulateLongitudinalMotion.updatePosition {
				in velocity = velocityPort.velocity;
				in initialPosition = initialPositionPort.initialPosition;
				in timeStep = timeStepPort.timeStep;
				out position = positionPort.position;
			}
			port accelerationPort: acceleration;
			port velocityPort: velocity;
			port positionPort: position;
			port outputPort: AccelerationSequencePort, VelocityVariationSequencePort, DisplacementSequencePort;
			perform simulateLongitudinalMotion.generateSequences {
				in acceleration = accelerationPort.acceleration;
				in velocity = velocityPort.velocity;
				in position = positionPort.position;
				out accelerationSequence = outputPort.accelerationSequence;
				out velocityVariationSequence = outputPort.velocityVariationSequence;
				out displacementSequence = outputPort.displacementSequence;
			}
		}
	}
	action simulateLongitudinalMotion {
		action sendInputs { 
			out vehicleMass: vehicleMass; 
			out initialPosition: initialPosition; 
			out initialVelocity: initialVelocity; 
			out powerOutputData: powerOutputData; 
			out timeStep: timeStep; 
		}
		succession flow inputFlow from sendInputs.vehicleMass to calculateAcceleration.vehicleMass;
		succession flow inputFlow2 from sendInputs.initialPosition to updatePosition.initialPosition;
		succession flow inputFlow3 from sendInputs.initialVelocity to updateVelocity.initialVelocity;
		succession flow inputFlow4 from sendInputs.powerOutputData to calculateAcceleration.powerOutput;
		succession flow inputFlow5 from sendInputs.timeStep to calculateAcceleration.timeStep;
		action calculateAcceleration { 
			in vehicleMass; 
			in powerOutput; 
			in timeStep; 
			out acceleration: acceleration; 
		}
		succession flow accelerationFlow from calculateAcceleration.acceleration to updateVelocity.acceleration;
		action updateVelocity { 
			in acceleration; 
			in initialVelocity; 
			in timeStep; 
			out velocity: velocity; 
		}
		succession flow velocityFlow from updateVelocity.velocity to updatePosition.velocity;
		action updatePosition { 
			in velocity; 
			in initialPosition; 
			in timeStep; 
			out position: position; 
		}
		succession flow positionFlow from updatePosition.position to generateSequences.position;
		action generateSequences { 
			in acceleration; 
			in velocity; 
			in position; 
			out accelerationSequence: accelerationSequence; 
			out velocityVariationSequence: velocityVariationSequence; 
			out displacementSequence: displacementSequence; 
		}
	}
}

