			ref flow references manageAndVisualize.securityFlow
				from source.componentType to target.componentType;
		}
		part managementSystem {
			port safetyPort: ~SafetyComponentPort;
			port securityPort: ~SecurityComponentPort;
			perform manageAndVisualize.organizeAndVisualize;
		}
	}
	action manageAndVisualize {
		action collectMandatorySafety { out componentType = 'Safety'; out mandatoryStatus = 'Mandatory'; }
		action collectOptionalSafety { out componentType = 'Safety'; out mandatoryStatus = 'Optional'; }
		action collectSecurity { out componentType = 'Security'; }
		succession flow safetyFlow from collectMandatorySafety.componentType to organizeSafety.safetyComponents;
		succession flow safetyFlow from collectOptionalSafety.componentType to organizeSafety.safetyComponents;
		succession flow securityFlow from collectSecurity.componentType to organizeSecurity.securityComponents;
		action organizeSafety { in safetyComponents; in mandatoryStatus; out mandatorySafety; out optionalSafety; }
		action organizeSecurity { in securityComponents; out securityList; }
		succession flow organizeSafetyFlow from organizeSafety.mandatorySafety to visualizeTree.mandatorySafety;
		succession flow organizeSafetyFlow from organizeSafety.optionalSafety to visualizeTree.optionalSafety;
		succession flow organizeSecurityFlow from organizeSecurity.securityList to visualizeTree.security;
		action visualizeTree { in mandatorySafety; in optionalSafety; in security; out treeStructure; }
		action visualizeTable { in mandatorySafety; in optionalSafety; in security; out tableFormat; }
	}
}

package 'LayeredSystem' {
	attribute def x;
	attribute def y;
	attribute def z;
	z depends on x, y;

	port def AppServicePort {
		out command : Command;
	}

	port def ServiceDataPort {
		out dataRequest : DataRequest;
		in dataResponse : DataResponse;
	}

	port def DataPort {
		in dataRequest : DataRequest;
		out dataResponse : DataResponse;
	}

	part context {
		part application {
			port appServicePort: AppServicePort;
			perform handleRequest.sendCommand {
				out command = appServicePort.command;
			}
		}

		interface appToService connect application.appServicePort to service.serviceAppPort {
			ref flow references handleRequest.commandFlow
				from source.command to target.command;
		}

		part service {
			port serviceAppPort: ~AppServicePort;
			port serviceDataPort: ServiceDataPort;
			perform handleRequest.processCommand {
				in command = serviceAppPort.command;
				out dataRequest = serviceDataPort.dataRequest;
			}
			perform handleRequest.computeZ {
				in x, y;
				out z;
			}
		}

		interface serviceToData connect service.serviceDataPort to data.dataServicePort {
			ref flow references handleRequest.dataRequestFlow
				from source.dataRequest to target.dataRequest;
			ref flow references handleRequest.dataResponseFlow
				from source.dataResponse to target.dataResponse;
		}

		part data {
			port dataServicePort: ~ServiceDataPort;
			perform handleRequest.storeRetrieveData {
				in dataRequest = dataServicePort.dataRequest;
				out dataResponse = dataServicePort.dataResponse;
			}
		}
	}

	action handleRequest {
		action sendCommand { out command: Command; }
		succession flow commandFlow from sendCommand.command to processCommand.command;

		action processCommand { in command; out dataRequest: DataRequest; in dataResponse: DataResponse; }
		succession flow dataRequestFlow from processCommand.dataRequest to storeRetrieveData.dataRequest;
		succession flow dataResponseFlow from storeRetrieveData.dataResponse to processCommand.dataResponse;

		action storeRetrieveData { in dataRequest; out dataResponse; }

		action computeZ { in x, y; out z; }
		succession flow xFlow from processCommand.dataResponse to computeZ.x;
		succession flow yFlow from processCommand.dataResponse to computeZ.y;
	}
}

package 'PowertrainTorqueSystem' {
	attribute def TorqueCmd;
	attribute def Torque;
	port def TorqueCmdPort {
		out torqueCmd : TorqueCmd;
	}
	port def TorqueOutputPort {
		out torque: Torque;
	}
	part context {
		part logicLayer {
			port torqueCmdPort: TorqueCmdPort;
			perform torqueGenerationRegion.generateTorqueCmd {
				out torqueCmd = torqueCmdPort.torqueCmd;
			}
		}
		interface logicToPhysical connect logicLayer.torqueCmdPort to physicalLayer.torqueCmdPort {
			ref flow references torqueGenerationRegion.torqueCmdFlow
				from source.torqueCmd to target.torqueCmd;
		}
		part physicalLayer {
			port torqueCmdPort: ~TorqueCmdPort;
			perform torqueGenerationRegion.produceTorque {
				in torqueCmd = torqueCmdPort.torqueCmd;
				out torque = engine.torqueOutputPort.torque;
			}
			part engine {
				port torqueOutputPort: TorqueOutputPort;
			}
		}
	}
	action torqueGenerationRegion {
		action generateTorqueCmd { out torqueCmd: TorqueCmd; }
		succession flow torqueCmdFlow from generateTorqueCmd.torqueCmd to produceTorque.torqueCmd;
		action produceTorque { in torqueCmd: TorqueCmd; out torque: Torque; }
		post torque > 0;
	}
}

package 'PowerSystem' {
	attribute def Power;
	attribute def Torque;
	port def TorqueCmdPort {
		out torqueCmd : Torque;
	}
	port def PowerPort {
		out power: Power;
	}
	part context {
		part system {
			port torqueCmdPort: TorqueCmdPort;
			perform powerGeneration.outputPower {
				out power = powerPort.power;
			}
		}
		interface systemToTorqueGenerator connect system.torqueCmdPort to torqueGenerator.torqueCmdPort {
			ref flow references powerGeneration.torqueCmdFlow
				from source.torqueCmd to target.torqueCmd;
		}
		part torqueGenerator {
			port torqueCmdPort: ~TorqueCmdPort;
			perform powerGeneration.generateTorque {
				out torqueCmd = torqueCmdPort.torqueCmd;
			}
		}
		interface torqueGeneratorToPowertrain connect torqueGenerator.enginePowerPort to powertrain.engineInputPort {
			ref flow references powerGeneration.powerFlow
				from source.power to target.power;
		}
		part powertrain {
			port engineInputPort: PowerPort;
			port systemPowerPort: ~PowerPort;
			perform powerGeneration.transmitPower {
				in power = engineInputPort.power;
				out power = systemPowerPort.power;
			}
		}
		interface powertrainToEngine connect powertrain.engineInputPort to engine.powerOutPort {
			ref flow references powerGeneration.powerFlow
				from source.power to target.power;
		}
		part engine {
			port powerOutPort: ~PowerPort;
			perform powerGeneration.executeTorqueGeneration {
				in torqueCmd = torqueGenerator.torqueCmdPort.torqueCmd;
				out power = powerOutPort.power;
			}
		}
	}
	action powerGeneration {
		action generateTorque { out torqueCmd: Torque; }
		succession flow torqueCmdFlow from generateTorque.torqueCmd to executeTorqueGeneration.torqueCmd;
		action executeTorqueGeneration { in torqueCmd: Torque; out power: Power; }
		succession flow powerFlow from executeTorqueGeneration.power to transmitPower.power;
		action transmitPower { in power: Power; out power: Power; }
		succession flow powerFlow from transmitPower.power to outputPower.power;
		action outputPower { in power: Power; out power: Power; }
	}
}

