package 'FuelSystem' {
	attribute def Fuel;
	attribute def TemperatureData;
	port def FuelSupplyPort {
		out fuel: Fuel;
	}
	port def FuelReturnPort {
		in fuel: Fuel;
	}
	port def TemperatureSensorPort {
		out temperatureData: TemperatureData;
	}
	port def TemperatureDataPort {
		out temperatureData: TemperatureData;
	}
	part context {
		part vehicle {
			port temperatureDataPort: TemperatureDataPort;
			perform fuelSupplyChain.returnExcessFuel {
				out temperatureData = temperatureDataPort.temperatureData;
			}
		}
		interface vehicleToFuelSystem connect vehicle.temperatureDataPort to fuelTankAssembly.temperatureSensorPort {
			ref flow references fuelSupplyChain.temperatureFlow
				from source.temperatureData to target.temperatureData;
		}
		part fuelTankAssembly {
			port temperatureSensorPort: ~TemperatureSensorPort;
			perform fuelSupplyChain.pressurizeAndDeliverFuel {
				out fuel = fuelPump.supplyPort.fuel;
				in temperatureData = temperatureSensorPort.temperatureData;
			}
			perform fuelSupplyChain.returnExcessFuel {
				in fuel = fuelPump.returnPort.fuel;
				out temperatureData = temperatureSensorPort.temperatureData;
			}
		}
		part fuelPump {
			port supplyPort: FuelSupplyPort;
			port returnPort: FuelReturnPort;
		}
		part fuelTank {
			port fuelInPort: FuelReturnPort;
			port fuelOutPort: FuelSupplyPort;
		}
		interface tankToPump connect fuelTank.fuelOutPort to fuelPump.supplyPort {
			ref flow references fuelSupplyChain.fuelFlow
				from source.fuel to target.fuel;
		}
		interface pumpToTank connect fuelPump.returnPort to fuelTank.fuelInPort {
			ref flow references fuelSupplyChain.returnFlow
				from source.fuel to target.fuel;
		}
		part fuelConsumingUnits {
			port inletPort: FuelSupplyPort;
			port outletPort: FuelReturnPort;
		}
		interface pumpToConsumers connect fuelPump.supplyPort to fuelConsumingUnits.inletPort {
			ref flow references fuelSupplyChain.fuelFlow
				from source.fuel to target.fuel;
		}
		interface consumersToPump connect fuelConsumingUnits.outletPort to fuelPump.returnPort {
			ref flow references fuelSupplyChain.returnFlow
				from source.fuel to target.fuel;
		}
	}
	action fuelSupplyChain {
		action pressurizeAndDeliverFuel { 
			in temperatureData: TemperatureData; 
			out fuel: Fuel; 
		}
		succession flow fuelFlow from pressurizeAndDeliverFuel.fuel to deliverFuelToConsumers.fuel;
		action deliverFuelToConsumers { 
			in fuel; 
			out fuel; 
		}
		succession flow returnFlow from deliverFuelToConsumers.fuel to returnExcessFuel.fuel;
		action returnExcessFuel { 
			in fuel: Fuel; 
			out temperatureData: TemperatureData; 
		}
		succession flow temperatureFlow from returnExcessFuel.temperatureData to transmitTemperatureData.temperatureData;
		action transmitTemperatureData { 
			in temperatureData: TemperatureData; 
		}
	}
}

