package 'AutomotiveECU' {  
	attribute def VehicleState;  
	attribute def EngineState;  
	attribute def SensorData;  
	attribute def Message;  

	port def BusIFPort {  
		out sendMsg : Message;  
		in receiveMsg : Message;  
	}  

	port def SensorIFPort {  
		out sensorData : SensorData;  
	}  

	part context {  
		part vcu {  
			port busIFPort: BusIFPort;  
			port sensorIFPort: SensorIFPort;  
			perform controlActions.monitorVehicleState {  
				out vehicleState = vcu.vehicleState;  
			}  
			perform controlActions.sendVCUCommand {  
				out sendMsg = busIFPort.sendMsg;  
			}  
		}  

		interface vcuToBusIF connect vcu.busIFPort to vcuBusIF.busIFPort {  
			ref flow references controlActions.vcuCmdFlow  
				from source.sendMsg to target.receiveMsg;  
		}  

		part vcuBusIF {  
			port busIFPort: ~BusIFPort;  
			perform controlActions.transmitVCUMessage {  
				in receiveMsg = busIFPort.receiveMsg;  
				out sendMsg = canBus.busIFPort1.sendMsg;  
			}  
		}  

		interface busIFToCAN connect vcuBusIF.busIFPort to canBus.busIFPort1 {  
			ref flow references controlActions.vcuMsgFlow  
				from source.sendMsg to target.receiveMsg;  
		}  

		part ecu {  
			port busIFPort: BusIFPort;  
			port sensorIFPort: SensorIFPort;  
			perform controlActions.monitorEngineState {  
				out engineState = ecu.engineState;  
			}  
			perform controlActions.sendECUCommand {  
				out sendMsg = busIFPort.sendMsg;  
			}  
		}  

		interface ecuToBusIF connect ecu.busIFPort to ecuBusIF.busIFPort {  
			ref flow references controlActions.ecuCmdFlow  
				from source.sendMsg to target.receiveMsg;  
		}  

		part ecuBusIF {  
			port busIFPort: ~BusIFPort;  
			perform controlActions.transmitECUMessage {  
				in receiveMsg = busIFPort.receiveMsg;  
				out sendMsg = canBus.busIFPort2.sendMsg;  
			}  
		}  

		interface busIFToCAN2 connect ecuBusIF.busIFPort to canBus.busIFPort2 {  
			ref flow references controlActions.ecuMsgFlow  
				from source.sendMsg to target.receiveMsg;  
		}  

		part canBus {  
			port busIFPort1: ~BusIFPort;  
			port busIFPort2: ~BusIFPort;  
			port reservedSensorPort: SensorIFPort;  
		}  
	}  

	action controlActions {  
		action monitorVehicleState { out vehicleState: VehicleState; }  
		action monitorEngineState { out engineState: EngineState; }  

		succession flow vcuCmdFlow from monitorVehicleState.vehicleState to sendVCUCommand.input;  
		action sendVCUCommand { in input: VehicleState; out sendMsg: Message; }  

		succession flow vcuMsgFlow from sendVCUCommand.sendMsg to transmitVCUMessage.receiveMsg;  
		action transmitVCUMessage { in receiveMsg: Message; out sendMsg: Message; }  

		succession flow ecuCmdFlow from monitorEngineState.engineState to sendECUCommand.input;  
		action sendECUCommand { in input: EngineState; out sendMsg: Message; }  

		succession flow ecuMsgFlow from sendECUCommand.sendMsg to transmitECUMessage.receiveMsg;  
		action transmitECUMessage { in receiveMsg: Message; out sendMsg: Message; }  
	}  
}

package 'BloodGlucoseMeter' {
	attribute def BatteryStatus;
	attribute def Alert;
	attribute def Measurement;
	attribute def EmergencyResponse;

	port def BatteryStatusPort {
		in batteryStatus: BatteryStatus;
	}
	port def AlertPort {
		out alert: Alert;
	}
	port def MeasurementPort {
		out measurement: Measurement;
	}
	port def EmergencyResponsePort {
		out emergencyResponse: EmergencyResponse;
	}

	part context {
		part user {
			port alertPort: AlertPort;
			perform batteryMonitoring.sendAlert {
				in alert = alertPort.alert;
			}
		}
		interface userToMeter connect user.alertPort to meter.alertPort {
			ref flow references batteryMonitoring.alertFlow
				from source.alert to target.alert;
		}
		part meter {
			port batteryStatusPort: BatteryStatusPort;
			port alertPort: ~AlertPort;
			port measurementPort: MeasurementPort;
			perform batteryMonitoring.detectBatteryStatus {
				in batteryStatus = batteryStatusPort.batteryStatus;
			}
			perform measurementProcess.measureBloodGlucose {
				in batteryStatus = batteryStatusPort.batteryStatus;
				out measurement = measurementPort.measurement;
			}
		}
		interface meterToEmergency connect meter.alertPort to emergencyResponseSystem.alertPort {
			ref flow references batteryMonitoring.alertFlow
				from source.alert to target.alert;
		}
		interface meterToEmergencyMeasurement connect meter.measurementPort to emergencyResponseSystem.measurementPort {
			ref flow references measurementProcess.measurementFlow
				from source.measurement to target.measurement;
		}
		part emergencyResponseSystem {
			port alertPort: ~AlertPort;
			port measurementPort: ~MeasurementPort;
			port emergencyResponsePort: EmergencyResponsePort;
			perform measurementProcess.monitorMeasurement {
				in measurement = measurementPort.measurement;
				out abnormality = handleEmergency.abnormality;
			}
			perform emergencyHandling.handleEmergency {
				in alert = alertPort.alert;
				in abnormality = monitorMeasurement.abnormality;
				out emergencyResponse = emergencyResponsePort.emergencyResponse;
			}
		}
	}

	action batteryMonitoring {
		action detectBatteryStatus { in batteryStatus: BatteryStatus; }
		succession flow alertFlow from detectBatteryStatus.batteryStatus to sendAlert.batteryStatus;
		action sendAlert { in batteryStatus; out alert: Alert; }
	}
	action measurementProcess {
		action measureBloodGlucose { in batteryStatus: BatteryStatus; out measurement: Measurement; }
		succession flow measurementFlow from measureBloodGlucose.measurement to monitorMeasurement.measurement;
		action monitorMeasurement { in measurement; out abnormality: Boolean; }
	}
	action emergencyHandling {
		action handleEmergency { in alert: Alert; in abnormality: Boolean; out emergencyResponse: EmergencyResponse; }
	}
}

