    attribute def OuterDiameter with unit mm, in;

    part context {
        part vehicle {
            attribute mass: 1200 kg;
            attribute length: 4.82 m;
            part frontWheels {
                part leftWheel {
                    attribute hubDiameter: 18 in;
                    attribute width: 245 mm;
                    attribute outerDiameter: OuterDiameter;
                    part tire {
                        attribute height: TireHeight;
                    }
                    perform calculateWheelDimensions {
                        in hubDiameter = this.hubDiameter;
                        in tireHeight = tire.height;
                        out outerDiameter = this.outerDiameter;
                    }
                }
                part rightWheel {
                    attribute hubDiameter: 18 in;
                    attribute width: 245 mm;
                    attribute outerDiameter: OuterDiameter;
                    part tire {
                        attribute height: TireHeight;
                    }
                    perform calculateWheelDimensions {
                        in hubDiameter = this.hubDiameter;
                        in tireHeight = tire.height;
                        out outerDiameter = this.outerDiameter;
                    }
                }
            }
        }
    }

    action calculateWheelDimensions {
        in hubDiameter: HubDiameter;
        in tireHeight: TireHeight;
        out outerDiameter: OuterDiameter;
    }

    succession flow leftHubFlow from vehicle.frontWheels.leftWheel.hubDiameter to calculateWheelDimensions.hubDiameter;
    succession flow leftTireFlow from vehicle.frontWheels.leftWheel.tire.height to calculateWheelDimensions.tireHeight;
    succession flow leftOuterFlow from calculateWheelDimensions.outerDiameter to vehicle.frontWheels.leftWheel.outerDiameter;

    succession flow rightHubFlow from vehicle.frontWheels.rightWheel.hubDiameter to calculateWheelDimensions.hubDiameter;
    succession flow rightTireFlow from vehicle.frontWheels.rightWheel.tire.height to calculateWheelDimensions.tireHeight;
    succession flow rightOuterFlow from calculateWheelDimensions.outerDiameter to vehicle.frontWheels.rightWheel.outerDiameter;
}

package 'VehiclePowertrain' {
	attribute def isHighPerformance : Boolean;
	attribute def CylinderType : String;
	attribute def TransmissionType : String;
	part context {
		part engine {
			attribute cylinderType : CylinderType;
		}
		part transmission {
			attribute transmissionType : TransmissionType;
		}
		constraint highPerformanceEngine {
			if isHighPerformance then engine.cylinderType = "six" else engine.cylinderType = "four";
		}
		constraint validCombination {
			(engine.cylinderType = "four" and transmission.transmissionType = "manual") or (engine.cylinderType = "six" and transmission.transmissionType = "automatic");
		}
	}
}

package 'AutomotiveComponents' {
	attribute def CarMass = 1200 kg;
	attribute def CarLength = 4.82 m;
	attribute def TreadDepthMin = 3.5 mm;
	attribute def TreadDepthDefault = 6.0 mm;
	attribute def RimDiameter = 18 in;
	attribute def TireWidth = 245 mm;
	attribute def TireHeight = 45 mm;
	attribute def BrakeDiscRadius = 95 mm;

	part context {
		part car {
			property mass: CarMass;
			property length: CarLength;
			part wheelAssemblies[*] {
				part wheel {
					part rim {
						property diameter: RimDiameter;
					}
					part tire {
						property width: TireWidth;
						property height: TireHeight;
						property treadDepth: TreadDepthDefault;
					}
					property outerDiameter = rim.diameter * 25.4 + 2 * tire.height;
				}
				part brakeDisc {
					property radius: BrakeDiscRadius;
					property diameter = 2 * radius;
				}
				constraint brakeDiscFitment {
					parameter discDiameter = brakeDisc.diameter;
					parameter wheelOD = wheel.outerDiameter;
					discDiameter < wheelOD;
				}
				constraint treadDepthConstraint {
					parameter minTread = TreadDepthMin;
					tire.treadDepth >= minTread;
				}
			}
		}
	}
}

package 'AutomotiveFuelSystem' {
	attribute def Fuel;
	attribute def Temperature;
	port def FuelOutletPort {
		out fuel: Fuel;
		out temperature: Temperature;
	}
	port def FuelInletPort {
		in fuel: Fuel;
	}
	port def FuelPumpPort {
		in fuel: Fuel;
		out fuel: Fuel;
		in returnFuel: Fuel;
		in temperature: Temperature;
	}
	port def EngineFuelPort {
		in fuel: Fuel;
		out returnFuel: Fuel;
	}
	part context {
		part fuelTankAssembly {
			port outletPort: FuelOutletPort;
			perform manageFuelDelivery {
				out fuel = outletPort.fuel;
				out temperature = outletPort.temperature;
			}
		}
		interface assemblyToPump connect fuelTankAssembly.outletPort to fuelPump.fuelPumpPort {
			ref flow references manageFuelDelivery.fuelFlow
				from source.fuel to target.fuel;
			ref flow references manageFuelDelivery.temperatureFlow
				from source.temperature to target.temperature;
		}
		part fuelPump {
			port fuelPumpPort: FuelPumpPort;
			perform pumpFuel {
				in fuel = fuelPumpPort.fuel;
				out fuel = enginePort.fuel;
				in returnFuel = enginePort.returnFuel;
			}
			perform monitorTemperature {
				in temperature = fuelPumpPort.temperature;
				out temperature = transmitTemperature.temperature;
			}
			port enginePort: ~EngineFuelPort;
			perform handleFuelReturn {
				in returnFuel = enginePort.returnFuel;
				out returnFuel = fuelPumpPort.returnFuel;
			}
		}
		interface pumpToEngine connect fuelPump.enginePort to engine.fuelPort {
			ref flow references pumpFuel.fuelFlow
				from source.fuel to target.fuel;
			ref flow references handleFuelReturn.returnFuelFlow
				from source.returnFuel to target.returnFuel;
		}
		part fuelTank {
			port inletPort: FuelInletPort;
			perform supplyFuel {
				out fuel = inletPort.fuel;
			}
		}
		interface tankToAssembly connect fuelTank.inletPort to fuelTankAssembly.outletPort {
			ref flow references supplyFuel.fuelFlow
				from source.fuel to target.fuel;
		}
		part engine {
			port fuelPort: ~EngineFuelPort;
		}
		part monitoringUnit {
			perform transmitTemperature {
				in temperature: Temperature;
			}
		}
		interface pumpToMonitoring connect fuelPump.monitorTemperature to monitoringUnit.transmitTemperature {
			ref flow references monitorTemperature.temperatureFlow
				from source.temperature to target.temperature;
		}
	}
	action manageFuelDelivery {
		action supplyFuel { out fuel: Fuel; }
		succession flow fuelFlow from supplyFuel.fuel to pumpFuel.fuel;
		action pumpFuel { in fuel: Fuel; out fuel: Fuel; }
		succession flow returnFuelFlow from handleFuelReturn.returnFuel to supplyFuel.returnFuel;
		action monitorTemperature { in temperature: Temperature; out temperature: Temperature; }
	}
}

package 'PhysicalQuantitySystem' {
	attribute def Length;
	attribute def Mass;
	attribute def Time;
	attribute def ElectricCurrent;
	attribute def Temperature;
	attribute def AmountOfSubstance;
	attribute def LuminousIntensity;
	port def QuantityInputPort {
		in quantity: Length | Mass | Time | ElectricCurrent | Temperature | AmountOfSubstance | LuminousIntensity;
		in unit: String;
	}
	port def QuantityOutputPort {
		out convertedQuantity: Length | Mass | Time | ElectricCurrent | Temperature | AmountOfSubstance | LuminousIntensity;
		out convertedUnit: String;
	}
	interface def ImportInterface {
		import baseQuantities: Length, Mass, Time, ElectricCurrent, Temperature, AmountOfSubstance, LuminousIntensity;
	}
	interface def ExtensionInterface {
		extend derivedQuantities: Area, Volume, Speed;
	}
	part context {
		part user {
			port inputPort: QuantityInputPort;
			perform dataProcessing.receiveQuantity {
				in quantity = inputPort.quantity;
				in unit = inputPort.unit;
			}
		}
		interface userToSystem connect user.inputPort to system.inputPort {
			ref flow references dataProcessing.quantityFlow
				from source.quantity to target.quantity,
				from source.unit to target.unit;
		}
		part system {
			port inputPort: ~QuantityInputPort;
			port outputPort: QuantityOutputPort;
			perform dataProcessing.validateQuantity {
				in quantity = inputPort.quantity;
				in unit = inputPort.unit;
				out validatedQuantity = validatedQuantity;
				out baseUnit = baseUnit;
			}
			perform dataProcessing.convertUnits {
				in validatedQuantity;
				in baseUnit;
				in targetUnit = outputPort.convertedUnit;
				out convertedQuantity = outputPort.convertedQuantity;
			}
			perform importOperation.importBaseQuantities {
				ref importInterface: ImportInterface;
			}
			perform extensionOperation.extendQuantities {
				ref extensionInterface: ExtensionInterface;
			}
		}
	}
	action dataProcessing {
		action receiveQuantity { in quantity: Length | Mass | Time | ElectricCurrent | Temperature | AmountOfSubstance | LuminousIntensity; in unit: String; }
		succession flow quantityFlow from receiveQuantity.quantity to validateQuantity.quantity, from receiveQuantity.unit to validateQuantity.unit;
		action validateQuantity { in quantity; in unit; out validatedQuantity; out baseUnit: String; }
		succession flow validationFlow from validateQuantity.validatedQuantity to convertUnits.validatedQuantity, from validateQuantity.baseUnit to convertUnits.baseUnit;
		action convertUnits { in validatedQuantity; in baseUnit; in targetUnit: String; out convertedQuantity; }
		succession flow conversionFlow from convertUnits.convertedQuantity to outputQuantity.convertedQuantity;
		action outputQuantity { out convertedQuantity: Length | Mass | Time | ElectricCurrent | Temperature | AmountOfSubstance | LuminousIntensity; out convertedUnit: String; }
	}
	action importOperation {
		action importBaseQuantities {
			ref importInterface: ImportInterface;
			import baseQuantities from importInterface.baseQuantities;
		}
	}
	action extensionOperation {
		action extendQuantities {
			ref extensionInterface: ExtensionInterface;
			extend derivedQuantities to extensionInterface.derivedQuantities;
		}
	}
}

package 'UnitSystemLibrary' {  
	attribute def ConversionRequest;  
	attribute def ConversionResult;  
	attribute def ValidationStatus;  
	port def ConversionRequestPort {  
		out conversionRequest : ConversionRequest;  
	}  
	port def ConversionResultPort {  
		out conversionResult: ConversionResult;  
	}  
	port def ValidationPort {  
		out validationStatus: ValidationStatus;  
	}  
	part context {  
		part user {  
			port requestPort: ConversionRequestPort;  
			perform conversionRegion.sendConversionRequest {  
				out conversionRequest = requestPort.conversionRequest;  
			}  
		}  
		interface userToManager connect user.requestPort to manager.requestPort {  
			ref flow references conversionRegion.requestFlow  
				from source.conversionRequest to target.conversionRequest;  
		}  
		part manager {  
			port requestPort: ~ConversionRequestPort;  
			port resultPort: ConversionResultPort;  
			port validationPort: ValidationPort;  
			perform conversionRegion.validateUnits {  
				in conversionRequest = requestPort.conversionRequest;  
				out validationStatus = validationPort.validationStatus;  
			}  
			perform conversionRegion.convertToTargetUnit {  
				in conversionRequest = requestPort.conversionRequest;  
				out conversionResult = resultPort.conversionResult;  
			}  
		}  
		interface managerToISQ connect manager.resultPort to isq.unitPort {  
			ref flow references conversionRegion.resultFlow  
				from source.conversionResult to target.unitValue;  
		}  
		interface managerToUSCustomary connect manager.resultPort to usCustomary.unitPort {  
			ref flow references conversionRegion.resultFlow  
				from source.conversionResult to target.unitValue;  
		}  
		part isq {  
			port unitPort: ~ConversionResultPort;  
			attribute length: Double;  
			attribute mass: Double;  
			attribute time: Double;  
		}  
		part usCustomary {  
			port unitPort: ~ConversionResultPort;  
			attribute distance: Double;  
			attribute weight: Double;  
			attribute time: Double;  
		}  
	}  
	action conversionRegion {  
		action sendConversionRequest { out conversionRequest: ConversionRequest; }  
		succession flow requestFlow from sendConversionRequest.conversionRequest to validateUnits.conversionRequest;  
		action validateUnits { in conversionRequest: ConversionRequest; out validationStatus: ValidationStatus; }  
		succession flow validatedFlow from validateUnits.validationStatus to convertToTargetUnit.conversionRequest;  
		action convertToTargetUnit { in conversionRequest: ConversionRequest; out conversionResult: ConversionResult; }  
		succession flow resultFlow from convertToTargetUnit.conversionResult to sendConversionResult.conversionResult;  
		action sendConversionResult { out conversionResult: ConversionResult; }  
	}  
}

