package 'MaterialsSystem' {
	attribute def atomicMass unit kg/mol;
	attribute def massFraction unit dimensionless;
	attribute def tensileStrength unit N/mm;
	port def SubstancePort {
		out atomicMass: atomicMass;
	}
	port def AlloyComponentPort {
		out massFraction: massFraction;
		out atomicMass: atomicMass;
	}
	port def AlloyPropertyPort {
		out tensileStrength: tensileStrength;
	}
	part context {
		part iron {
			port substancePort: SubstancePort;
			perform defineSubstance.setAtomicMass {
				out atomicMass = substancePort.atomicMass;
			}
		}
		part carbon {
			port substancePort: SubstancePort;
			perform defineSubstance.setAtomicMass {
				out atomicMass = substancePort.atomicMass;
			}
		}
		part manganese {
			port substancePort: SubstancePort;
			perform defineSubstance.setAtomicMass {
				out atomicMass = substancePort.atomicMass;
			}
		}
		part steel980 {
			part ironComponent {
				port componentPort: AlloyComponentPort;
				perform defineAlloy.setMassFraction {
					out massFraction = componentPort.massFraction;
				}
				perform defineSubstance.setAtomicMass {
					out atomicMass = componentPort.atomicMass;
				}
			}
			part carbonComponent {
				port componentPort: AlloyComponentPort;
				perform defineAlloy.setMassFraction {
					out massFraction = componentPort.massFraction;
				}
				perform defineSubstance.setAtomicMass {
					out atomicMass = componentPort.atomicMass;
				}
			}
			part manganeseComponent {
				port componentPort: AlloyComponentPort;
				perform defineAlloy.setMassFraction {
					out massFraction = componentPort.massFraction;
				}
				perform defineSubstance.setAtomicMass {
					out atomicMass = componentPort.atomicMass;
				}
			}
			port propertyPort: AlloyPropertyPort;
			perform calculateTensileStrength.determineStrength {
				in ironMassFraction = ironComponent.componentPort.massFraction;
				in carbonMassFraction = carbonComponent.componentPort.massFraction;
				in manganeseMassFraction = manganeseComponent.componentPort.massFraction;
				in ironAtomicMass = ironComponent.componentPort.atomicMass;
				in carbonAtomicMass = carbonComponent.componentPort.atomicMass;
				in manganeseAtomicMass = manganeseComponent.componentPort.atomicMass;
				out strength = propertyPort.tensileStrength;
			}
		}
	}
	action defineSubstance {
		action setAtomicMass { out atomicMass: atomicMass; }
	}
	action defineAlloy {
		action setMassFraction { out massFraction: massFraction; }
	}
	action calculateTensileStrength {
		action determineStrength { 
			in ironMassFraction: massFraction; 
			in carbonMassFraction: massFraction; 
			in manganeseMassFraction: massFraction; 
			in ironAtomicMass: atomicMass; 
			in carbonAtomicMass: atomicMass; 
			in manganeseAtomicMass: atomicMass; 
			out tensileStrength: tensileStrength; 
		}
	}
	succession flow from defineSubstance.setAtomicMass to defineAlloy.setMassFraction;
	succession flow from defineAlloy.setMassFraction to calculateTensileStrength.determineStrength;
	succession flow from defineSubstance.setAtomicMass to calculateTensileStrength.determineStrength;
}

