	port def QueryPort {
		in queryMaterialCmd: QueryMaterialCmd;
		out materialData: MaterialData;
	}
	port def ComparisonPort {
		in compareMaterialsCmd: CompareMaterialsCmd;
		out comparisonResult: ComparisonResult;
	}
	port def PropertyPort {
		out tensileStrength: TensileStrength;
		out unit: Unit;
	}
	part context {
		part user {
			port dataEntryPort: DataEntryPort;
			perform materialManagement.addMaterial {
				out addMaterialCmd = dataEntryPort.addMaterialCmd;
			}
			port queryPort: QueryPort;
			perform materialManagement.queryMaterial {
				out queryMaterialCmd = queryPort.queryMaterialCmd;
			}
			port comparisonPort: ComparisonPort;
			perform materialManagement.compareMaterials {
				out compareMaterialsCmd = comparisonPort.compareMaterialsCmd;
			}
		}
		interface userDataEntry connect user.dataEntryPort to database.dataEntryPort {
			ref flow references materialManagement.addMaterialFlow
				from source.addMaterialCmd to target.addMaterialCmd;
		}
		interface userQuery connect user.queryPort to database.queryPort {
			ref flow references materialManagement.queryMaterialFlow
				from source.queryMaterialCmd to target.queryMaterialCmd;
			ref flow references materialManagement.materialDataFlow
				from source.materialData to target.materialData;
		}
		interface userComparison connect user.comparisonPort to database.comparisonPort {
			ref flow references materialManagement.compareMaterialsFlow
				from source.compareMaterialsCmd to target.compareMaterialsCmd;
			ref flow references materialManagement.comparisonResultFlow
				from source.comparisonResult to target.comparisonResult;
		}
		part database {
			port dataEntryPort: ~DataEntryPort;
			perform materialManagement.addMaterial {
				in addMaterialCmd = dataEntryPort.addMaterialCmd;
			}
			port queryPort: ~QueryPort;
			perform materialManagement.queryMaterial {
				in queryMaterialCmd = queryPort.queryMaterialCmd;
				out materialData = queryPort.materialData;
			}
			port comparisonPort: ~ComparisonPort;
			perform materialManagement.compareMaterials {
				in compareMaterialsCmd = comparisonPort.compareMaterialsCmd;
				out comparisonResult = comparisonPort.comparisonResult;
			}
			part materialStorage {
				property materials: Material[];
			}
		}
		part material {
			property atomicMass: AtomicMass;
			property fundamentalProperties: FundamentalProperty;
			property mechanicalProperties: MechanicalProperty;
		}
		part metal {
			extends material;
		}
		part alloy {
			extends material;
			part constituent1 {
				property massFraction: MassFraction;
			}
			part constituent2 {
				property massFraction: MassFraction;
			}
			part constituent3 {
				property massFraction: MassFraction;
			}
		}
		part steel980 {
			extends alloy;
			property name: String = "Steel_980";
			part iron {
				reference to metal;
				property massFraction: MassFraction = 0.96;
			}
			part carbon {
				reference to material;
				property massFraction: MassFraction = 0.025;
			}
			part manganese {
				reference to material;
				property massFraction: MassFraction = 0.015;
			}
			property tensileStrength: TensileStrength = 980;
			property unit: Unit = "N/mm";
		}
	}
	action materialManagement {
		action addMaterial { out addMaterialCmd: AddMaterialCmd; }
		succession flow addMaterialFlow from addMaterial.addMaterialCmd to addMaterialCmd.addMaterialCmd;
		action queryMaterial { out queryMaterialCmd: QueryMaterialCmd; }
		succession flow queryMaterialFlow from queryMaterial.queryMaterialCmd to queryMaterialCmd.queryMaterialCmd;
		succession flow materialDataFlow from retrieveMaterialData.materialData to queryPort.materialData;
		action compareMaterials { out compareMaterialsCmd: CompareMaterialsCmd; }
		succession flow compareMaterialsFlow from compareMaterials.compareMaterialsCmd to compareMaterialsCmd.compareMaterialsCmd;
		succession flow comparisonResultFlow from generateComparisonResult.comparisonResult to comparisonPort.comparisonResult;
		action retrieveMaterialData { in queryMaterialCmd; out materialData; }
		action generateComparisonResult { in compareMaterialsCmd; out comparisonResult; }
	}
}

package 'PubSubSystem' {  
	attribute def Message;  
	attribute def Topic;  
	attribute def Subscription;  
	port def PublishPort {  
		out message : Message;  
		out topic : Topic;  
	}  
	port def SubscribePort {  
		in topic : Topic;  
		out message : Message;  
	}  
	port def SubscriptionPort {  
		out subscription : Subscription;  
	}  
	part context {  
		part producer {  
			port publishPort: PublishPort;  
			perform publishMessage.sendPublish {  
				out message = publishPort.message;  
				out topic = publishPort.topic;  
			}  
		}  
		interface producerToServer connect producer.publishPort to server.publishPort {  
			ref flow references publishMessage.publishFlow  
				from source.message to target.message,  
				from source.topic to target.topic;  
		}  
		part server {  
			port publishPort: ~PublishPort;  
			port subscribePort: ~SubscribePort;  
			port subscriptionPort: SubscriptionPort;  
			perform publishMessage.receivePublish {  
				in message = publishPort.message;  
				in topic = publishPort.topic;  
				out subscription = subscriptionPort.subscription;  
			}  
			perform manageSubscriptions.updateSubscriptions {  
				in subscription = subscriptionPort.subscription;  
			}  
			perform distributeMessage.distribute {  
				in message = publishPort.message;  
				in topic = publishPort.topic;  
				out message = subscribePort.message;  
			}  
		}  
		interface serverToSubscriber connect server.subscribePort to subscriber.subscribePort {  
			ref flow references distributeMessage.distributionFlow  
				from source.message to target.message,  
				from source.topic to target.topic;  
		}  
		part subscriber {  
			port subscribePort: SubscribePort;  
			perform receiveMessage.deliverMessage {  
				in message = subscribePort.message;  
				in topic = subscribePort.topic;  
			}  
		}  
	}  
	action publishMessage {  
		action sendPublish { out message: Message; out topic: Topic; }  
		succession flow publishFlow from sendPublish.message to receivePublish.message,  
			sendPublish.topic to receivePublish.topic;  
		action receivePublish { in message: Message; in topic: Topic; out subscription: Subscription; }  
	}  
	action manageSubscriptions {  
		action updateSubscriptions { in subscription: Subscription; }  
	}  
	action distributeMessage {  
		action distribute { in message: Message; in topic: Topic; out message: Message; }  
		succession flow distributionFlow from distribute.message to deliverMessage.message;  
		action deliverMessage { in message: Message; in topic: Topic; }  
	}  
}

package 'VehicleFuelSystem' {
	attribute def Fuel;
	attribute def Temperature;
	port def FuelPort {
		in fuel: Fuel;
		out fuel: Fuel;
	}
	port def TemperaturePort {
		out temperature: Temperature;
	}
	part context {
		part fuelTankAssembly {
			port supplyFuelPort: FuelPort;
			port returnFuelPort: FuelPort;
			perform supplyFuelAction {
				out fuel = supplyFuelPort.out.fuel;
			}
			perform returnFuelAction {
				in fuel = returnFuelPort.in.fuel;
			}
		}
		part engine {
			port supplyFuelPort: FuelPort;
			port returnFuelPort: FuelPort;
			perform supplyFuelAction {
				in fuel = supplyFuelPort.in.fuel;
			}
			perform returnFuelAction {
				out fuel = returnFuelPort.out.fuel;
			}
		}
		part supplyFuelLine {
			port inFuelPort: FuelPort;
			port outFuelPort: FuelPort;
			port temperaturePort: TemperaturePort;
			perform monitorTemperatureSupply {
				in fuel = inFuelPort.in.fuel;
				out temperature = temperaturePort.out.temperature;
			}
		}
		part returnFuelLine {
			port inFuelPort: FuelPort;
			port outFuelPort: FuelPort;
			port temperaturePort: TemperaturePort;
			perform monitorTemperatureReturn {
				in fuel = inFuelPort.in.fuel;
				out temperature = temperaturePort.out.temperature;
			}
		}
		interface fuelTankToSupplyLine connect fuelTankAssembly.supplyFuelPort to supplyFuelLine.inFuelPort {
			ref flow references supplyFuelFlow;
		}
		interface supplyLineToEngine connect supplyFuelLine.outFuelPort to engine.supplyFuelPort {
			ref flow references supplyFuelFlow;
		}
		interface engineToReturnLine connect engine.returnFuelPort to returnFuelLine.inFuelPort {
			ref flow references returnFuelFlow;
		}
		interface returnLineToFuelTank connect returnFuelLine.outFuelPort to fuelTankAssembly.returnFuelPort {
			ref flow references returnFuelFlow;
		}
	}
	action supplyFuelFlow {
		action supplyFuel { out fuel: Fuel; }
		succession flow from supplyFuel.fuel to monitorTemperatureSupply.fuel;
		action monitorTemperatureSupply { in fuel; out temperature: Temperature; }
	}
	action returnFuelFlow {
		action returnFuel { out fuel: Fuel; }
		succession flow from returnFuel.fuel to monitorTemperatureReturn.fuel;
		action monitorTemperatureReturn { in fuel; out temperature: Temperature; }
	}
}

package 'PublishSubscribeSystem' {
	attribute def Message;
	attribute def Topic;
	port def SubscribePort {
		out topic: Topic;
	}
	port def PublishPort {
		in message: Message;
		in topic: Topic;
	}
	port def DeliverPort {
		out message: Message;
		out topic: Topic;
	}
	part context {
		part consumer {
			port subscribePort: SubscribePort;
			perform subscriptionRequest.sendSubscriptionRequest {
				out topic = subscribePort.topic;
			}
		}
		interface consumerToServer connect consumer.subscribePort to server.subscribePort {
			ref flow references subscriptionRequest.subscriptionFlow
				from source.topic to target.topic;
		}
		part producer {
			port publishPort: PublishPort;
			perform publishMessage.sendMessage {
				in message = publishPort.message;
				in topic = publishPort.topic;
			}
		}
		interface producerToServer connect producer.publishPort to server.publishPort {
			ref flow references publishMessage.messageFlow
				from source.message to target.message;
			ref flow references publishMessage.topicFlow
				from source.topic to target.topic;
		}
		part server {
			port subscribePort: ~SubscribePort;
			port publishPort: ~PublishPort;
			perform handleSubscribe.storeSubscription {
				in topic = subscribePort.topic;
			}
			perform messageDistribution.distributeMessage {
				in message = publishPort.message;
				in topic = publishPort.topic;
				out topics = handleSubscribe.topic;
			}
			interface serverToConsumers connect messageDistribution.deliverPort to consumer.deliverPort {
				ref flow references messageDistribution.deliveryFlow
					from source.message to target.message;
				ref flow references messageDistribution.topicFlow
					from source.topics to target.topics;
			}
		}
		part consumer {
			port deliverPort: ~DeliverPort;
			perform messageReception.receiveMessage {
				in message = deliverPort.message;
				in topic = deliverPort.topic;
			}
		}
	}
	action subscriptionRequest {
		action sendSubscriptionRequest { out topic: Topic; }
		succession flow subscriptionFlow from sendSubscriptionRequest.topic to storeSubscription.topic;
	}
	action publishMessage {
		action sendMessage { in message: Message; in topic: Topic; }
		succession flow messageFlow from sendMessage.message to distributeMessage.message;
		succession flow topicFlow from sendMessage.topic to distributeMessage.topic;
	}
	action messageDistribution {
		action distributeMessage { in message: Message; in topic: Topic; out topics: Topic; }
		succession flow deliveryFlow from distributeMessage.message to receiveMessage.message;
		succession flow topicFlow from distributeMessage.topics to receiveMessage.topic;
	}
	action messageReception {
		action receiveMessage { in message: Message; in topic: Topic; }
	}
}

package 'TransportationSystem' {
	attribute def FuelLevel;
	attribute def MaxTankCapacity;
	attribute def BoardingStatus;
	attribute def ExitStatus;
	port def OperationCmdPort {
		out operationCmd;
	}
	port def RefuelCmdPort {
		out refuelCmd;
	}
	port def BoardingPort {
		out boarding: BoardingStatus;
	}
	port def ExitPort {
		out exit: ExitStatus;
	}
	port def EnvironmentalInputPort {
		in environmentalFactors;
	}
	part context {
		part driver {
			port operationCmdPort: OperationCmdPort;
			perform operateVehicle.sendOperationCmd {
				out operationCmd = operationCmdPort.operationCmd;
			}
			port refuelCmdPort: RefuelCmdPort;
			perform refuelVehicle.sendRefuelCmd {
				out refuelCmd = refuelCmdPort.refuelCmd;
			}
		}
		interface driverToVehicle connect driver.operationCmdPort to vehicle.operationCmdPort {
			ref flow references operateVehicle.operationCmdFlow
				from source.operationCmd to target.operationCmd;
		}
		interface driverToFuelSystem connect driver.refuelCmdPort to fuelSystem.refuelCmdPort {
			ref flow references refuelVehicle.refuelCmdFlow
				from source.refuelCmd to target.refuelCmd;
		}
		part passengers {
			port boardingPort: BoardingPort;
			perform boardUsers.sendBoardingStatus {
				out boarding = boardingPort.boarding;
			}
			port exitPort: ExitPort;
			perform exitUsers.sendExitStatus {
				out exit = exitPort.exit;
			}
		}
		interface passengersToVehicle connect passengers.boardingPort to vehicle.boardingPort {
			ref flow references boardUsers.boardingFlow
				from source.boarding to target.boarding;
		}
		interface passengersToVehicleExit connect passengers.exitPort to vehicle.exitPort {
			ref flow references exitUsers.exitFlow
				from source.exit to target.exit;
		}
		part vehicle {
			port operationCmdPort: ~OperationCmdPort;
			port boardingPort: ~BoardingPort;
			port exitPort: ~ExitPort;
			port environmentalInputPort: EnvironmentalInputPort;
			perform operateVehicle.consumeFuel {
				in operationCmd = operationCmdPort.operationCmd;
				out fuelLevel = fuelSystem.fuelLevel;
			}
			perform checkFuelLevel.evaluate {
				in fuelLevel = fuelSystem.fuelLevel;
				out refuelDecision = fuelSystem.refuelDecision;
			}
			perform exitUsers.finalizeExit {
				in exit;
				out exitStatus;
			}
		}
		part fuelSystem {
			port refuelCmdPort: ~RefuelCmdPort;
			attribute fuelLevel: FuelLevel;
			attribute maxTankCapacity: MaxTankCapacity;
			perform refuelVehicle.updateFuel {
				in refuelCmd = refuelCmdPort.refuelCmd;
				out fuelLevel = fuelSystem.fuelLevel;
			}
		}
		part environment {
			port environmentalInputPort: ~EnvironmentalInputPort;
			perform monitorEnvironment.provideFactors {
				out environmentalFactors = environmentalInputPort.environmentalFactors;
			}
		}
	}
	action operateVehicle {
		action sendOperationCmd { out operationCmd; }
		succession flow operationCmdFlow from sendOperationCmd.operationCmd to consumeFuel.operationCmd;
		action consumeFuel { in operationCmd; out fuelLevel; }
	}
	action checkFuelLevel {
		action evaluate { in fuelLevel; out refuelDecision; }
		decision ifLowFuel condition refuelDecision < 0.1 * maxTankCapacity {
			branch refuel { to refuelVehicle.evaluate; }
			branch continue { to operateVehicle.consumeFuel; }
		}
	}
	action refuelVehicle {
		action sendRefuelCmd { out refuelCmd; }
		succession flow refuelCmdFlow from sendRefuelCmd.refuelCmd to updateFuel.refuelCmd;
		action updateFuel { in refuelCmd; out fuelLevel; }
	}
	action boardUsers {
		action sendBoardingStatus { out boarding; }
		succession flow boardingFlow from sendBoardingStatus.boarding to vehicle.boarding;
	}
	action exitUsers {
		action sendExitStatus { out exit; }
		succession flow exitFlow from sendExitStatus.exit to vehicle.exit;
	}
}

