			perform simulateVehicle.initializeSimulation {
				in mass = massPort.mass;
				in initialPosition = initialPositionPort.initialPosition;
				in initialVelocity = initialVelocityPort.initialVelocity;
				in timeInterval = timeIntervalPort.timeInterval;
				out currentVelocity = initialVelocity;
				out currentPosition = initialPosition;
			}
			perform simulateVehicle.processStep {
				in mass = massPort.mass;
				in timeInterval = timeIntervalPort.timeInterval;
				in currentVelocity;
				in currentPosition;
				in powerInput = powerInputPort.powerInput;
				out newVelocity;
				out newPosition;
			}
			perform simulateVehicle.outputTrajectory {
				in newPosition;
				out trajectoryPort.trajectory;
			}
		}
	}
	action simulateVehicle {
		in mass: Mass;
		in initialPosition: InitialPosition;
		in initialVelocity: InitialVelocity;
		in timeInterval: TimeInterval;
		in powerInputs: Sequence<PowerInput>;
		out trajectory: Trajectory;

		action initializeSimulation {
			in mass, initialPosition, initialVelocity, timeInterval;
			out currentVelocity, currentPosition;
		}
		loop processSteps for (step in powerInputs) {
			action processStep {
				in mass, timeInterval, currentVelocity, currentPosition, powerInput;
				out newVelocity, newPosition;
			}
			succession flow from processStep.newVelocity to processStep.currentVelocity;
			succession flow from processStep.newPosition to processStep.currentPosition;
		}
		action outputTrajectory {
			in newPosition;
			out trajectory;
		}
		succession flow from initializeSimulation.currentVelocity to processSteps.processStep.currentVelocity;
		succession flow from initializeSimulation.currentPosition to processSteps.processStep.currentPosition;
		succession flow from processSteps.processStep.newPosition to outputTrajectory.newPosition;
	}
}

