		out updateCmd : UpdateCmd;
	}
	part context {
		part system {
			port sensorStatusPort: ~SensorStatusPort;
			perform manageSensors.checkSensorStatus {
				in status = sensorStatusPort.status;
			}
			perform manageSensors.sendUpdateCmd {
				in status = checkSensorStatus.status;
				out updateCmd = updateCmdPort.updateCmd;
			}
			port updateCmdPort: UpdateCmdPort;
		}
		interface systemToSensorStatus connect system.sensorStatusPort to sensor.sensorStatusPort {
			ref flow references manageSensors.statusFlow
				from source.status to target.status;
		}
		interface systemToSensorCmd connect system.updateCmdPort to sensor.updateCmdPort {
			ref flow references manageSensors.updateFlow
				from source.updateCmd to target.updateCmd;
		}
