package 'VehicleStateControl' {
	attribute def startSignal;
	attribute def powerOnSignal;
	attribute def shutdownSignal;
	port def StartPort {
		in startSig : startSignal;
	}
	port def PowerOnPort {
		in powerOnSig : powerOnSignal;
	}
	port def ShutdownPort {
		in shutdownSig : shutdownSignal;
	}
	port def StatePort {
		out state : String;
	}
	part context {
		part user {
			port startPort: StartPort;
			port powerOnPort: PowerOnPort;
			port shutdownPort: ShutdownPort;
			perform startup.shutdownAction {
				out shutdownSig = shutdownPort.shutdownSig;
			}
			perform startup.startAction {
				out startSig = startPort.startSig;
			}
			perform startup.powerOnAction {
				out powerOnSig = powerOnPort.powerOnSig;
			}
		}
		interface userToVehicle connect user.startPort to vehicle.startPort {
			ref flow references startup.startFlow
				from source.startSig to target.startSig;
		}
		interface userToVehicle connect user.powerOnPort to vehicle.powerOnPort {
			ref flow references startup.powerOnFlow
				from source.powerOnSig to target.powerOnSig;
		}
		interface userToVehicle connect user.shutdownPort to vehicle.shutdownPort {
			ref flow references startup.shutdownFlow
				from source.shutdownSig to target.shutdownSig;
		}
		part vehicle {
			port startPort: ~StartPort;
			port powerOnPort: ~PowerOnPort;
			port shutdownPort: ~ShutdownPort;
			port statePort: StatePort;
			perform startup.transitionToOff {
				in shutdownSig = shutdownPort.shutdownSig;
				out state = statePort.state;
			}
			perform startup.transitionToStarting {
				in startSig = startPort.startSig;
				out state = statePort.state;
			}
			perform startup.transitionToOn {
				in powerOnSig = powerOnPort.powerOnSig;
				out state = statePort.state;
			}
		}
	}
	action startup {
		state def off;
		state def starting;
		state def on;
		action startAction { out startSig: startSignal; }
		action powerOnAction { out powerOnSig: powerOnSignal; }
		action shutdownAction { out shutdownSig: shutdownSignal; }
		succession flow startFlow from startAction.startSig to transitionToStarting.startSig;
		succession flow powerOnFlow from powerOnAction.powerOnSig to transitionToOn.powerOnSig;
		succession flow shutdownFlow from shutdownAction.shutdownSig to transitionToOff.shutdownSig;
		action transitionToStarting {
			in startSig: startSignal;
			out state: String = "starting";
		}
		action transitionToOn {
			in powerOnSig: powerOnSignal;
			out state: String = "on";
		}
		action transitionToOff {
			in shutdownSig: shutdownSignal;
			out state: String = "off";
		}
	}
}

