package 'VehiclePowerManagement' {  
	attribute def VehicleStartCmd;  
	attribute def VehicleStartedSignal;  
	attribute def VehicleShutdownCmd;  
	attribute def DiagnosticResult;  
	attribute def PowerSupplyStatus;  
	attribute def SafetyOperationsStatus;  

	port def StartCmdPort {  
		out startCmd : VehicleStartCmd;  
	}  
	port def ShutdownCmdPort {  
		out shutdownCmd : VehicleShutdownCmd;  
	}  
	port def StartedSignalPort {  
		out startedSignal : VehicleStartedSignal;  
	}  
	port def DiagnosticPort {  
		out diagnosticResult : DiagnosticResult;  
	}  
	port def PowerSupplyPort {  
		out powerSupply : PowerSupplyStatus;  
	}  

	part context {  
		part user {  
			port startCmdPort: StartCmdPort;  
			port shutdownCmdPort: ShutdownCmdPort;  
			perform powerManagement.sendStartCmd {  
				out startCmd = startCmdPort.startCmd;  
			}  
			perform powerManagement.sendShutdownCmd {  
				out shutdownCmd = shutdownCmdPort.shutdownCmd;  
			}  
		}  
		interface userToVehicleStart connect user.startCmdPort to vehiclePowerSystem.startCmdPort {  
			ref flow references powerManagement.startCmdFlow  
				from source.startCmd to target.startCmd;  
		}  
		interface userToVehicleShutdown connect user.shutdownCmdPort to vehiclePowerSystem.shutdownCmdPort {  
			ref flow references powerManagement.shutdownCmdFlow  
				from source.shutdownCmd to target.shutdownCmd;  
		}  

		part vehiclePowerSystem {  
			port startCmdPort: ~StartCmdPort;  
			port shutdownCmdPort: ~ShutdownCmdPort;  
			port startedSignalPort: StartedSignalPort;  
			port diagnosticPort: DiagnosticPort;  
			port powerSupplyPort: PowerSupplyPort;  

			perform powerManagement.transitionToStartPhase {  
				in startCmd = startCmdPort.startCmd;  
				out startedSignal = startedSignalPort.startedSignal;  
			}  
			perform powerManagement.transitionToPoweredOn {  
				in startedSignal = startedSignalPort.startedSignal;  
				out diagnosticResult = diagnosticPort.diagnosticResult;  
				out powerSupply = powerSupplyPort.powerSupply;  
			}  
			perform powerManagement.executeSafetyOperations {  
				in shutdownCmd = shutdownCmdPort.shutdownCmd;  
				out safetyStatus = safetySystem.safetyStatus;  
			}  
		}  

		part engine {  
			port startedSignalPort: ~StartedSignalPort;  
			perform powerManagement.engineStartup {  
				in startedSignal = startedSignalPort.startedSignal;  
			}  
		}  

		part safetySystem {  
			port safetyStatusPort: ~PowerSupplyPort;  
			perform powerManagement.parkingBrakeEngagement {  
				out powerSupply = safetyStatusPort.powerSupply;  
			}  
		}  

		part functionalUnits {  
			port powerSupplyPort: ~PowerSupplyPort;  
			perform powerManagement.receivePower {  
				in powerSupply = powerSupplyPort.powerSupply;  
			}  
		}  
	}  

	action powerManagement {  
		action sendStartCmd { out startCmd: VehicleStartCmd; }  
		succession flow startCmdFlow from sendStartCmd.startCmd to transitionToStartPhase.startCmd;  
		action transitionToStartPhase { in startCmd: VehicleStartCmd; out startedSignal: VehicleStartedSignal; }  
		succession flow startedSignalFlow from transitionToStartPhase.startedSignal to transitionToPoweredOn.startedSignal;  
		action transitionToPoweredOn {  
			in startedSignal: VehicleStartedSignal;  
			out diagnosticResult: DiagnosticResult;  
			out powerSupply: PowerSupplyStatus;  
		}  
		succession flow diagnosticFlow from transitionToPoweredOn.diagnosticResult to diagnosticResult;  
		succession flow powerSupplyFlow from transitionToPoweredOn.powerSupply to receivePower.powerSupply;  
		action sendShutdownCmd { out shutdownCmd: VehicleShutdownCmd; }  
		succession flow shutdownCmdFlow from sendShutdownCmd.shutdownCmd to executeSafetyOperations.shutdownCmd;  
		action executeSafetyOperations {  
			in shutdownCmd: VehicleShutdownCmd;  
			out safetyStatus: PowerSupplyStatus;  
		}  
		succession flow safetyPowerFlow from executeSafetyOperations.safetyStatus to parkingBrakeEngagement.powerSupply;  
		action parkingBrakeEngagement { out powerSupply: PowerSupplyStatus; }  
		succession flow finalShutdownFlow from parkingBrakeEngagement.powerSupply to transitionToOff.powerStatus;  
		action transitionToOff { in powerStatus: PowerSupplyStatus; }  
	}  
}

