package 'VehicleHealthMonitoringSystem' {
	attribute def CurrentTemperature;
	attribute def MaxAllowableTemperature;
	attribute def HealthStatus;
	attribute def MaintenanceStatus;
	attribute def NextMaintenanceTime;
	attribute def MaintenanceDuration;
	port def TemperaturePort {
		out currentTemperature : CurrentTemperature;
	}
	port def WarningPort {
		out warning: Boolean;
	}
	port def StatusPort {
		out healthStatus: HealthStatus;
	}
	port def MaintenancePort {
		out maintenanceStatus: MaintenanceStatus;
		out nextMaintenanceTime: NextMaintenanceTime;
	}
	port def ClockPort {
		out currentTime: CurrentTime;
	}
	part context {
		part temperatureSensor {
			port tempPort: TemperaturePort;
			perform monitorTemperature.sendCurrentTemp {
				out currentTemperature = tempPort.currentTemperature;
			}
		}
		interface sensorToController connect temperatureSensor.tempPort to controller.tempPort {
			ref flow references monitorTemperature.tempFlow
				from source.currentTemperature to target.currentTemp;
		}
		part controller {
			port tempPort: ~TemperaturePort;
			port warningPort: WarningPort;
			port statusPort: StatusPort;
			perform monitorTemperature.compareTemperature {
				in currentTemp = tempPort.currentTemperature;
				out warning = warningPort.warning;
				out healthStatus = statusPort.healthStatus;
			}
			perform maintenanceScheduler.updateMaintenanceStatus {
				in currentTime = clock.currentTime;
				out maintenanceStatus = statusPort.healthStatus;
			}
		}
		part maintenanceScheduler {
			port statusPort: ~StatusPort;
			port maintenancePort: MaintenancePort;
			perform maintenanceCycle.enterMaintenance {
				in maintenanceTrigger = statusPort.healthStatus;
				out maintenanceStatus = maintenancePort.maintenanceStatus;
				out nextMaintenanceTime = maintenancePort.nextMaintenanceTime;
			}
			perform maintenanceCycle.exitMaintenance {
				in duration = maintenancePort.maintenanceStatus;
				out healthStatus = statusPort.healthStatus;
			}
		}
		part clock {
			port clockPort: ClockPort;
			perform maintenanceCycle.provideCurrentTime {
				out currentTime = clockPort.currentTime;
			}
		}
	}
	action monitorTemperature {
		action sendCurrentTemp { out currentTemperature: CurrentTemperature; }
		succession flow tempFlow from sendCurrentTemp.currentTemperature to compareTemperature.currentTemp;
		action compareTemperature { 
			in currentTemp: CurrentTemperature; 
			out warning: Boolean; 
			out healthStatus: HealthStatus; 
			if (currentTemp > MaxAllowableTemperature) {
				out warning = true;
				out healthStatus = "Degraded";
			} else {
				out healthStatus = "Normal";
			}
		}
	}
	action maintenanceCycle {
		action provideCurrentTime { out currentTime: CurrentTime; }
		succession flow timeFlow from provideCurrentTime.currentTime to enterMaintenance.currentTime;
		action enterMaintenance { 
			in currentTime: CurrentTime; 
			out maintenanceStatus: MaintenanceStatus; 
			out nextMaintenanceTime: NextMaintenanceTime; 
			if (currentTime >= NextMaintenanceTime) {
				out maintenanceStatus = "Maintenance";
				out nextMaintenanceTime = currentTime + 30d; // Example: 30-day cycle
			}
		}
		succession flow maintenanceFlow from enterMaintenance.maintenanceStatus to exitMaintenance.duration;
		action exitMaintenance { 
			in duration: MaintenanceStatus; 
			out healthStatus: HealthStatus; 
			if (duration >= 48h) {
				out healthStatus = "Normal";
			}
		}
	}
}

package 'ServerSystem' {
	attribute def Command;
	attribute def Time;
	port def RequestPort {
		in command: Command;
	}
	part context {
		part user {
			port requestPort: RequestPort;
			perform serverStateMachine.sendStartCommand {
				out command = requestPort.command;
			}
			perform serverStateMachine.sendRequestCommand {
				out command = requestPort.command;
			}
		}
		interface userToServer connect user.requestPort to server.requestPort {
			ref flow references serverStateMachine.startFlow, serverStateMachine.requestFlow, serverStateMachine.dailyReset1, serverStateMachine.dailyReset2;
		}
		part server {
			port requestPort: ~RequestPort;
			attribute clock: Time;
			perform serverStateMachine.offState { ... }
			perform serverStateMachine.waitingState { ... }
			perform serverStateMachine.responseState { ... }
		}
	}
	action serverStateMachine {
		action sendStartCommand { out command: Command; }
		action sendRequestCommand { out command: Command; }
		action offState { }
		action waitingState { }
		action responseState { }

		succession flow startFlow from sendStartCommand.command to waitingState.command when (command == "start");
		succession flow requestFlow from sendRequestCommand.command to responseState.command when (command == "request");
		succession flow delayFlow from responseState to waitingState after 5 minutes;
		succession flow dailyReset1 from waitingState to offState when (clock == "11:59:00");
