		succession flow setSpeedFlow from sendSetSpeedCmd.setSpeed to processSpeedCommands.setSpeed;
		action processSpeedCommands { in setSpeed; out fuelControlCmd: FuelControlCmd; }
		action readActualSpeed { out actualSpeed: ActualSpeed; }
		succession flow speedFlow from readActualSpeed.actualSpeed to processSpeedCommands.actualSpeed;
		action transmitFuelControlCmd { in fuelControlCmd; out fuelCommand: FuelControlCmd; }
		succession flow fuelFlow from processSpeedCommands.fuelControlCmd to transmitFuelControlCmd.fuelControlCmd;
		action adjustFuelFlow { in fuelCommand: FuelControlCmd; }
	}
}

package 'VehicleLifecycle' {
	attribute def DeliveryDate;
	attribute def OwnerInfo;
	attribute def Driver;
	attribute def ScrapInfo;
	port def DeliveryPort {
		out deliveryDate : DeliveryDate;
	}
	port def OwnershipPort {
		out ownerInfo : OwnerInfo;
	}
	port def DriverPort {
		out driver : Driver;
	}
	port def EndOfUsePort {
		out scrapInfo : ScrapInfo;
	}
	part context {
		part vehicle {
			port deliveryPort: DeliveryPort;
			perform manageLifecycle.recordDelivery {
				out deliveryDate = deliveryPort.deliveryDate;
			}
		}
		interface vehicleToDelivery connect vehicle.deliveryPort to delivery.deliveryPort {
			ref flow references manageLifecycle.deliveryFlow
				from source.deliveryDate to target.deliveryDate;
		}
		part delivery {
			port deliveryPort: ~DeliveryPort;
			perform manageLifecycle.recordDelivery {
				in deliveryDate = deliveryPort.deliveryDate;
				out deliveryDate = deliveryPort.deliveryDate;
			}
			port ownershipPort: ~OwnershipPort;
			perform manageLifecycle.recordOwnershipChange {
				in deliveryDate = deliveryPort.deliveryDate;
				out ownerInfo = ownershipPort.ownerInfo;
			}
		}
		interface deliveryToOwnership connect delivery.ownershipPort to ownership.ownershipPort {
			ref flow references manageLifecycle.ownershipFlow
				from source.ownerInfo to target.ownerInfo;
		}
		part ownership {
			port ownershipPort: ~OwnershipPort;
			perform manageLifecycle.recordOwnershipChange {
				in ownerInfo = deliveryToOwnership.ownerInfo;
				out ownerInfo = ownershipPort.ownerInfo;
			}
			port driverPort: DriverPort;
			perform manageLifecycle.trackDriver {
				in ownerInfo = ownershipPort.ownerInfo;
				out driver = driverPort.driver;
			}
		}
		interface ownershipToDriving connect ownership.driverPort to driving.driverPort {
			ref flow references manageLifecycle.driverFlow
				from source.driver to target.driver;
		}
		part driving {
			port driverPort: ~DriverPort;
			perform manageLifecycle.trackDriver {
				in driver = driverPort.driver;
				out driver = driverPort.driver;
			}
		}
		interface ownershipToNextOwnership connect ownership.ownershipPort to nextOwnership.ownershipPort {
			ref flow references manageLifecycle.ownershipFlow
				from source.ownerInfo to target.ownerInfo;
		}
		part endOfUse {
			port ownershipPort: ~OwnershipPort;
			perform manageLifecycle.recordScrap {
				in ownerInfo = ownershipToNextOwnership.ownerInfo;
				out scrapInfo = endOfUsePort.scrapInfo;
			}
			port endOfUsePort: EndOfUsePort;
		}
	}
	action manageLifecycle {
		action recordDelivery { out deliveryDate: DeliveryDate; }
		succession flow deliveryFlow from recordDelivery.deliveryDate to recordOwnershipChange.deliveryDate;
		action recordOwnershipChange { in deliveryDate; out ownerInfo: OwnerInfo; }
		succession flow ownershipFlow from recordOwnershipChange.ownerInfo to trackDriver.ownerInfo;
		action trackDriver { in ownerInfo; out driver: Driver; }
		succession flow driverFlow from trackDriver.driver to recordOwnershipChange.driver;
		action recordScrap { in ownerInfo; out scrapInfo: ScrapInfo; }
		succession flow scrapFlow from recordScrap.scrapInfo to endOfUse.scrapInfo;
	}
}

