		action sendCommands { out gearCmd : Gear; out acceleratorCmd : Accelerator; }
		succession flow commandFlow from sendCommands.gearCmd to setUpdatedAttributes.gearCmd;
		succession flow commandFlow from sendCommands.acceleratorCmd to setUpdatedAttributes.acceleratorCmd;
		action setUpdatedAttributes { in gearCmd : Gear; in acceleratorCmd : Accelerator; out mass : Mass; out gear : Gear; out accelerator : Accelerator; }
