		in threshold : Threshold;
	}
	part context {
		part vehicle {
			port vinPort: ~VIN;
			port massPort: VehicleMassPort;
			perform calculateTotalMassAction {
				in threshold = thresholdPort.threshold;
				out mass = massPort.mass;
			}
			attribute vin = vinPort.vin;
		}
		interface vehicleToComponents connect vehicle.massPort to component.massPort {
			ref flow references calculateTotalMassAction.massFlow
				from source.mass to target.mass;
		}
		part component {
			port massPort: ~ComponentMassPort;
			port subcomponentsPort: ~ComponentMassPort;
			perform calculateSubcomponentMass {
				in threshold = thresholdPort.threshold;
				out mass = massPort.mass;
			}
			attribute serialNumber = massPort.serialNumber;
		}
		part massManager {
			port thresholdPort: ~ThresholdPort;
			port componentMassPort: ~ComponentMassPort;
			perform filterSubcomponents {
				in threshold = thresholdPort.threshold;
				in componentMass = componentMassPort.mass;
				out filteredMass: Mass;
			}
		}
	}
	action calculateTotalMassAction {
		action addBaseMass { in baseMass: Mass; }
		action filterSubcomponents { in threshold: Threshold; in componentMass: Mass; out filteredMass: Mass; }
		action sumComponentMasses { in componentMasses: Mass[]; out totalMass: Mass; }
		succession flow baseMassFlow from addBaseMass.baseMass to sumComponentMasses.componentMasses;
		succession flow filteredMassFlow from filterSubcomponents.filteredMass to sumComponentMasses.componentMasses;
		succession flow totalMassFlow from sumComponentMasses.totalMass to calculateTotalMassAction.mass;
	}
}

package 'MassAggregationSystem' {
