				out mass = massPort.mass;
			}
		}
		part compositeThing {
			port massPort: MassPort;
			port subcomponentsPort: * MassPort;
			perform calculateTotalMass {
				in simpleMass = this.simpleMass;
				in subcomponentMass = subcomponentsPort.mass;
				out mass = simpleMass + subcomponentMass;
			}
		}
		interface compositeToSubcomponents connect compositeThing.subcomponentsPort to subcomponent.massPort {
			ref flow references calculateTotalMass.subcomponentMassFlow
				from source.mass to target.mass;
		}
	}
	action calculateTotalMass {
		action calculateSimpleMass {
			out mass: totalMass;
		}
		succession flow simpleMassFlow from calculateSimpleMass.mass to calculateTotalMass.mass;
		action calculateCompositeMass {
			in simpleMass: simpleMass;
			in subcomponentMass: totalMass[*];
			out mass: totalMass;
		}
	}
}

package 'MassAggregationSystem' {  
	attribute def basicMass;  
	attribute def totalMass;  
	attribute def minMassThreshold;  
	port def MassPort {  
		in minMassThreshold : minMassThreshold;  
		out totalMass : totalMass;  
	}  
	port def SubcomponentPort {  
		in minMassThreshold : minMassThreshold;  
		out totalMass : totalMass;  
	}  
	part context {  
		part massAggregationSystem {  
			port massPort: MassPort;  
			perform calculateTotalMass.calculateTotalMass {  
				in minMassThreshold = massPort.minMassThreshold;  
				out totalMass = massPort.totalMass;  
			}  
		}  
		interface systemToSubcomponents connect massAggregationSystem.massPort to subcomponent1.massPort, subcomponent2.massPort {  
			ref flow references calculateTotalMass.minMassThresholdFlow  
				from source.minMassThreshold to target.minMassThreshold;  
			ref flow references calculateTotalMass.totalMassFlow  
				from source.totalMass to target.totalMass;  
		}  
		part subcomponent1 {  
			port massPort: ~MassPort;  
			perform calculateTotalMass.calculateSubcomponentMass {  
				in minMassThreshold = massPort.minMassThreshold;  
				out totalMass = massPort.totalMass;  
			}  
		}  
		part subcomponent2 {  
			port massPort: ~MassPort;  
			perform calculateTotalMass.calculateSubcomponentMass {  
				in minMassThreshold = massPort.minMassThreshold;  
				out totalMass = massPort.totalMass;  
			}  
		}  
	}  
	action calculateTotalMass {  
		action calculateTotalMass {  
			in minMassThreshold: minMassThreshold;  
			out totalMass: totalMass;  
		}  
		succession flow minMassThresholdFlow from calculateTotalMass.minMassThreshold to calculateSubcomponentMass.minMassThreshold;  
		action calculateSubcomponentMass {  
			in minMassThreshold: minMassThreshold;  
			out totalMass: totalMass;  
		}  
		succession flow totalMassFlow from calculateSubcomponentMass.totalMass to calculateTotalMass.subcomponentMasses;  
		action aggregateMass {  
			in basicMass: basicMass;  
			in subcomponentMasses: totalMass[];  
			out totalMass: totalMass;  
		}  
		succession flow aggregationFlow from calculateTotalMass.basicMass to aggregateMass.basicMass;  
		succession flow aggregationFlow from calculateSubcomponentMass.totalMass to aggregateMass.subcomponentMasses;  
		action filterSubcomponents {  
			in minMassThreshold: minMassThreshold;  
			in subcomponentMasses: totalMass[];  
			out filteredMasses: totalMass[];  
		}  
		succession flow filterFlow from calculateTotalMass.subcomponentMasses to filterSubcomponents.subcomponentMasses;  
		succession flow filterFlow from filterSubcomponents.filteredMasses to aggregateMass.subcomponentMasses;  
	}  
}

package 'VehicleDynamicsSystem' {
	attribute def WheelPower;
	attribute def DragCoeff;
	attribute def FrictionCoeff;
	attribute def Mass;
	attribute def InitialSpeed;
	attribute def Time;
	attribute def EffectivePower;
	attribute def Acceleration;
	attribute def CurrentSpeed;
	attribute def Displacement;

	port def WheelPowerPort { out wheelPower: WheelPower; }
	port def DragCoeffPort { out dragCoeff: DragCoeff; }
	port def FrictionCoeffPort { out frictionCoeff: FrictionCoeff; }
	port def MassPort { out mass: Mass; }
	port def InitialSpeedPort { out initialSpeed: InitialSpeed; }
	port def TimePort { out time: Time; }
	port def EffectivePowerPort { out effectivePower: EffectivePower; }
	port def AccelerationPort { out acceleration: Acceleration; }
	port def CurrentSpeedPort { out currentSpeed: CurrentSpeed; }
	port def DisplacementPort { out displacement: Displacement; }

	part context {
		part user {
			port wheelPowerPort: WheelPowerPort;
			port dragCoeffPort: DragCoeffPort;
			port frictionCoeffPort: FrictionCoeffPort;
			port massPort: MassPort;
			port initialSpeedPort: InitialSpeedPort;
			port timePort: TimePort;
			perform vehicleDynamicsSystem.sendInputs {
				out wheelPower = wheelPowerPort.wheelPower;
				out dragCoeff = dragCoeffPort.dragCoeff;
				out frictionCoeff = frictionCoeffPort.frictionCoeff;
				out mass = massPort.mass;
				out initialSpeed = initialSpeedPort.initialSpeed;
				out time = timePort.time;
			}
		}

		interface userToSystem connect user.wheelPowerPort to vehicleDynamicsSystem.wheelPowerPort {
			ref flow references vehicleDynamics.wheelPowerFlow from source.wheelPower to target.wheelPower;
		}
		interface userToSystem connect user.dragCoeffPort to vehicleDynamicsSystem.dragCoeffPort {
			ref flow references vehicleDynamics.dragCoeffFlow from source.dragCoeff to target.dragCoeff;
		}
		interface userToSystem connect user.frictionCoeffPort to vehicleDynamicsSystem.frictionCoeffPort {
			ref flow references vehicleDynamics.frictionCoeffFlow from source.frictionCoeff to target.frictionCoeff;
		}
		interface userToSystem connect user.massPort to vehicleDynamicsSystem.massPort {
			ref flow references vehicleDynamics.massFlow from source.mass to target.mass;
		}
		interface userToSystem connect user.initialSpeedPort to vehicleDynamicsSystem.initialSpeedPort {
			ref flow references vehicleDynamics.initialSpeedFlow from source.initialSpeed to target.initialSpeed;
		}
		interface userToSystem connect user.timePort to vehicleDynamicsSystem.timePort {
			ref flow references vehicleDynamics.timeFlow from source.time to target.time;
		}

		part vehicleDynamicsSystem {
			port wheelPowerPort: ~WheelPowerPort;
			port dragCoeffPort: ~DragCoeffPort;
			port frictionCoeffPort: ~FrictionCoeffPort;
			port massPort: ~MassPort;
			port initialSpeedPort: ~InitialSpeedPort;
			port timePort: ~TimePort;
			perform vehicleDynamicsSystem.calculateEffectivePower {
				in wheelPower = wheelPowerPort.wheelPower;
				in dragCoeff = dragCoeffPort.dragCoeff;
				in frictionCoeff = frictionCoeffPort.frictionCoeff;
				in mass = massPort.mass;
				in initialSpeed = initialSpeedPort.initialSpeed;
				out effectivePower = effectivePowerPort.effectivePower;
			}
			port effectivePowerPort: EffectivePowerPort;
			perform vehicleDynamicsSystem.calculateAcceleration {
				in effectivePower = effectivePowerPort.effectivePower;
				in mass = massPort.mass;
				in initialSpeed = initialSpeedPort.initialSpeed;
				out acceleration = accelerationPort.acceleration;
			}
			port accelerationPort: AccelerationPort;
			perform vehicleDynamicsSystem.computeCurrentSpeed {
				in acceleration = accelerationPort.acceleration;
				in initialSpeed = initialSpeedPort.initialSpeed;
				in time = timePort.time;
				out currentSpeed = currentSpeedPort.currentSpeed;
			}
			port currentSpeedPort: CurrentSpeedPort;
			perform vehicleDynamicsSystem.estimateDisplacement {
				in currentSpeed = currentSpeedPort.currentSpeed;
				in time = timePort.time;
				out displacement = displacementPort.displacement;
			}
			port displacementPort: DisplacementPort;
		}
	}
	action vehicleDynamics {
		action sendInputs { 
			out wheelPower: WheelPower; 
			out dragCoeff: DragCoeff; 
			out frictionCoeff: FrictionCoeff; 
			out mass: Mass; 
			out initialSpeed: InitialSpeed; 
			out time: Time; 
		}
		succession flow wheelPowerFlow from sendInputs.wheelPower to calculateEffectivePower.wheelPower;
		succession flow dragCoeffFlow from sendInputs.dragCoeff to calculateEffectivePower.dragCoeff;
		succession flow frictionCoeffFlow from sendInputs.frictionCoeff to calculateEffectivePower.frictionCoeff;
		succession flow massFlow from sendInputs.mass to calculateEffectivePower.mass;
		succession flow initialSpeedFlow from sendInputs.initialSpeed to calculateEffectivePower.initialSpeed;
		succession flow timeFlow from sendInputs.time to calculateEffectivePower.time;

		action calculateEffectivePower { 
			in wheelPower; 
			in dragCoeff; 
			in frictionCoeff; 
			in mass; 
			in initialSpeed; 
			out effectivePower; 
		}
		succession flow effectivePowerToAcceleration from calculateEffectivePower.effectivePower to calculateAcceleration.effectivePower;
		succession flow massToAcceleration from calculateEffectivePower.mass to calculateAcceleration.mass;
		succession flow initialSpeedToAcceleration from calculateEffectivePower.initialSpeed to calculateAcceleration.initialSpeed;

		action calculateAcceleration { 
			in effectivePower; 
			in mass; 
			in initialSpeed; 
			out acceleration; 
		}
		succession flow accelerationToCurrentSpeed from calculateAcceleration.acceleration to computeCurrentSpeed.acceleration;
		succession flow initialSpeedToCurrentSpeed from calculateAcceleration.initialSpeed to computeCurrentSpeed.initialSpeed;
		succession flow timeToCurrentSpeed from sendInputs.time to computeCurrentSpeed.time;

		action computeCurrentSpeed { 
			in acceleration; 
			in initialSpeed; 
			in time; 
			out currentSpeed; 
		}
		succession flow currentSpeedToDisplacement from computeCurrentSpeed.currentSpeed to estimateDisplacement.currentSpeed;
		succession flow timeToDisplacement from sendInputs.time to estimateDisplacement.time;

		action estimateDisplacement { 
			in currentSpeed; 
			in time; 
			out displacement; 
		}
	}
}

package 'VehicleDynamicsSystem' {
	attribute def dragCoefficient;
