			port parameterPort: ParameterPort;
			perform simulateVehicleDynamics.sendParameters {
				out dragCoefficient = parameterPort.dragCoefficient;
				out rollingResistanceCoefficient = parameterPort.rollingResistanceCoefficient;
				out initialSpeed = parameterPort.initialSpeed;
				out initialPosition = parameterPort.initialPosition;
