			}
			perform simulateVehicleDynamics.computeAcceleration {
				in drivingPower = dynamicsPort.drivingPower;
				in mass = parameterPort.mass;
				out acceleration = dynamicsPort.acceleration;
			}
			perform simulateVehicleDynamics.updateSpeed {
				in initialSpeed = parameterPort.initialSpeed;
				in acceleration = dynamicsPort.acceleration;
				in timeStep = parameterPort.timeStep;
				out newSpeed = dynamicsPort.newSpeed;
			}
			perform simulateVehicleDynamics.updatePosition {
				in initialPosition = parameterPort.initialPosition;
				in initialSpeed = parameterPort.initialSpeed;
				in newSpeed = dynamicsPort.newSpeed;
				in timeStep = parameterPort.timeStep;
				out newPosition = dynamicsPort.newPosition;
			}
		}
	}

	action simulateVehicleDynamics {
		action sendParameters {
			out dragCoefficient: dragCoefficient;
			out rollingResistanceCoefficient: rollingResistanceCoefficient;
			out initialSpeed: initialSpeed;
			out initialPosition: initialPosition;
			out timeStep: timeStep;
			out wheelEndPower: wheelEndPower;
			out mass: mass;
		}
		succession flow parameterFlow from sendParameters.dragCoefficient to calculateDrivingPower.dragCoefficient;
		succession flow parameterFlow from sendParameters.rollingResistanceCoefficient to calculateDrivingPower.rollingResistanceCoefficient;
		succession flow parameterFlow from sendParameters.initialSpeed to calculateDrivingPower.initialSpeed;
		succession flow parameterFlow from sendParameters.mass to calculateDrivingPower.mass;
		succession flow parameterFlow from sendParameters.wheelEndPower to calculateDrivingPower.wheelEndPower;

		action calculateDrivingPower {
			in dragCoefficient;
			in rollingResistanceCoefficient;
			in initialSpeed;
			in mass;
			in wheelEndPower;
			out drivingPower: drivingPower;
		}
		succession flow drivingPowerFlow from calculateDrivingPower.drivingPower to computeAcceleration.drivingPower;
		succession flow accelerationFlow from computeAcceleration.acceleration to updateSpeed.acceleration;
		succession flow speedUpdateFlow from updateSpeed.newSpeed to updatePosition.newSpeed;
		succession flow positionUpdateFlow from updatePosition.newPosition to dynamicsPort.newPosition;

		action computeAcceleration {
			in drivingPower;
			in mass;
			out acceleration: acceleration;
		}

		action updateSpeed {
			in initialSpeed;
			in acceleration;
			in timeStep;
			out newSpeed: newSpeed;
		}

		action updatePosition {
			in initialPosition;
			in initialSpeed;
			in newSpeed;
			in timeStep;
			out newPosition: newPosition;
		}
	}
}

package 'VehicleDynamics' {
	attribute def VehicleMass;
	attribute def WheelPower;
	attribute def DragCoefficient;
	attribute def RollingResistanceCoefficient;
	attribute def InitialSpeed;
	attribute def InitialPosition;
	attribute def Speed;
	attribute def Position;
	attribute def Acceleration;
	attribute def TimeStep;
	port def InputPort {
		in vehicleMass: VehicleMass;
		in wheelPower: WheelPower;
		in dragCoefficient: DragCoefficient;
		in rollingResistanceCoefficient: RollingResistanceCoefficient;
		in initialSpeed: InitialSpeed;
		in initialPosition: InitialPosition;
		in timeStep: TimeStep;
	}
	port def OutputPort {
		out speed: Speed;
		out position: Position;
		out acceleration: Acceleration;
	}
	part context {
		part user {
			port inputPort: InputPort;
			perform computeDynamics.sendInputParameters {
				out vehicleMass = inputPort.vehicleMass;
				out wheelPower = inputPort.wheelPower;
				out dragCoefficient = inputPort.dragCoefficient;
				out rollingResistanceCoefficient = inputPort.rollingResistanceCoefficient;
				out initialSpeed = inputPort.initialSpeed;
				out initialPosition = inputPort.initialPosition;
				out timeStep = inputPort.timeStep;
			}
		}
		interface userToCalculator connect user.inputPort to dynamicsCalculator.inputPort {
			ref flow references computeDynamics.inputFlow
				from source.vehicleMass to target.vehicleMass,
				from source.wheelPower to target.wheelPower,
				from source.dragCoefficient to target.dragCoefficient,
				from source.rollingResistanceCoefficient to target.rollingResistanceCoefficient,
				from source.initialSpeed to target.speed,
				from source.initialPosition to target.position,
				from source.timeStep to target.timeStep;
		}
		part dynamicsCalculator {
			port inputPort: ~InputPort;
			port outputPort: OutputPort;
			perform computeDynamics.computeForces {
				in vehicleMass = inputPort.vehicleMass;
				in wheelPower = inputPort.wheelPower;
				in dragCoefficient = inputPort.dragCoefficient;
				in rollingResistanceCoefficient = inputPort.rollingResistanceCoefficient;
				in speed = inputPort.speed;
				in position = inputPort.position;
				in timeStep = inputPort.timeStep;
				out dragForce;
				out rollingResistanceForce;
			}
			perform computeDynamics.calculateNetForce {
				in dragForce from computeForces.dragForce;
				in rollingResistanceForce from computeForces.rollingResistanceForce;
				in wheelPower from inputPort.wheelPower;
				out netForce;
			}
			perform computeDynamics.calculateAcceleration {
				in netForce from calculateNetForce.netForce;
				in vehicleMass from inputPort.vehicleMass;
				out acceleration;
			}
			perform computeDynamics.updateSpeedPosition {
				in acceleration from calculateAcceleration.acceleration;
				in speed from computeForces.speed;
				in position from computeForces.position;
				in timeStep from computeForces.timeStep;
				out nextSpeed;
				out nextPosition;
			}
			perform computeDynamics.outputResults {
				in nextSpeed from updateSpeedPosition.nextSpeed;
				in nextPosition from updateSpeedPosition.nextPosition;
				in acceleration from calculateAcceleration.acceleration;
				out speed = outputPort.speed;
				out position = outputPort.position;
				out acceleration = outputPort.acceleration;
			}
		}
	}
	action computeDynamics {
		action sendInputParameters {
			out vehicleMass: VehicleMass;
			out wheelPower: WheelPower;
			out dragCoefficient: DragCoefficient;
			out rollingResistanceCoefficient: RollingResistanceCoefficient;
			out initialSpeed: InitialSpeed;
			out initialPosition: InitialPosition;
			out timeStep: TimeStep;
		}
		succession flow inputFlow from sendInputParameters.vehicleMass to computeForces.vehicleMass;
		succession flow inputFlow from sendInputParameters.wheelPower to computeForces.wheelPower;
		succession flow inputFlow from sendInputParameters.dragCoefficient to computeForces.dragCoefficient;
		succession flow inputFlow from sendInputParameters.rollingResistanceCoefficient to computeForces.rollingResistanceCoefficient;
		succession flow inputFlow from sendInputParameters.initialSpeed to computeForces.speed;
		succession flow inputFlow from sendInputParameters.initialPosition to computeForces.position;
		succession flow inputFlow from sendInputParameters.timeStep to computeForces.timeStep;
		action computeForces {
			in vehicleMass: VehicleMass;
			in wheelPower: WheelPower;
			in dragCoefficient: DragCoefficient;
			in rollingResistanceCoefficient: RollingResistanceCoefficient;
			in speed: Speed;
			in position: Position;
			in timeStep: TimeStep;
			out dragForce;
			out rollingResistanceForce;
		}
		succession flow netForceFlow from computeForces.dragForce to calculateNetForce.dragForce;
		succession flow netForceFlow from computeForces.rollingResistanceForce to calculateNetForce.rollingResistanceForce;
		succession flow netForceFlow from computeForces.wheelPower to calculateNetForce.wheelPower;
		action calculateNetForce {
			in dragForce;
			in rollingResistanceForce;
			in wheelPower;
			out netForce;
		}
		succession flow accelerationFlow from calculateNetForce.netForce to calculateAcceleration.netForce;
		succession flow massFlow from computeForces.vehicleMass to calculateAcceleration.vehicleMass;
		action calculateAcceleration {
			in netForce;
			in vehicleMass;
			out acceleration;
		}
		succession flow updateFlow from calculateAcceleration.acceleration to updateSpeedPosition.acceleration;
		succession flow speedFlow from computeForces.speed to updateSpeedPosition.speed;
		succession flow positionFlow from computeForces.position to updateSpeedPosition.position;
		succession flow timeStepFlow from computeForces.timeStep to updateSpeedPosition.timeStep;
		action updateSpeedPosition {
			in acceleration;
			in speed;
			in position;
			in timeStep;
			out nextSpeed;
			out nextPosition;
		}
		succession flow outputFlow from updateSpeedPosition.nextSpeed to outputResults.nextSpeed;
		succession flow outputFlow from updateSpeedPosition.nextPosition to outputResults.nextPosition;
		succession flow accelerationOutput from calculateAcceleration.acceleration to outputResults.acceleration;
		action outputResults {
			in nextSpeed;
			in nextPosition;
			in acceleration;
			out speed;
			out position;
			out acceleration;
		}
	}
}

package 'VehicleLongitudinalDynamics' {
	attribute def InitialPosition;
	attribute def InitialVelocity;
	attribute def Mass;
	attribute def WheelEndDrivePower;
	attribute def TimeInterval;
	attribute def DragCoefficient;
	attribute def FrictionCoefficient;
	attribute def Acceleration;
	attribute def FinalVelocity;
	attribute def FinalPosition;
	attribute def DriveForce;
	attribute def DragForce;
	attribute def FrictionForce;
	attribute def NetForce;
	port def InputPort {
		out initialPosition: InitialPosition;
		out initialVelocity: InitialVelocity;
		out mass: Mass;
		out wheelEndDrivePower: WheelEndDrivePower;
		out timeInterval: TimeInterval;
		out dragCoefficient: DragCoefficient;
		out frictionCoefficient: FrictionCoefficient;
	}
	port def OutputPort {
		out acceleration: Acceleration;
		out finalVelocity: FinalVelocity;
		out finalPosition: FinalPosition;
	}
	port def DragForcePort {
		in dragForce: DragForce;
	}
	port def FrictionForcePort {
		in frictionForce: FrictionForce;
	}
	part context {
		part user {
			port inputPort: InputPort;
			perform longitudinalDynamicsComputation.sendInputParameters {
				out initialPosition = inputPort.initialPosition;
				out initialVelocity = inputPort.initialVelocity;
				out mass = inputPort.mass;
				out wheelEndDrivePower = inputPort.wheelEndDrivePower;
				out timeInterval = inputPort.timeInterval;
				out dragCoefficient = inputPort.dragCoefficient;
				out frictionCoefficient = inputPort.frictionCoefficient;
			}
		}
		interface userToVehicle connect user.inputPort to vehicle.inputPort {
			ref flow references longitudinalDynamicsComputation.inputFlow
				from source.initialPosition to target.initialPosition;
				from source.initialVelocity to target.initialVelocity;
				from source.mass to target.mass;
				from source.wheelEndDrivePower to target.wheelEndDrivePower;
				from source.timeInterval to target.timeInterval;
				from source.dragCoefficient to target.dragCoefficient;
				from source.frictionCoefficient to target.frictionCoefficient;
		}
		part vehicle {
			port inputPort: ~InputPort;
			perform longitudinalDynamicsComputation.calculateDriveForce {
				in wheelEndDrivePower = inputPort.wheelEndDrivePower;
				in initialVelocity = inputPort.initialVelocity;
				out driveForce = dragForcePort.driveForce;
			}
			port dragForcePort: ~DragForcePort;
			perform longitudinalDynamicsComputation.calculateDragForce {
				in initialVelocity = inputPort.initialVelocity;
				in dragCoefficient = inputPort.dragCoefficient;
				out dragForce = dragForcePort.dragForce;
			}
			perform longitudinalDynamicsComputation.calculateFrictionForce {
				in mass = inputPort.mass;
				in frictionCoefficient = inputPort.frictionCoefficient;
				out frictionForce = frictionForcePort.frictionForce;
			}
			port frictionForcePort: ~FrictionForcePort;
			perform longitudinalDynamicsComputation.computeNetForce {
				in driveForce = dragForcePort.driveForce;
				in dragForce = dragForcePort.dragForce;
				in frictionForce = frictionForcePort.frictionForce;
				out netForce = accelerationCalculation.netForce;
			}
			perform longitudinalDynamicsComputation.calculateAcceleration {
				in netForce = accelerationCalculation.netForce;
				in mass = inputPort.mass;
				out acceleration = accelerationOutput.acceleration;
			}
			perform longitudinalDynamicsComputation.updateVelocity {
				in initialVelocity = inputPort.initialVelocity;
				in acceleration = accelerationOutput.acceleration;
				in timeInterval = inputPort.timeInterval;
				out finalVelocity = velocityOutput.finalVelocity;
			}
			perform longitudinalDynamicsComputation.updatePosition {
				in initialPosition = inputPort.initialPosition;
				in initialVelocity = inputPort.initialVelocity;
				in acceleration = accelerationOutput.acceleration;
				in timeInterval = inputPort.timeInterval;
				out finalPosition = positionOutput.finalPosition;
			}
			port outputPort: OutputPort;
			perform longitudinalDynamicsComputation.outputResults {
				in acceleration = accelerationOutput.acceleration;
				in finalVelocity = velocityOutput.finalVelocity;
				in finalPosition = positionOutput.finalPosition;
				out acceleration = outputPort.acceleration;
				out finalVelocity = outputPort.finalVelocity;
				out finalPosition = outputPort.finalPosition;
			}
		}
		interface vehicleToAirResistance connect vehicle.dragForcePort to airResistance.dragForcePort {
			ref flow references longitudinalDynamicsComputation.dragForceFlow
				from source.driveForce to target.driveForce;
		}
		part airResistance {
			port dragForcePort: DragForcePort;
		}
		interface vehicleToFriction connect vehicle.frictionForcePort to friction.frictionForcePort {
			ref flow references longitudinalDynamicsComputation.frictionForceFlow
				from source.frictionForce to target.frictionForce;
		}
		part friction {
			port frictionForcePort: FrictionForcePort;
		}
	}
	action longitudinalDynamicsComputation {
		action sendInputParameters {
			out initialPosition: InitialPosition;
			out initialVelocity: InitialVelocity;
			out mass: Mass;
			out wheelEndDrivePower: WheelEndDrivePower;
			out timeInterval: TimeInterval;
			out dragCoefficient: DragCoefficient;
			out frictionCoefficient: FrictionCoefficient;
		}
		succession flow inputFlow from sendInputParameters.initialPosition to calculateDriveForce.initialPosition;
		succession flow inputFlow from sendInputParameters.initialVelocity to calculateDriveForce.initialVelocity;
		succession flow inputFlow from sendInputParameters.mass to calculateFrictionForce.mass;
		succession flow inputFlow from sendInputParameters.wheelEndDrivePower to calculateDriveForce.wheelEndDrivePower;
		succession flow inputFlow from sendInputParameters.timeInterval to updateVelocity.timeInterval;
		succession flow inputFlow from sendInputParameters.timeInterval to updatePosition.timeInterval;
		succession flow inputFlow from sendInputParameters.dragCoefficient to calculateDragForce.dragCoefficient;
		succession flow inputFlow from sendInputParameters.frictionCoefficient to calculateFrictionForce.frictionCoefficient;
		action calculateDriveForce {
			in wheelEndDrivePower: WheelEndDrivePower;
			in initialVelocity: InitialVelocity;
			out driveForce: DriveForce;
		}
		action calculateDragForce {
			in initialVelocity: InitialVelocity;
			in dragCoefficient: DragCoefficient;
			out dragForce: DragForce;
		}
		action calculateFrictionForce {
			in mass: Mass;
			in frictionCoefficient: FrictionCoefficient;
			out frictionForce: FrictionForce;
		}
		action computeNetForce {
			in driveForce: DriveForce;
			in dragForce: DragForce;
			in frictionForce: FrictionForce;
			out netForce: NetForce;
		}
		action calculateAcceleration {
			in netForce: NetForce;
			in mass: Mass;
			out acceleration: Acceleration;
		}
		action updateVelocity {
			in initialVelocity: InitialVelocity;
			in acceleration: Acceleration;
			in timeInterval: TimeInterval;
			out finalVelocity: FinalVelocity;
		}
		action updatePosition {
			in initialPosition: InitialPosition;
			in initialVelocity: InitialVelocity;
			in acceleration: Acceleration;
			in timeInterval: TimeInterval;
			out finalPosition: FinalPosition;
		}
		action outputResults {
			in acceleration: Acceleration;
			in finalVelocity: FinalVelocity;
			in finalPosition: FinalPosition;
			out acceleration: Acceleration;
			out finalVelocity: FinalVelocity;
			out finalPosition: FinalPosition;
		}
		succession flow dragForceFlow from calculateDragForce.dragForce to computeNetForce.dragForce;
		succession flow frictionForceFlow from calculateFrictionForce.frictionForce to computeNetForce.frictionForce;
		succession flow driveForceFlow from calculateDriveForce.driveForce to computeNetForce.driveForce;
		succession flow netForceFlow from computeNetForce.netForce to calculateAcceleration.netForce;
		succession flow accelerationFlow from calculateAcceleration.acceleration to updateVelocity.acceleration;
		succession flow accelerationFlow from calculateAcceleration.acceleration to updatePosition.acceleration;
		succession flow velocityFlow from updateVelocity.finalVelocity to outputResults.finalVelocity;
		succession flow positionFlow from updatePosition.finalPosition to outputResults.finalPosition;
	}
}

package 'VehicleMassManagement' {
	attribute def Mass;
	attribute def Warning;
	port def MassPort {
		out mass: Mass;
	}
	port def WarningPort {
		out warning: Warning;
	}
	part context {
		part chassis {
			port massPort: MassPort;
			perform calculateTotalMass.sendChassisMass {
				out mass = massPort.mass;
			}
		}
		part engine {
			port massPort: MassPort;
			perform calculateTotalMass.sendEngineMass {
				out mass = massPort.mass;
			}
		}
		part gearbox {
			port massPort: MassPort;
			perform calculateTotalMass.sendGearboxMass {
				out mass = massPort.mass;
			}
		}
		interface chassisToCalculator connect chassis.massPort to massCalculator.chassisMassPort {
			ref flow references calculateTotalMass.chassisMassFlow
				from source.mass to target.mass;
		}
		interface engineToCalculator connect engine.massPort to massCalculator.engineMassPort {
			ref flow references calculateTotalMass.engineMassFlow
				from source.mass to target.mass;
		}
		interface gearboxToCalculator connect gearbox.massPort to massCalculator.gearboxMassPort {
			ref flow references calculateTotalMass.gearboxMassFlow
				from source.mass to target.mass;
		}
		part massCalculator {
			port chassisMassPort: ~MassPort;
			port engineMassPort: ~MassPort;
			port gearboxMassPort: ~MassPort;
			perform calculateTotalMass.calculateTotal {
				in chassisMass = chassisMassPort.mass;
				in engineMass = engineMassPort.mass;
				in gearboxMass = gearboxMassPort.mass;
				out totalMass = totalMassPort.mass;
			}
			port totalMassPort: MassPort;
		}
		interface calculatorToWarning connect massCalculator.totalMassPort to warningSystem.totalMassPort {
			ref flow references calculateTotalMass.totalMassFlow
				from source.mass to target.mass;
		}
		part warningSystem {
			port totalMassPort: ~MassPort;
			perform checkTotalMass.checkLimit {
				in totalMass = totalMassPort.mass;
				out warning = warningPort.warning;
			}
			port warningPort: WarningPort;
		}
	}
	action calculateTotalMass {
		action sendChassisMass { out mass: Mass; }
		action sendEngineMass { out mass: Mass; }
		action sendGearboxMass { out mass: Mass; }
		succession flow chassisMassFlow from sendChassisMass.mass to calculateTotal.mass;
		succession flow engineMassFlow from sendEngineMass.mass to calculateTotal.mass;
		succession flow gearboxMassFlow from sendGearboxMass.mass to calculateTotal.mass;
		action calculateTotal { 
			in chassisMass: Mass;
			in engineMass: Mass;
			in gearboxMass: Mass;
			out mass: Mass;
		}
		succession flow totalMassFlow from calculateTotal.mass to checkLimit.totalMass;
		action checkLimit { 
			in totalMass: Mass;
			out warning: Warning;
			if (totalMass > 2500) {
				warning = "Overweight: Total mass exceeds 2,500 kg limit.";
			}
		}
	}
}

package 'VehicleMassBudgeting' {
	attribute def ChassisMass;
	attribute def EngineMass;
	attribute def TransmissionMass;
	attribute def TotalMass;
	attribute def MassLimit = 2500;

	part context {
		part vehicle {
			part chassis {
				attribute mass: ChassisMass;
			}
			part engine {
				attribute mass: EngineMass;
			}
			part transmission {
				attribute mass: TransmissionMass;
			}
			perform calculateTotalMass {
				in chassisMass = chassis.mass;
				in engineMass = engine.mass;
				in transmissionMass = transmission.mass;
				out totalMass = TotalMass;
			}
		}
	}
	action calculateTotalMass {
		in chassisMass: ChassisMass;
		in engineMass: EngineMass;
		in transmissionMass: TransmissionMass;
		out totalMass: TotalMass;
	}
	action checkMassConstraint {
		in totalMass: TotalMass;
		in massLimit: MassLimit;
	}
	succession flow from calculateTotalMass.totalMass to checkMassConstraint.totalMass;
}

package 'VehicleMassCompliance' {
	attribute def ChassisMass;
	attribute def EngineMass;
	attribute def TransmissionMass;
	attribute def TotalMass;
	attribute def ComplianceStatus;
	port def MassPort {
		out mass : ChassisMass, EngineMass, TransmissionMass;
	}
	port def CompliancePort {
		out compliance : ComplianceStatus;
	}
	part context {
		part chassis {
			port massPort: MassPort;
			perform checkVehicleMassCompliance.getChassisMass {
				out mass = massPort.mass;
			}
		}
		interface chassisToVehicle connect chassis.massPort to vehicle.massPort {
			ref flow references checkVehicleMassCompliance.chassisMassFlow
				from source.mass to target.mass;
		}
		part engine {
			port massPort: MassPort;
			perform checkVehicleMassCompliance.getEngineMass {
				out mass = massPort.mass;
			}
		}
		interface engineToVehicle connect engine.massPort to vehicle.massPort {
			ref flow references checkVehicleMassCompliance.engineMassFlow
				from source.mass to target.mass;
		}
		part transmission {
			port massPort: MassPort;
			perform checkVehicleMassCompliance.getTransmissionMass {
				out mass = massPort.mass;
			}
		}
		interface transmissionToVehicle connect transmission.massPort to vehicle.massPort {
			ref flow references checkVehicleMassCompliance.transmissionMassFlow
				from source.mass to target.mass;
		}
		part vehicle {
			port massPort: ~MassPort;
			perform checkVehicleMassCompliance.sumTotalMass {
				in chassisMass = massPort.mass;
				in engineMass = massPort.mass;
				in transmissionMass = massPort.mass;
				out totalMass = totalMass;
			}
			port compliancePort: CompliancePort;
			perform checkVehicleMassCompliance.checkCompliance {
				in totalMass = sumTotalMass.totalMass;
				out compliance = compliancePort.compliance;
			}
		}
	}
	action checkVehicleMassCompliance {
		action getChassisMass { out mass: ChassisMass; }
		action getEngineMass { out mass: EngineMass; }
		action getTransmissionMass { out mass: TransmissionMass; }
		succession flow chassisMassFlow from getChassisMass.mass to sumTotalMass.chassisMass;
		succession flow engineMassFlow from getEngineMass.mass to sumTotalMass.engineMass;
		succession flow transmissionMassFlow from getTransmissionMass.mass to sumTotalMass.transmissionMass;
		action sumTotalMass { 
			in chassisMass: ChassisMass; 
			in engineMass: EngineMass; 
			in transmissionMass: TransmissionMass; 
			out totalMass: TotalMass = chassisMass + engineMass + transmissionMass; 
		}
		action checkCompliance { 
			in totalMass: TotalMass; 
			out compliance: ComplianceStatus = if totalMass <= 2500 then true else false; 
		}
	}
}

