		part warningSystem {  
			port totalWeightPort: ~TotalWeightPort;  
			port warningPort: WarningPort;  
			perform weightManagementRegion.checkWeightLimit {  
				in totalWeight = totalWeightPort.totalWeight;  
				out warning = warningPort.warning;  
			}  
		}  
	}  
	action weightManagementRegion {  
		action recordChassisWeight { out weight: ChassisWeight; }  
		action recordEngineWeight { out weight: EngineWeight; }  
		action recordTransmissionWeight { out weight: TransmissionWeight; }  
		succession flow chassisWeightFlow from recordChassisWeight.weight to sumWeights.chassisWeight;  
		succession flow engineWeightFlow from recordEngineWeight.weight to sumWeights.engineWeight;  
		succession flow transmissionWeightFlow from recordTransmissionWeight.weight to sumWeights.transmissionWeight;  
		action sumWeights {  
			in chassisWeight: ChassisWeight;  
			in engineWeight: EngineWeight;  
			in transmissionWeight: TransmissionWeight;  
			out totalWeight: TotalWeight;  
		}  
		succession flow totalWeightFlow from sumWeights.totalWeight to checkWeightLimit.totalWeight;  
		action checkWeightLimit {  
			in totalWeight: TotalWeight;  
			out warning: WarningSignal;  
		}  
		succession flow warningFlow from checkWeightLimit.warning to sendWarning.warning;  
		action sendWarning { out warning: WarningSignal; }  
	}  
}

package 'VehicleSystem' {  
	attribute def mass : Real;  
	attribute def initialVelocity : Real;  
	attribute def finalVelocity : Real;  
	attribute def appliedForce : Real;  
	attribute def timePeriod : Real;  
	attribute def chassisMass : Real;  
	attribute def engineMass : Real;  
	attribute def transmissionMass : Real;  

	part context {  
		part vehicle {  
			part chassis {  
				attribute chassisMass;  
			}  
			part engine {  
				attribute engineMass;  
			}  
			part transmission {  
				attribute transmissionMass;  
			}  
		}  
		action massConstraintRegion {  
			action calculateTotalMass {  
				in chassisMass = vehicle.chassis.chassisMass;  
				in engineMass = vehicle.engine.engineMass;  
				in transmissionMass = vehicle.transmission.transmissionMass;  
				out totalMass = mass;  
			}  
			action checkTotalMassConstraint {  
				in totalMass = calculateTotalMass.mass;  
				constraint totalMass <= 2500;  
			}  
			action verifyDynamicConstraints {  
				in mass = calculateTotalMass.mass;  
				in initialVelocity;  
				in finalVelocity;  
				in appliedForce;  
				in timePeriod;  
				constraint mass > 0;  
				constraint appliedForce * timePeriod == mass * (finalVelocity - initialVelocity);  
			}  
		}  
		succession flow from calculateTotalMass.chassisMass to checkTotalMassConstraint.chassisMass;  
		succession flow from calculateTotalMass.engineMass to checkTotalMassConstraint.engineMass;  
		succession flow from calculateTotalMass.transmissionMass to checkTotalMassConstraint.transmissionMass;  
		succession flow from calculateTotalMass.totalMass to verifyDynamicConstraints.mass;  
	}  
}

package 'VehicleMaintenanceSystem' {
	attribute def MaintenanceCmd;
	attribute def HealthStatus;
	attribute def Time;
	attribute def MaintenanceInterval;
	attribute def MaxTemperature;
	port def MaintenanceCmdPort {
		out maintenanceCmd : MaintenanceCmd;
	}
	port def HealthStatusPort {
		out healthStatus: HealthStatus;
	}
	port def TimePort {
		in currentTime: Time;
	}
	part context {
		part vehicle {
			port maintenanceCmdPort: MaintenanceCmdPort;
			port healthStatusPort: HealthStatusPort;
			attribute nextMaintenanceTime: Time;
			attribute interval: MaintenanceInterval;
			attribute maxTemp: MaxTemperature;
			perform maintenanceScheduler.checkMaintenanceDue {
				in currentTime = timeSource.currentTime;
				out maintenanceCmd = maintenanceCmdPort.maintenanceCmd;
			}
			perform maintenanceProcess.updateNextMaintenance {
				in currentTime = timeSource.currentTime;
				in duration = maintenanceProcess.duration;
				out nextMaintenanceTime = nextMaintenanceTime;
			}
		}
		part maintenanceScheduler {
			port maintenanceCmdPort: ~MaintenanceCmdPort;
			port timePort: TimePort;
			perform maintenanceScheduler.checkMaintenanceDue {
				in currentTime = timePort.currentTime;
				out maintenanceCmd = maintenanceCmdPort.maintenanceCmd;
			}
		}
		part maintenanceProcess {
			port maintenanceCmdPort: ~MaintenanceCmdPort;
			port healthStatusPort: ~HealthStatusPort;
			attribute startTime: Time;
			attribute endTime: Time;
			perform maintenanceProcess.initiateMaintenance {
				in maintenanceCmd = maintenanceCmdPort.maintenanceCmd;
				constraint startTime >= vehicle.nextMaintenanceTime;
				constraint startTime - vehicle.nextMaintenanceTime <= 2s;
				out healthStatusPort.healthStatus = "under maintenance";
			}
			perform maintenanceProcess.performMaintenance {
				in startTime = startTime;
				constraint endTime - startTime <= 48h;
			}
			perform maintenanceProcess.completeMaintenance {
				out healthStatusPort.healthStatus = "normal";
			}
		}
		part timeSource {
			port timePort: TimePort;
			perform timeSource.provideCurrentTime {
				out timePort.currentTime = currentTime;
			}
		}
	}
	action maintenanceScheduler {
		action checkMaintenanceDue {
			in currentTime: Time;
			out maintenanceCmd: MaintenanceCmd;
		}
	}
	action maintenanceProcess {
		action initiateMaintenance {
			in maintenanceCmd: MaintenanceCmd;
			in startTime: Time;
			out healthStatus: HealthStatus;
		}
		action performMaintenance {
			in startTime: Time;
			constraint duration = endTime - startTime <= 48h;
		}
		action completeMaintenance {
			out healthStatus: HealthStatus;
		}
		succession flow maintenanceCmdFlow from checkMaintenanceDue.maintenanceCmd to initiateMaintenance.maintenanceCmd;
		succession flow maintenanceDurationFlow from initiateMaintenance.startTime to performMaintenance.startTime;
		succession flow maintenanceCompletionFlow from performMaintenance.endTime to completeMaintenance;
	}
}

package 'VehicleSystem' {
	attribute def TotalMass;
	attribute def CurbWeight;
	attribute def FuelMass;
	attribute def MaxMassLimit;
	attribute def EngineTorque;
	attribute def EngineSpeed;
	port def TorquePort {
		out torque: EngineTorque;
	}
	part context {
		part vehicle {
			part curbWeight;
			part fuelMass;
			constraint totalMassConstraint {
				TotalMass = CurbWeight + FuelMass;
				TotalMass <= MaxMassLimit;
			}
			constraint fuelMassConstraint {
				FuelMass > 0;
			}
		}
		interface vehicleMassConstraint {
			ref CurbWeight references vehicle.curbWeight;
			ref FuelMass references vehicle.fuelMass;
			ref TotalMass references vehicle.totalMassConstraint.TotalMass;
			ref MaxMassLimit references vehicle.totalMassConstraint.MaxMassLimit;
		}
		part engine {
			perform engineOperation {
				out torquePort: TorquePort;
			}
		}
		interface engineToClutch connect engine.torquePort to clutch.torquePort {
			ref engineOperation.torquePort.torque to clutchOperation.torquePort.torque;
		}
		part clutch {
			port torquePort: ~TorquePort;
			perform clutchOperation {
				in torquePort: TorquePort;
				out torquePort: TorquePort;
			}
		}
		interface clutchToTransmission connect clutch.torquePort to transmission.torquePort {
			ref clutchOperation.torquePort.torque to transmissionOperation.torquePort.torque;
		}
		part transmission {
			port torquePort: ~TorquePort;
			perform transmissionOperation {
				in torquePort: TorquePort;
			}
		}
	}
	action engineOperation {
		action generateTorque {
			in engineSpeed: EngineSpeed;
			out torque: EngineTorque;
			constraint speedTorqueCurve {
				EngineTorque = function(EngineSpeed); 
			}
		}
		succession flow torqueFlow from generateTorque.torque to torquePort.torque;
	}
	action clutchOperation {
		action transmitTorque {
			in torque: EngineTorque;
			out torque: EngineTorque;
		}
		succession flow torqueTransfer from torquePort.torque to transmitTorque.torque;
		succession flow torqueOutput from transmitTorque.torque to torquePort.torque;
	}
	action transmissionOperation {
		action deliverPower {
			in torque: EngineTorque;
		}
		succession flow powerDelivery from torquePort.torque to deliverPower.torque;
	}
}

package 'VehicleSystem' {
	attribute def dryMass;
	attribute def fuelMass;
	attribute def torque;
	attribute def speed;
	port def TorquePort {
		out torque : torque;
	}
	port def SpeedPort {
		in speed : speed;
	}
	part context {
		part vehicle {
			attribute dryMass = 1500;
			attribute fuelMass = 500;
			constraint massConstraint {
				dryMass + fuelMass <= 2000;
				dryMass <= 1500;
			}
		}
		part engine {
			port speedPort: SpeedPort;
			port torquePort: TorquePort;
			perform powertrainRegion.produceTorque {
				in speed = speedPort.speed;
				out torque = torquePort.torque;
			}
		}
		part clutch {
			port inTorquePort: ~TorquePort;
			port outTorquePort: TorquePort;
			perform powertrainRegion.transmitTorque {
				in torque = inTorquePort.torque;
				out torque = outTorquePort.torque;
			}
		}
		part transmission {
			port torquePort: ~TorquePort;
			perform powertrainRegion.receiveTorque {
				in torque = torquePort.torque;
			}
		}
		interface engineToClutch connect engine.torquePort to clutch.inTorquePort {
			ref flow references powertrainRegion.torqueFlow
				from source.torque to target.torque;
		}
		interface clutchToTransmission connect clutch.outTorquePort to transmission.torquePort {
			ref flow references powertrainRegion.torqueFlow
				from source.torque to target.torque;
		}
	}
	action powertrainRegion {
		action produceTorque { 
			in speed: speed; 
			out torque: torque; 
			constraint torqueConstraint references 'Table1_SpeedTorqueCurve';
		}
		succession flow torqueFlow from produceTorque.torque to transmitTorque.torque;
		action transmitTorque { 
			in torque: torque; 
			out torque: torque; 
		}
		succession flow torqueFlow from transmitTorque.torque to receiveTorque.torque;
		action receiveTorque { 
			in torque: torque; 
		}
	}
}

