		interface vehicleToAnalysis connect vehicle.powerDemandPort to analysisEngine.powerDemandPort {
			ref flow references calculatePowerDemand.powerDemandFlow
				from source.powerDemand to target.powerDemand;
		}

		part analysisEngine {
			port drivingCyclePort: ~DrivingCyclePort;
			port powerDemandPort: ~PowerDemandPort;
			perform calculatePowerDemand.analyzePowerDemand {
				in drivingCycle = drivingCyclePort.drivingCycle;
				in powerDemand = powerDemandPort.powerDemand;
				out acceleration = accelerationPort.acceleration;
				out speed = speedPort.speed;
				out position = positionPort.position;
			}
			port accelerationPort: AccelerationPort;
			port speedPort: SpeedPort;
			port positionPort: PositionPort;
		}

		interface analysisToFuel connect analysisEngine.positionPort to fuelCalculator.positionPort {
			ref flow references calculatePosition.positionFlow
				from source.position to target.position;
		}
		interface analysisToFuel2 connect analysisEngine.speedPort to fuelCalculator.speedPort {
			ref flow references calculateSpeed.speedFlow
				from source.speed to target.speed;
		}

		part fuelCalculator {
			port positionPort: ~PositionPort;
			port speedPort: ~SpeedPort;
			port vehicleAttributesPort: ~VehicleAttributesPort;
			perform computeFuelEconomy.calculateEconomy {
				in position = positionPort.position;
				in speed = speedPort.speed;
				in vehicleAttributes = vehicleAttributesPort.vehicleAttributes;
				out fuelEconomy = fuelEconomyPort.fuelEconomy;
			}
			port fuelEconomyPort: FuelEconomyPort;
		}

