			ref flow references computeFuelEconomy.economyFlow
				from source.fuelEconomy to target.fuelEconomy;
		}

		part complianceChecker {
			port fuelEconomyPort: ~FuelEconomyPort;
			port targetPort: TargetPort;
			perform verifyCompliance.checkTarget {
				in fuelEconomy = fuelEconomyPort.fuelEconomy;
				in target = targetPort.target;
				out result: Boolean;
			}
