	action setDrivingCycle {
		action sendDrivingCycle { out drivingCycle: DrivingCycle; }
	}

	action calculatePowerDemand {
		succession flow attributesFlow from sendVehicleAttributes.vehicleAttributes to analyzePowerDemand.vehicleAttributes;
		succession flow powerDemandFlow from analyzePowerDemand.powerDemand to calculateEconomy.vehicleAttributes;
		action analyzePowerDemand { 
			in drivingCycle: DrivingCycle; 
			in powerDemand: PowerDemand; 
			out acceleration: AccelerationProfile; 
			out speed: SpeedProfile; 
			out position: PositionProfile; 
		}
	}

	action calculateAcceleration {
		succession flow accelerationFlow from analyzePowerDemand.acceleration to ...;
	}

	action calculateSpeed {
		succession flow speedFlow from analyzePowerDemand.speed to calculateSpeed.speed;
	}

	action calculatePosition {
		succession flow positionFlow from analyzePowerDemand.position to calculatePosition.position;
	}

	action computeFuelEconomy {
		succession flow positionFlow from calculatePosition.position to calculateEconomy.position;
		succession flow speedFlow from calculateSpeed.speed to calculateEconomy.speed;
		action calculateEconomy { 
			in position: PositionProfile; 
			in speed: SpeedProfile; 
			in vehicleAttributes: VehicleAttributes; 
			out fuelEconomy: FuelEconomy; 
		}
	}

	action verifyCompliance {
		succession flow economyFlow from calculateEconomy.fuelEconomy to checkTarget.fuelEconomy;
		succession flow targetFlow from sendTarget.target to checkTarget.target;
		action checkTarget { 
			in fuelEconomy: FuelEconomy; 
			in target: EnergyConsumptionTarget; 
			out result: Boolean; 
		}
	}
}

package 'VehicleFuelEconomySimulation' {
	attribute def CurbWeight;
	attribute def Load;
	attribute def WheelDiameter;
	attribute def TransmissionEfficiency;
	attribute def FuelEconomy;
	attribute def DrivingScenario;
	attribute def WheelPower;
	attribute def Acceleration;
	attribute def EnergyConsumption;
	attribute def EnginePowerDemand;
	port def ScenarioPort {
		out drivingScenario: DrivingScenario;
	}
	port def FuelEconomyPort {
		out fuelEconomy: FuelEconomy;
	}
	port def ParameterPort {
		in curbWeight: CurbWeight;
		in load: Load;
		in wheelDiameter: WheelDiameter;
		in transmissionEfficiency: TransmissionEfficiency;
	}
	part context {
		part user {
			port scenarioPort: ScenarioPort;
			port fuelEconomyPort: ~FuelEconomyPort;
			perform simulationRequest.sendDrivingScenario {
				out drivingScenario = scenarioPort.drivingScenario;
			}
			perform resultReception.receiveFuelEconomy {
				in fuelEconomy = fuelEconomyPort.fuelEconomy;
			}
		}
		interface userToSimulation connect user.scenarioPort to simulation.scenarioPort {
			ref flow references simulationRequest.scenarioFlow
				from source.drivingScenario to target.drivingScenario;
		}
		interface simulationToUser connect simulation.fuelEconomyPort to user.fuelEconomyPort {
			ref flow references determineFuelEconomy.fuelEconomyFlow
				from source.fuelEconomy to target.fuelEconomy;
		}
		part simulation {
			port scenarioPort: ~ScenarioPort;
			port fuelEconomyPort: FuelEconomyPort;
			perform drivingScenarioProcessing.setDrivingScenario {
				in drivingScenario = scenarioPort.drivingScenario;
			}
			perform drivingScenarioProcessing.calculateWheelPower {
				in drivingScenario = scenarioPort.drivingScenario;
				in curbWeight = parameterPort.curbWeight;
				in load = parameterPort.load;
				in wheelDiameter = parameterPort.wheelDiameter;
				out wheelPower = wheelPowerPort.wheelPower;
			}
			port wheelPowerPort: ~WheelPowerPort;
			perform vehicleDynamics.applyVehicleDynamics {
				in wheelPower = wheelPowerPort.wheelPower;
				in transmissionEfficiency = parameterPort.transmissionEfficiency;
				out acceleration = accelerationPort.acceleration;
				out energyConsumption = energyConsumptionPort.energyConsumption;
			}
			port accelerationPort: ~AccelerationPort;
			port energyConsumptionPort: ~EnergyConsumptionPort;
			perform engineAnalysis.estimateEnginePower {
				in energyConsumption = energyConsumptionPort.energyConsumption;
				out enginePowerDemand = enginePowerPort.enginePowerDemand;
			}
			port enginePowerPort: ~EnginePowerPort;
			perform fuelEconomyAnalysis.determineFuelEconomy {
				in enginePowerDemand = enginePowerPort.enginePowerDemand;
				in curbWeight = parameterPort.curbWeight;
				in load = parameterPort.load;
				in wheelDiameter = parameterPort.wheelDiameter;
				in transmissionEfficiency = parameterPort.transmissionEfficiency;
				out fuelEconomy = fuelEconomyPort.fuelEconomy;
			}
		}
	}
	action drivingScenarioProcessing {
		action sendDrivingScenario { out drivingScenario: DrivingScenario; }
		succession flow scenarioFlow from sendDrivingScenario.drivingScenario to setDrivingScenario.drivingScenario;
		action setDrivingScenario { in drivingScenario: DrivingScenario; }
	}
	action vehicleDynamics {
		action calculateWheelPower { in drivingScenario; in curbWeight; in load; in wheelDiameter; out wheelPower: WheelPower; }
		succession flow wheelPowerFlow from calculateWheelPower.wheelPower to applyVehicleDynamics.wheelPower;
		action applyVehicleDynamics { in wheelPower: WheelPower; in transmissionEfficiency; out acceleration: Acceleration; out energyConsumption: EnergyConsumption; }
	}
	action engineAnalysis {
		action estimateEnginePower { in energyConsumption: EnergyConsumption; out enginePowerDemand: EnginePowerDemand; }
		succession flow enginePowerFlow from estimateEnginePower.enginePowerDemand to determineFuelEconomy.enginePowerDemand;
	}
	action fuelEconomyAnalysis {
		action determineFuelEconomy { in enginePowerDemand: EnginePowerDemand; in curbWeight; in load; in wheelDiameter; in transmissionEfficiency; out fuelEconomy: FuelEconomy; }
	}
}

