				from source.power to target.sixPower;
			ref flow references aggregateSixCylinderKPIs.sendSixKPIs
				from source.mass to target.sixMass;
			ref flow references aggregateSixCylinderKPIs.sendSixKPIs
				from source.efficiency to target.sixEfficiency;
			ref flow references aggregateSixCylinderKPIs.sendSixKPIs
				from source.cost to target.sixCost;
		}
		part evaluationFunction {
			port kpiInputPort: ~KPIPort;
			port evaluationOutputPort: EvaluationPort;
			perform calculateEvaluation.evaluateEngines {
				in fourPower = kpiInputPort.power;
				in fourMass = kpiInputPort.mass;
				in fourEfficiency = kpiInputPort.efficiency;
				in fourCost = kpiInputPort.cost;
				in sixPower = kpiInputPort.power;
				in sixMass = kpiInputPort.mass;
				in sixEfficiency = kpiInputPort.efficiency;
				in sixCost = kpiInputPort.cost;
				out evaluationScore = evaluationOutputPort.evaluationScore;
			}
		}
		interface evaluationToRecommendation connect evaluationFunction.evaluationOutputPort to recommendation.evaluationInputPort {
			ref flow references calculateEvaluation.evaluateEngines
				from source.evaluationScore to target.evaluationScore;
		}
		part recommendation {
			port evaluationInputPort: ~EvaluationPort;
			port selectionOutputPort: Port {
				out selectedEngine: EngineType;
			}
			perform recommendEngine.selectOptimalEngine {
				in evaluationScore = evaluationInputPort.evaluationScore;
				out selectedEngine = selectionOutputPort.selectedEngine;
			}
		}
	}
	action calculateEvaluation {
		action evaluateEngines {
			in fourPower: Power;
			in fourMass: Mass;
			in fourEfficiency: Efficiency;
			in fourCost: Cost;
			in sixPower: Power;
			in sixMass: Mass;
			in sixEfficiency: Efficiency;
			in sixCost: Cost;
			out evaluationScore: EvaluationScore;
		}
	}
	action recommendEngine {
		action selectOptimalEngine {
			in evaluationScore: EvaluationScore;
			out selectedEngine: EngineType;
		}
	}
	action aggregateFourCylinderKPIs {
		action sendFourKPIs {
			out power: Power;
			out mass: Mass;
			out efficiency: Efficiency;
			out cost: Cost;
		}
	}
	action aggregateSixCylinderKPIs {
		action sendSixKPIs {
			out power: Power;
			out mass: Mass;
			out efficiency: Efficiency;
			out cost: Cost;
		}
	}
}

package 'VehicleWeightCheck' {
