		out curbWeight : CurbWeight;
	}
	port def ProcessedDataPort {
		out processedData : ProcessedData;
	}
	port def EvaluationPort {
		out evaluationResult : EvaluationResult;
	}
	part context {
		part vehicle {
			port weightDataPort: WeightDataPort;
			perform evaluateCurbWeightProcess.sendCurbWeight {
				out curbWeight = weightDataPort.curbWeight;
			}
		}
		interface vehicleToTestingSystem connect vehicle.weightDataPort to testingSystem.weightDataPort {
			ref flow references evaluateCurbWeightProcess.dataCollectionFlow
				from source.curbWeight to target.curbWeight;
		}
		part testingSystem {
			port weightDataPort: ~WeightDataPort;
			port processedDataPort: ProcessedDataPort;
			perform evaluateCurbWeightProcess.processWeightData {
				in curbWeight = weightDataPort.curbWeight;
				out processedData = processedDataPort.processedData;
			}
		}
		interface testingSystemToEvaluator connect testingSystem.processedDataPort to evaluator.processedDataPort {
			ref flow references evaluateCurbWeightProcess.dataProcessingFlow
				from source.processedData to target.processedData;
		}
		part evaluator {
			port processedDataPort: ~ProcessedDataPort;
			port evaluationPort: EvaluationPort;
			perform evaluateCurbWeightProcess.evaluateCompliance {
				in processedData = processedDataPort.processedData;
				out evaluationResult = evaluationPort.evaluationResult;
