		succession flow dataCollectionFlow from sendCurbWeight.curbWeight to processWeightData.curbWeight;
		action processWeightData { in curbWeight; out processedData: ProcessedData; }
		succession flow dataProcessingFlow from processWeightData.processedData to evaluateCompliance.processedData;
		action evaluateCompliance { in processedData: ProcessedData; out evaluationResult: EvaluationResult; }
	}
