package 'VehicleMassVerification' {  
	attribute def vehicleMass;  
	attribute def measuredMass;  
	attribute def qualificationStatus;  
	port def VehicleMassPort {  
		out vehicleMass : vehicleMass;  
	}  
	port def MeasuredMassPort {  
		out measuredMass : measuredMass;  
	}  
	port def QualificationStatusPort {  
		out qualificationStatus : qualificationStatus;  
	}  
	part context {  
		part vehicle {  
			port vehicleMassPort : VehicleMassPort;  
			perform measureAndVerifyMass.sendVehicleMass {  
				out vehicleMass = vehicleMassPort.vehicleMass;  
			}  
		}  
		interface vehicleToWeighing connect vehicle.vehicleMassPort to weighingEquipment.vehicleMassPort {  
			ref flow references measureAndVerifyMass.massFlow  
				from source.vehicleMass to target.vehicleMass;  
		}  
		part weighingEquipment {  
			port vehicleMassPort : ~VehicleMassPort;  
			perform measureAndVerifyMass.measureVehicleMass {  
				in vehicleMass = vehicleMassPort.vehicleMass;  
				out measuredMass = measuredMassPort.measuredMass;  
			}  
			port measuredMassPort : MeasuredMassPort;  
		}  
		interface weighingToVerification connect weighingEquipment.measuredMassPort to massVerificationUnit.measuredMassPort {  
			ref flow references measureAndVerifyMass.measuredMassFlow  
				from source.measuredMass to target.measuredMass;  
		}  
		part massVerificationUnit {  
			port measuredMassPort : ~MeasuredMassPort;  
