package 'TransportationSystem' {
	attribute def EntryExitCmd;
	attribute def EnvironmentalData;
	attribute def InfrastructureSignal;
	port def EntryExitCmdPort {
		out entryExitCmd : EntryExitCmd;
	}
	port def EnvironmentalDataPort {
		in environmentalData : EnvironmentalData;
	}
	port def InfrastructureSignalPort {
		out infrastructureSignal : InfrastructureSignal;
	}
	part context {
		part user {
			port entryExitCmdPort: EntryExitCmdPort;
			perform transportationTask.sendEntryExitCmd {
				out entryExitCmd = entryExitCmdPort.entryExitCmd;
			}
		}
		interface userToVehicle connect user.entryExitCmdPort to vehicle.entryExitCmdPort {
			ref flow references transportationTask.entryExitFlow
				from source.entryExitCmd to target.entryExitCmd;
		}
		part vehicle {
			port entryExitCmdPort: ~EntryExitCmdPort;
			port environmentalDataPort: EnvironmentalDataPort;
			port infrastructureSignalPort: InfrastructureSignalPort;
			perform transportationTask.handleEntryExit {
				in entryExitCmd = entryExitCmdPort.entryExitCmd;
				out safetyStatus = ensureSafety.safetyStatus;
			}
			perform transportationTask.monitorEnvironment {
				in environmentalData = environmentalDataPort.environmentalData;
				out adjustedParameters = adjustForEnvironment.adjustedParameters;
			}
			perform transportationTask.adjustForEnvironment {
				in environmentalData = monitorEnvironment.adjustedParameters;
				out light = provideComfort.light;
			}
			perform transportationTask.ensureSafety {
				in safetyInput = handleEntryExit.safetyStatus;
				out infrastructureSignal = infrastructureSignalPort.infrastructureSignal;
			}
			perform transportationTask.provideComfort {
				in comfortInput = adjustForEnvironment.light;
			}
		}
		interface vehicleToInfrastructure connect vehicle.infrastructureSignalPort to infrastructure.infrastructureSignalPort {
			ref flow references transportationTask.infrastructureFlow
				from source.infrastructureSignal to target.infrastructureSignal;
		}
		part infrastructure {
			port infrastructureSignalPort: ~InfrastructureSignalPort;
			perform transportationTask.supportInfrastructure {
				in infrastructureSignal = infrastructureSignalPort.infrastructureSignal;
			}
		}
		part environment {
			port environmentalDataPort: ~EnvironmentalDataPort;
			perform transportationTask.provideEnvironmentalData {
				out environmentalData = environmentalDataPort.environmentalData;
			}
		}
	}
	action transportationTask {
		action sendEntryExitCmd { out entryExitCmd: EntryExitCmd; }
		succession flow entryExitFlow from sendEntryExitCmd.entryExitCmd to handleEntryExit.entryExitCmd;
		action handleEntryExit { in entryExitCmd: EntryExitCmd; out safetyStatus: Boolean; }
		action provideEnvironmentalData { out environmentalData: EnvironmentalData; }
		succession flow environmentalFlow from provideEnvironmentalData.environmentalData to monitorEnvironment.environmentalData;
		action monitorEnvironment { in environmentalData: EnvironmentalData; out adjustedParameters: EnvironmentalData; }
		succession flow adjustmentFlow from monitorEnvironment.adjustedParameters to adjustForEnvironment.adjustedParameters;
		action adjustForEnvironment { in environmentalData: EnvironmentalData; out light: Light; }
		succession flow comfortFlow from adjustForEnvironment.light to provideComfort.light;
		action provideComfort { in light: Light; }
		succession flow safetyFlow from handleEntryExit.safetyStatus to ensureSafety.safetyInput;
		action ensureSafety { in safetyInput: Boolean; out infrastructureSignal: InfrastructureSignal; }
		succession flow infrastructureFlow from ensureSafety.infrastructureSignal to supportInfrastructure.infrastructureSignal;
		action supportInfrastructure { in infrastructureSignal: InfrastructureSignal; }
	}
}

package 'TransportationSystem' {  
	attribute def TransportationStatus;  
	attribute def FuelLevel;  
	port def CommandPort {  
		out command : TransportationStatus;  
	}  
	port def FuelPort {  
		out fuel : FuelLevel;  
	}  
	port def ExitPort {  
		out status : TransportationStatus;  
	}  
	part context {  
		part driver {  
			port commandPort: CommandPort;  
			perform transportationProcess.sendStartCommand {  
				out command = commandPort.command;  
			}  
			perform transportationProcess.sendRefuelCommand {  
				out command = commandPort.command;  
			}  
		}  
		part passenger1 {  
			port entryExitPort: ~ExitPort;  
			perform transportationProcess.enterVehicle {  
				out status = entryExitPort.status;  
			}  
		}  
		part passenger2 {  
			port entryExitPort: ~ExitPort;  
			perform transportationProcess.enterVehicle {  
				out status = entryExitPort.status;  
			}  
		}  
		part passenger3 {  
			port entryExitPort: ~ExitPort;  
			perform transportationProcess.enterVehicle {  
				out status = entryExitPort.status;  
			}  
		}  
		part passenger4 {  
			port entryExitPort: ~ExitPort;  
			perform transportationProcess.enterVehicle {  
				out status = entryExitPort.status;  
			}  
		}  
		part vehicle {  
			port commandPort: ~CommandPort;  
			port fuelPort: FuelPort;  
			port entryExitPort: ~ExitPort;  
			perform transportationProcess.operateVehicle {  
				in command = commandPort.command;  
				in environment = environmentalContext.environmentalConditions;  
				out fuel = fuelPort.fuel;  
				out status = entryExitPort.status;  
			}  
			perform transportationProcess.refuelVehicle {  
				in fuelLevel = fuelPort.fuel;  
				out command = commandPort.command;  
			}  
		}  
		part environment {  
			port commandPort: ~CommandPort;  
			perform transportationProcess.applyEnvironmentalConditions {  
				out command = commandPort.command;  
			}  
		}  
		part gasStation {  
			port fuelPort: ~FuelPort;  
			perform transportationProcess.provideFuel {  
				out fuel = fuelPort.fuel;  
			}  
		}  
	}  
	action transportationProcess {  
		action sendStartCommand { out command: TransportationStatus; }  
		action enterVehicle { out status: TransportationStatus; }  
		succession flow startFlow from sendStartCommand.command to operateVehicle.command;  
		action operateVehicle {  
			in command: TransportationStatus;  
			in environmentalConditions: TransportationStatus;  
			out fuel: FuelLevel;  
			out status: TransportationStatus;  
		}  
		succession flow fuelFlow from operateVehicle.fuel to refuelVehicle.fuelLevel;  
		action refuelVehicle {  
			in fuelLevel: FuelLevel;  
			out command: TransportationStatus;  
		}  
		succession flow refuelFlow from refuelVehicle.command to provideFuel.fuel;  
		action provideFuel { out fuel: FuelLevel; }  
		action applyEnvironmentalConditions { out command: TransportationStatus; }  
		succession flow envFlow from applyEnvironmentalConditions.command to operateVehicle.environmentalConditions;  
		action exitVehicle { in status: TransportationStatus; }  
	}  
}

package 'VehicleConfiguration' {
	enumeration def EngineType { fourCylinder, sixCylinder; }
	enumeration def TransmissionType { manual, automatic; }
	enumeration def CylinderDiameter { small, large; }

	part context {
		part user {
			perform configureVehicle.selectEngineType;
			perform configureVehicle.selectCylinderDiameter;
			perform configureVehicle.selectTransmissionType;
		}
		interface userToEngine connect user.engineTypePort to engine.engineTypePort {
			ref flow references configureVehicle.engineTypeFlow
				from source.engineType to target.engineType;
		}
		interface userToCylinderDiameter connect user.cylinderDiameterPort to engine.cylinderDiameterPort {
			ref flow references configureVehicle.cylinderDiameterFlow
				from source.cylinderDiameter to target.cylinderDiameter;
		}
		interface userToTransmission connect user.transmissionTypePort to transmission.transmissionTypePort {
			ref flow references configureVehicle.transmissionTypeFlow
				from source.transmissionType to target.transmissionType;
		}
		part engine {
			port engineTypePort: out EngineType;
			port cylinderDiameterPort: out CylinderDiameter;
		}
		part transmission {
			port transmissionTypePort: out TransmissionType;
		}
	}
	action configureVehicle {
		action selectEngineType { out engineType: EngineType; }
		action selectCylinderDiameter { out cylinderDiameter: CylinderDiameter; }
		action selectTransmissionType { out transmissionType: TransmissionType; }
		action checkEngineTransmissionConstraint {
			in engineType: EngineType;
			in transmissionType: TransmissionType;
			constraint engineType and transmissionType satisfy:
				(engineType = fourCylinder and transmissionType = manual) or
				(engineType = sixCylinder and transmissionType = automatic);
		}
		succession flow engineTypeFlow from selectEngineType.engineType to checkEngineTransmissionConstraint.engineType;
		succession flow transmissionTypeFlow from selectTransmissionType.transmissionType to checkEngineTransmissionConstraint.transmissionType;
	}
}

package 'ConfigurableEngine' {
