package 'AutomotiveConfiguration' {
	attribute def CylinderCount;
	enum CylinderCount {
		'4-cylinder',
		'6-cylinder'
	}
	attribute def CylinderDiameter;
	enum CylinderDiameter {
		'70mm',
		'100mm'
	}
	attribute def GearboxType;
	enum GearboxType {
		'manual',
		'automatic'
	}
	part context {
		part vehicle {
			part engine {
				property cylinderCount: CylinderCount;
				property cylinderDiameter: CylinderDiameter;
			}
			part transmission {
				property gearboxType: GearboxType;
			}
			constraint configConstraint {
				derive from engine.cylinderCount and transmission.gearboxType;
				if engine.cylinderCount == '4-cylinder' then transmission.gearboxType == 'manual';
				if engine.cylinderCount == '6-cylinder' then transmission.gearboxType == 'automatic';
			}
		}
	}
	action configuration {
		action selectEngine {
			out cylinderCount: CylinderCount;
			out cylinderDiameter: CylinderDiameter;
		}
		action selectTransmission {
			in engineCylinderCount: CylinderCount;
			out gearboxType: GearboxType;
		}
		succession flow from selectEngine.cylinderCount to selectTransmission.engineCylinderCount {
			constraint if selectEngine.cylinderCount == '4-cylinder' then selectTransmission.gearboxType == 'manual';
			constraint if selectEngine.cylinderCount == '6-cylinder' then selectTransmission.gearboxType == 'automatic';
		}
	}
}

package 'SystemAssembly' {
	attribute def MessageFormat;
	attribute def DataFormat;
	port def ComputerInterface {
		in messageFormat : MessageFormat;
		in dataFormat : DataFormat;
	}
	port def StorageInterface {
		in dataFormat : DataFormat;
		in messageFormat : MessageFormat;
	}
	part context {
		part computerSubSystem {
			port computerPort: ComputerInterface;
			perform softwareIntegration.processInformation {
				in messageFormat = computerPort.messageFormat;
				in dataFormat = computerPort.dataFormat;
				out processedData;
			}
		}
		interface computerToSoftware connect computerPort.messageFormat to softwareDef.messageFormatDef {
			ref flow references softwareIntegration.formatFlow;
		}
		interface computerToSoftwareData connect computerPort.dataFormat to softwareDef.dataFormatDef {
			ref flow references softwareIntegration.dataFlow;
		}
		part storageSubSystem {
			port storagePort: StorageInterface;
			perform softwareIntegration.storeData {
				in dataFormat = storagePort.dataFormat;
				in messageFormat = storagePort.messageFormat;
			}
		}
		interface storageToSoftwareData connect storagePort.dataFormat to softwareDef.dataFormatDef {
			ref flow references softwareIntegration.dataFlow;
		}
		interface storageToSoftwareMessage connect storagePort.messageFormat to softwareDef.messageFormatDef {
			ref flow references softwareIntegration.formatFlow;
		}
	}
	action softwareIntegration {
		action defineMessageFormat { out messageFormat: MessageFormat; }
		action defineDataFormat { out dataFormat: DataFormat; }
		succession flow formatFlow from defineMessageFormat.messageFormat to processInformation.messageFormat;
		succession flow dataFlow from defineDataFormat.dataFormat to processInformation.dataFormat;
		action processInformation { in messageFormat; in dataFormat; out processedData; }
		action storeData { in dataFormat; in messageFormat; }
		succession flow storageFlow from defineDataFormat.dataFormat to storeData.dataFormat;
		succession flow messageFlow from defineMessageFormat.messageFormat to storeData.messageFormat;
	}
}

package 'PowertrainSystem' {
