		out torqueCommand : TorqueCommand;
	}
	port def TorquePort {
		out torque: Torque;
	}
	part context {
		part logicalLayer {
			part torqueGenerator {
				port torqueCommandPort: TorqueCommandPort;
				perform generateTorqueCommand {
					out torqueCommand = torqueCommandPort.torqueCommand;
				}
			}
		}
		interface logicalToPhysical connect logicalLayer.torqueGenerator.torqueCommandPort to physicalLayer.powerUnit.torqueCommandPort {
			ref flow references generateTorqueCommand.torqueCommandFlow
				from source.torqueCommand to target.torqueCommand;
		}
		part physicalLayer {
			part powerUnit {
				port torqueCommandPort: ~TorqueCommandPort;
				perform produceTorque {
					in torqueCommand = torqueCommandPort.torqueCommand;
					out torque = torquePort.torque;
				}
				port torquePort: TorquePort;
			}
		}
	}
	action generateTorqueCommand {
		action generateTorqueCommand { out torqueCommand: TorqueCommand; }
		succession flow torqueCommandFlow from generateTorqueCommand.torqueCommand to produceTorque.torqueCommand;
		action produceTorque { in torqueCommand: TorqueCommand; out torque: Torque; }
