package 'PowerTransmissionSystem' {
	attribute def Torque;
	attribute def Power;
	port def TorquePort {
		out torque : Torque;
	}
	port def PowerPort {
		out power : Power;
	}
	part context {
		part logical {
			part torqueGenerator {
				port torquePort: TorquePort;
				perform generateTorqueLogical {
					out torque = torquePort.torque;
				}
			}
		}
		interface logicalToPowertrain connect logical.torqueGenerator.torquePort to physical.powertrain.torquePort {
			ref flow references generateTorqueLogical.torque to generateTorqueInPowertrain.torque;
		}
		part physical {
			part powertrain {
				port torquePort: ~TorquePort;
				perform generateTorqueInPowertrain {
					in torque = torquePort.torque;
					out power = powerPort.power;
				}
				port powerPort: PowerPort;
				part engine {
					port torquePort: TorquePort;
					perform generateTorquePhysical {
						out torque = torquePort.torque;
					}
				}
				interface engineToPowertrain connect engine.torquePort to powertrain.torquePort {
					ref flow references generateTorquePhysical.torque to generateTorqueInPowertrain.torque;
				}
			}
		}
	}
	action generateTorqueLogical {
		out torque : Torque;
	}
	action generateTorqueInPowertrain {
		in torque : Torque;
		out power : Power;
	}
	action generateTorquePhysical {
		out torque : Torque;
	}
	succession flow from generateTorqueLogical.torque to generateTorqueInPowertrain.torque;
	succession flow from generateTorquePhysical.torque to generateTorqueInPowertrain.torque;
}

