package 'VehicleDynamicsSimulation' {
	attribute def InitialVelocity;
	attribute def InitialPosition;
	attribute def Acceleration;
	attribute def TimeStep;
	attribute def FinalVelocity;
	attribute def FinalPosition;
	port def InputPort {
		out initialVelocity: InitialVelocity;
		out initialPosition: InitialPosition;
		out acceleration: Acceleration;
		out timeStep: TimeStep;
	}
	port def OutputPort {
		in finalVelocity: FinalVelocity;
		in finalPosition: FinalPosition;
	}
	part context {
		part user {
			port inputPort: InputPort;
			perform dynamicAnalysis.sendInputParameters {
				out initialVelocity = inputPort.initialVelocity;
				out initialPosition = inputPort.initialPosition;
				out acceleration = inputPort.acceleration;
				out timeStep = inputPort.timeStep;
			}
		}
		interface userToSimulation connect user.inputPort to simulationSystem.inputPort {
			ref flow references dynamicAnalysis.inputFlow
				from source.initialVelocity to target.initialVelocity,
				from source.initialPosition to target.initialPosition,
				from source.acceleration to target.acceleration,
				from source.timeStep to target.timeStep;
		}
		part simulationSystem {
			port inputPort: ~InputPort;
			port outputPort: ~OutputPort;
			perform dynamicAnalysis.calculateDynamicBehavior {
				in initialVelocity = inputPort.initialVelocity;
				in initialPosition = inputPort.initialPosition;
				in acceleration = inputPort.acceleration;
				in timeStep = inputPort.timeStep;
				out finalVelocity = outputPort.finalVelocity;
				out finalPosition = outputPort.finalPosition;
			}
			part modelCenterTool {
				port analysisPort: AnalysisPort;
				perform dynamicAnalysis.performAnalysis {
					in initialVelocity, initialPosition, acceleration, timeStep;
					out finalVelocity, finalPosition;
				}
			}
			interface simulationToModelCenter connect calculateDynamicBehavior to modelCenterTool.analysisPort {
				ref flow references dynamicAnalysis.analysisFlow
					from source.initialVelocity to target.initialVelocity,
					from source.initialPosition to target.initialPosition,
					from source.acceleration to target.acceleration,
					from source.timeStep to target.timeStep,
					from target.finalVelocity to source.finalVelocity,
					from target.finalPosition to source.finalPosition;
			}
		}
	}
	action dynamicAnalysis {
		action sendInputParameters {
			out initialVelocity: InitialVelocity;
			out initialPosition: InitialPosition;
			out acceleration: Acceleration;
			out timeStep: TimeStep;
		}
		succession flow inputFlow from sendInputParameters.initialVelocity to calculateDynamicBehavior.initialVelocity,
			from sendInputParameters.initialPosition to calculateDynamicBehavior.initialPosition,
			from sendInputParameters.acceleration to calculateDynamicBehavior.acceleration,
			from sendInputParameters.timeStep to calculateDynamicBehavior.timeStep;
		action calculateDynamicBehavior {
			in initialVelocity: InitialVelocity;
			in initialPosition: InitialPosition;
			in acceleration: Acceleration;
			in timeStep: TimeStep;
			out finalVelocity: FinalVelocity;
			out finalPosition: FinalPosition;
		}
		succession flow analysisFlow from calculateDynamicBehavior.initialVelocity to performAnalysis.initialVelocity,
			from calculateDynamicBehavior.initialPosition to performAnalysis.initialPosition,
			from calculateDynamicBehavior.acceleration to performAnalysis.acceleration,
			from calculateDynamicBehavior.timeStep to performAnalysis.timeStep,
			from performAnalysis.finalVelocity to calculateDynamicBehavior.finalVelocity,
			from performAnalysis.finalPosition to calculateDynamicBehavior.finalPosition;
		action performAnalysis {
			in initialVelocity: InitialVelocity;
			in initialPosition: InitialPosition;
			in acceleration: Acceleration;
			in timeStep: TimeStep;
			out finalVelocity: FinalVelocity;
			out finalPosition: FinalPosition;
		}
	}
}

