package 'RiskManagementSystem' {
	attribute def Cause;
	attribute def Probability;
	attribute def Situation;
	attribute def Failure;
	attribute def SeverityLevel;
	port def RiskScenarioPort {
		out riskScenario: Situation;
	}
	port def CausalRelationshipPort {
		in cause: Cause;
		in probability: Probability;
		out failure: Failure;
		out severity: SeverityLevel;
	}
	part context {
		part system {
			port riskScenarioPort: RiskScenarioPort;
			perform riskScenarioRegion.sendRiskScenario {
				out riskScenario = riskScenarioPort.riskScenario;
			}
		}
		interface systemToAnalyzer connect system.riskScenarioPort to analyzer.riskScenarioPort {
			ref flow references riskScenarioRegion.scenarioFlow
				from source.riskScenario to target.riskScenario;
		}
		part analyzer {
			port riskScenarioPort: ~RiskScenarioPort;
			perform riskScenarioRegion.analyzeCausalChain {
				in riskScenario = riskScenarioPort.riskScenario;
				out causalRelationship = causalRelationshipPort.cause;
				out probability = causalRelationshipPort.probability;
			}
			port causalRelationshipPort: CausalRelationshipPort;
		}
		interface analyzerToFailure connect analyzer.causalRelationshipPort to failureModel.causalInputPort {
			ref flow references riskScenarioRegion.causalFlow
				from source.cause to target.cause;
			ref flow references riskScenarioRegion.causalFlow
				from source.probability to target.probability;
		}
		part failureModel {
			port causalInputPort: ~CausalRelationshipPort;
			perform riskScenarioRegion.identifyFailure {
				in cause = causalInputPort.cause;
				in probability = causalInputPort.probability;
				out failure = causalRelationshipPort.failure;
				out severity = causalRelationshipPort.severity;
			}
			port causalRelationshipPort: ~CausalRelationshipPort;
		}
	}
	action riskScenarioRegion {
		action sendRiskScenario { out riskScenario: Situation; }
		succession flow scenarioFlow from sendRiskScenario.riskScenario to analyzeCausalChain.riskScenario;
		action analyzeCausalChain { in riskScenario: Situation; out cause: Cause; out probability: Probability; }
		succession flow causalFlow from analyzeCausalChain.cause to identifyFailure.cause;
		succession flow causalFlow from analyzeCausalChain.probability to identifyFailure.probability;
		action identifyFailure { in cause: Cause; in probability: Probability; out failure: Failure; out severity: SeverityLevel; }
	}
}

package 'RiskMetadataManagement' {
	attribute def Scenario;
	attribute def Cause;
	attribute def Probability;
	attribute def Failure;
	attribute def Severity;
	attribute def CausalRelationship;
	attribute def RiskScenario;
	attribute def StandardModelingLanguage;
	port def ScenarioCmdPort {
		out scenario: Scenario;
	}
	port def CausePort {
		out cause: Cause;
		out probability: Probability;
	}
	port def FailurePort {
		out failure: Failure;
		out severity: Severity;
	}
	port def CausalRelationshipPort {
		out relationship: CausalRelationship;
	}
	port def RiskScenarioPort {
		out riskScenario: RiskScenario;
	}
	port def CompatibilityPort {
		out compatibleMetadata: StandardModelingLanguage;
	}
	part context {
		part user {
			port scenarioCmdPort: ScenarioCmdPort;
			perform identifyScenario.sendScenario {
				out scenario = scenarioCmdPort.scenario;
			}
		}
		interface userToSystem connect user.scenarioCmdPort to riskSystem.scenarioCmdPort {
			ref flow references identifyScenario.scenarioFlow
				from source.scenario to target.scenario;
		}
		part riskSystem {
			port scenarioCmdPort: ~ScenarioCmdPort;
			port causePort: CausePort;
			port failurePort: FailurePort;
			port causalRelationshipPort: CausalRelationshipPort;
			port compatibilityPort: CompatibilityPort;
			perform identifyScenario.propagateScenario {
				in scenario = scenarioCmdPort.scenario;
				out cause = causePort.cause;
				out failure = failurePort.failure;
			}
			perform detailCauses.assignProbability {
				in cause = causePort.cause;
				out probability = causePort.probability;
			}
			perform associateFailures.assignSeverity {
				in failure = failurePort.failure;
				out severity = failurePort.severity;
			}
			perform trackCausalRelationships.buildChain {
				in cause = causePort.cause;
				in failure = failurePort.failure;
				out relationship = causalRelationshipPort.relationship;
			}
			perform combineRiskElements.formScenario {
				in relationship = causalRelationshipPort.relationship;
				in probability = causePort.probability;
				in severity = failurePort.severity;
				out riskScenario = riskScenarioPort.riskScenario;
			}
			perform ensureCompatibility.translate {
				in riskScenario = riskScenarioPort.riskScenario;
				out compatibleMetadata = compatibilityPort.compatibleMetadata;
			}
		}
	}
	action identifyScenario {
		action sendScenario { out scenario: Scenario; }
		succession flow scenarioFlow from sendScenario.scenario to propagateScenario.scenario;
		action propagateScenario { in scenario; out cause: Cause; out failure: Failure; }
	}
	action detailCauses {
		action assignProbability { in cause: Cause; out probability: Probability; }
		succession flow probabilityFlow from assignProbability.probability to formScenario.probability;
	}
	action associateFailures {
		action assignSeverity { in failure: Failure; out severity: Severity; }
		succession flow severityFlow from assignSeverity.severity to formScenario.severity;
	}
	action trackCausalRelationships {
		action buildChain { in cause: Cause; in failure: Failure; out relationship: CausalRelationship; }
		succession flow relationshipFlow from buildChain.relationship to formScenario.relationship;
	}
	action combineRiskElements {
		action formScenario { in relationship: CausalRelationship; in probability: Probability; in severity: Severity; out riskScenario: RiskScenario; }
		succession flow scenarioOutput from formScenario.riskScenario to translate.riskScenario;
	}
	action ensureCompatibility {
		action translate { in riskScenario: RiskScenario; out compatibleMetadata: StandardModelingLanguage; }
	}
}

