package 'StructuralDecomposition' {
	attribute def Subsystem;
	attribute def Component;
	attribute def InterfaceDefinition;
	attribute def VerificationResult;
	port def DecompositionPort {
		out subsystems: Subsystem;
		out components: Component;
	}
	port def InterfacePort {
		out interfaceDef: InterfaceDefinition;
	}
	port def VerificationPort {
		out verification: VerificationResult;
	}
	part context {
		part topLevelSystem {
			port decompositionPort: DecompositionPort;
			perform decomposeSystem {
				out subsystems = decompositionPort.subsystems;
				out components = decompositionPort.components;
			}
		}
		interface systemToSubsystems connect topLevelSystem.decompositionPort to subsystems.decompositionPort {
			ref flow references decomposeSystem.subsystemsFlow
				from source.subsystems to target.subsystems;
			ref flow references decomposeSystem.componentsFlow
				from source.components to target.components;
		}
		part subsystems {
			port decompositionPort: ~DecompositionPort;
			perform decomposeSystem {
				in subsystems = decompositionPort.subsystems;
				in components = decompositionPort.components;
				out interfaceDef = interfacePort.interfaceDef;
			}
			port interfacePort: InterfacePort;
		}
		interface subsystemsToComponents connect subsystems.interfacePort to components.interfacePort {
			ref flow references decomposeSystem.interfaceDefFlow
				from source.interfaceDef to target.interfaceDef;
		}
		part components {
			port interfacePort: ~InterfacePort;
			perform defineInterfaces {
				in interfaceDef = interfacePort.interfaceDef;
			}
		}
		part ivvTeam {
			port verificationPort: VerificationPort;
			perform verifyModularDesign {
				out verification = verificationPort.verification;
			}
		}
		interface systemToIVV connect topLevelSystem.decompositionPort to ivvTeam.verificationPort {
			ref flow references verifyModularDesign.systemStructureFlow
				from source.subsystems to target.verification;
			ref flow references verifyModularDesign.interfaceFlow
				from source.components to target.verification;
		}
	}
	action decomposeSystem {
		action decomposeSystem { out subsystems: Subsystem; out components: Component; }
		succession flow subsystemsFlow from decomposeSystem.subsystems to systemToSubsystems.subsystems;
		succession flow componentsFlow from decomposeSystem.components to systemToSubsystems.components;
	}
	action defineInterfaces {
		action defineInterfaces { in subsystems: Subsystem; in components: Component; out interfaceDef: InterfaceDefinition; }
		succession flow interfaceDefFlow from defineInterfaces.interfaceDef to subsystemsToComponents.interfaceDef;
	}
	action verifyModularDesign {
		action verifyModularDesign { in subsystems: Subsystem; in components: Component; in interfaceDef: InterfaceDefinition; out verification: VerificationResult; }
		succession flow systemStructureFlow from decomposeSystem.subsystems to verifyModularDesign.subsystems;
		succession flow interfaceFlow from decomposeSystem.components to verifyModularDesign.components;
	}
}

